      dot += `color="#9C27B0", `;
      dot += `fontcolor="#9C27B0", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=2, `;
      dot += `style="dotted", `;
      dot += `fontsize=8`;
