    for (const e of libraryCallEdges) {
      const edgeLabel = e.label || "library";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#4CAF50", `;
      dot += `fontcolor="#4CAF50", `;
      dot += `label="${edgeLabel}", `;
