    for (const e of brokenEdges) {
      const edgeLabel = e.label || "BROKEN CALL";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#F44336", `;
      dot += `fontcolor="#F44336", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=3, `;
      dot += `style="dashed", `;
