    const builtinPercent =
      totalCalls > 0 ? ((builtinCount / totalCalls) * 100).toFixed(1) : 0;
    const libraryPercent =
      totalCalls > 0 ? ((libraryCount / totalCalls) * 100).toFixed(1) : 0;
    const totalFiles = new Set(Array.from(nodes.values()).map((n) => n.file))
      .size;
    const totalFunctions = nodes.size;

    dot += "\n  // COMPLETE FUNCTION-LEVEL DEPENDENCY ANALYSIS SUMMARY\n";
