import fs from "fs";
import path from "path";
import * as acorn from "acorn";
import { execSync } from "child_process";
import * as walk from "acorn-walk";

export class FunctionLevelHook {
  constructor(config) {
    this.config = config;
  }

  run(analysisResult, options = {}) {
    const { modules } = analysisResult;

    // Enhanced configuration for comprehensive analysis
    const isComprehensive = options.comprehensive || this.config.alwaysRunFunctionAnalysis || true;
    const shouldDetectBroken = options.detectBrokenCalls || this.config.detectBrokenCalls || true;
    const shouldValidateDeps = options.validateDependencies || this.config.validateAllDependencies || true;
    const shouldGenerateReports = options.report !== false && (this.config.generateActionableReports || true);

    console.log("üîç Function-level analysis configuration:");
    console.log(`  - Comprehensive analysis: ${isComprehensive ? '‚úÖ' : '‚ùå'}`);
    console.log(`  - Broken call detection: ${shouldDetectBroken ? '‚úÖ' : '‚ùå'}`);
    console.log(`  - Dependency validation: ${shouldValidateDeps ? '‚úÖ' : '‚ùå'}`);
    console.log(`  - Actionable reports: ${shouldGenerateReports ? '‚úÖ' : '‚ùå'}`);

    const rules = this.loadRules();
    const fileIndex = this.buildIndex(modules, rules);
    const { nodes, edges } = this.buildFunctionGraphFromIndex(fileIndex, {
      detectBrokenCalls: shouldDetectBroken,
      validateDependencies: shouldValidateDeps,
      comprehensive: isComprehensive
    });

    // Add Chicago timezone timestamp if requested
    let timestamp = "";
    if (options.timestamp) {
      const now = new Date();
      const chicagoTime = now.toLocaleString("en-US", {
        timeZone: "America/Chicago",
        year: "numeric",
        month: "2-digit",
        day: "2-digit",
        hour: "2-digit",
        minute: "2-digit",
        second: "2-digit",
      });
      timestamp = chicagoTime.replace(/[\/\s:,]/g, "-");
    }

    const dotGraph = this.generateDotGraph(nodes, edges, options);

    // Generate timestamped filenames
    const baseName = timestamp
      ? `function-level-graph-${timestamp}`
      : "function-level-graph";
    const outputPath = path.join(this.config.outputDir, `${baseName}.svg`);

    if (!fs.existsSync(this.config.outputDir)) {
      fs.mkdirSync(this.config.outputDir, { recursive: true });
    }

    const dotPath = path.join(this.config.outputDir, `${baseName}.dot`);
    fs.writeFileSync(dotPath, dotGraph);
    execSync(`dot -Tsvg "${dotPath}" -o "${outputPath}"`);

    console.log(`Function-level dependency graph generated: ${outputPath}`);

    // Generate actionable report with Chicago timestamp if requested
    if (options.report) {
      const reportBaseName = timestamp
        ? `actionable-report-${timestamp}`
        : "actionable-report";
      const reportPath = path.join(
        this.config.outputDir,
        `${reportBaseName}.md`,
      );
      const agentPromptPath = path.join(
        this.config.outputDir,
        `agent-input-${timestamp || "latest"}.md`,
      );
      const { reportMarkdown, agentMarkdown } = this.generateActionableReport(
        nodes,
        edges,
        options,
      );
      fs.writeFileSync(reportPath, reportMarkdown, "utf8");
      fs.writeFileSync(agentPromptPath, agentMarkdown, "utf8");
      console.log(`Actionable report generated: ${reportPath}`);
      console.log(`Agent prompt generated: ${agentPromptPath}`);
    }
  }

  // Allow any AI/agent to steer function detection via config/env at runtime
  loadRules() {
    if (this.config.functionRules && Array.isArray(this.config.functionRules)) {
      return this.config.functionRules;
    }
    if (process.env.CODEMAP_RULES) {
      try {
        const parsed = JSON.parse(process.env.CODEMAP_RULES);
        if (Array.isArray(parsed)) return parsed;
      } catch {}
    }
    // Pragmatic defaults (overridable)
    return [
      { nodeType: "FunctionDeclaration", namePropPath: "id.name" },
      { nodeType: "VariableDeclarator", namePropPath: "id.name" },
      { nodeType: "MethodDefinition", namePropPath: "key.name" },
      { nodeType: "Property", namePropPath: "key.name" },
    ];
  }

  buildIndex(modules, rules) {
    const fileIndex = new Map();
    modules.forEach((module) => {
      if (
        !module.source ||
        module.coreModule ||
        module.source.includes("node_modules")
      )
        return;
      if (!/\.(m?js|ts)$/.test(module.source)) return;

      const modulePath = module.source;
      try {
        const code = fs.readFileSync(modulePath, "utf8");
        const ast = acorn.parse(code, {
          sourceType: "module",
          ecmaVersion: "latest",
          locations: true,
        });
        const entry = {
          path: modulePath,
          functions: [], // { name, startLine, endLine }
          exports: new Map(), // exportedName -> localName
          imports: new Map(), // localName -> { source, importedName }
        };

        // imports/exports
        walk.simple(ast, {
          ImportDeclaration: (node) => {
            const source = node.source && node.source.value;
            if (!source) return;
            node.specifiers?.forEach((spec) => {
              const local = spec.local?.name;
              if (!local) return;
              if (spec.type === "ImportNamespaceSpecifier") {
                entry.imports.set(local, {
                  source,
                  importedName: "*",
                  namespace: true,
                });
              } else if (spec.type === "ImportDefaultSpecifier") {
                entry.imports.set(local, { source, importedName: "default" });
              } else {
                const imported = spec.imported?.name || "default";
                entry.imports.set(local, { source, importedName: imported });
              }
            });
          },
          ExportNamedDeclaration: (node) => {
            if (
              node.declaration &&
              node.declaration.type === "FunctionDeclaration"
            ) {
              const name = node.declaration.id?.name;
              if (name) entry.exports.set(name, name);
            }
            node.specifiers?.forEach((spec) => {
              const exported = spec.exported?.name;
              const local = spec.local?.name || exported;
              if (exported) entry.exports.set(exported, local);
            });
          },
          ExportDefaultDeclaration: (node) => {
            if (node.declaration?.id?.name) {
              entry.exports.set("default", node.declaration.id.name);
            } else {
              entry.exports.set("default", "default");
            }
          },
        });

        // functions
        this.collectFunctions(ast, rules, (fnName, node) => {
          if (!fnName) return;
          const startLine = node.loc?.start?.line || 0;
          const endLine = node.loc?.end?.line || startLine;
          entry.functions.push({ name: fnName, startLine, endLine });
        });

        fileIndex.set(modulePath, entry);
      } catch (e) {
        console.error(`Error processing ${modulePath}:`, e);
      }
    });
    return fileIndex;
  }

  collectFunctions(ast, rules, onFunction) {
    const matchers = rules.map((r) => ({
      nodeType: r.nodeType,
      getName: (node) => {
        try {
          const parts = (r.namePropPath || "").split(".");
          let current = node;
          for (const p of parts) {
            if (!current) return null;
            current = current[p];
          }
          return typeof current === "string" ? current : current?.name || null;
        } catch {
          return null;
        }
      },
    }));

    walk.ancestor(ast, {
      FunctionDeclaration: (node) => {
        const rule = matchers.find((m) => m.nodeType === "FunctionDeclaration");
        const name = rule?.getName(node) || node.id?.name;
        onFunction(name, node);
      },
      VariableDeclarator: (node) => {
        const initType = node.init?.type;
        if (
          initType === "FunctionExpression" ||
          initType === "ArrowFunctionExpression"
        ) {
          const rule = matchers.find(
            (m) => m.nodeType === "VariableDeclarator",
          );
          const name = rule?.getName(node) || node.id?.name;
          onFunction(name, node);
        }
      },
      MethodDefinition: (node) => {
        const rule = matchers.find((m) => m.nodeType === "MethodDefinition");
        const name = rule?.getName(node) || node.key?.name;
        onFunction(name, node);
      },
      Property: (node) => {
        if (
          node.value?.type === "FunctionExpression" ||
          node.value?.type === "ArrowFunctionExpression"
        ) {
          const rule = matchers.find((m) => m.nodeType === "Property");
          const name = rule?.getName(node) || node.key?.name;
          onFunction(name, node);
        }
      },
    });
  }

  buildFunctionGraphFromIndex(fileIndex, options = {}) {
    const { detectBrokenCalls = true, validateDependencies = true, comprehensive = true } = options;
    const edges = [];
    const nodes = new Map();

    const fileToExports = new Map();
    const fileToFunctions = new Map();

    for (const [file, entry] of fileIndex.entries()) {
      const fnMap = new Map();
      entry.functions.forEach((fn) => fnMap.set(fn.name, fn));
      fileToFunctions.set(file, fnMap);
      fileToExports.set(file, entry.exports);
      entry.functions.forEach((fn) => {
        const id = `${file}::${fn.name}@${fn.startLine}`;
        nodes.set(id, {
          label: `${path.basename(file)}\\n${fn.name} [${this.formatChicagoTimestamp()}] [${fn.startLine}-${fn.endLine}]`,
          file,
          name: fn.name,
          startLine: fn.startLine,
          endLine: fn.endLine,
        });
      });
    }

    const resolveImport = (fromFile, imp) => {
      const src = imp.source;
      if (!src) return null;
      if (src.startsWith(".")) {
        const abs = path.resolve(path.dirname(fromFile), src);
        const candidates = [
          abs,
          `${abs}.js`,
          `${abs}.mjs`,
          `${abs}.ts`,
          path.join(abs, "index.js"),
        ];
        for (const c of candidates) {
          if (fileIndex.has(c)) return c;
        }
      }
      return null;
    };

    for (const [file, entry] of fileIndex.entries()) {
      try {
        const code = fs.readFileSync(file, "utf8");
        const ast = acorn.parse(code, {
          sourceType: "module",
          ecmaVersion: "latest",
          locations: true,
        });
        this.findFunctionCalls(
          ast,
          file,
          (callerName, calleeIdent, loc, calleeBase, calleeBaseFull) => {
            const callerFn = fileToFunctions.get(file)?.get(callerName);
            const fromId = callerFn
              ? `${file}::${callerFn.name}@${callerFn.startLine}`
              : `${file}::${callerName}@${loc?.start?.line || 0}`;

            let targetFile = file;
            let targetFn =
              fileToFunctions.get(targetFile)?.get(calleeIdent) || null;
            if (!targetFn) {
              let imp = entry.imports.get(calleeIdent);
              if (imp) {
                const resolved = resolveImport(file, imp);
                if (resolved) {
                  targetFile = resolved;
                  const exportedName = imp.importedName;
                  const exportedMap = fileToExports.get(targetFile);
                  const localExportName =
                    exportedMap?.get(exportedName) || exportedName;
                  targetFn =
                    fileToFunctions.get(targetFile)?.get(localExportName) ||
                    null;
                } else {
                  // non-relative import (npm/core) ‚Üí treat as external target
                  const toId = `ext:${imp.source}::${imp.importedName}`;
                  if (!nodes.has(toId)) {
                    nodes.set(toId, {
                      label: `${imp.source}\n${imp.importedName} [external]`,
                      file: imp.source,
                      name: imp.importedName,
                      startLine: 0,
                      endLine: 0,
                    });
                  }
                  edges.push({ from: fromId, to: toId, broken: false });
                  return;
                }
              } else if (calleeBase) {
                // namespace or global base
                const baseImp = entry.imports.get(calleeBase);
                if (baseImp) {
                  // module namespace import or default import used as namespace
                  const display = calleeBaseFull || calleeIdent;
                  const toId = `ext:${baseImp.source}::${display}`;
                  if (!nodes.has(toId)) {
                    nodes.set(toId, {
                      label: `${baseImp.source}\n${display} [external]`,
                      file: baseImp.source,
                      name: display,
                      startLine: 0,
                      endLine: 0,
                    });
                  }
                  edges.push({ from: fromId, to: toId, broken: false });
                  return;
                }
                // Check for i18n translation pattern (i18n.t calls)
                if (calleeBase === "i18n" && calleeIdent === "t") {
                  const toId = `i18n:translation`;
                  if (!nodes.has(toId)) {
                    nodes.set(toId, {
                      label: `i18n\\ntranslation\\n‚Üí localized strings\\nI18N [library]`,
                      file: "i18n-library",
                      name: "i18n.t",
                      startLine: 0,
                      endLine: 0,
                      destination: "localized strings",
                      impact: "I18N",
                    });
                  }
                  edges.push({
                    from: fromId,
                    to: toId,
                    broken: false,
                    type: "library-call",
                  });
                  return;
                }

                // known globals like console.log, JSON.parse, Math.max, etc. - trace their destinations
                const knownGlobals = new Set([
                  "console",
                  "JSON",
                  "Math",
                  "Date",
                  "process",
                  "global",
                  "globalThis",
                  "i18n",
                ]);
                if (knownGlobals.has(calleeBase)) {
                  const toId = `global:${calleeBase}.${calleeIdent}`;
                  if (!nodes.has(toId)) {
                    // Enhanced global node with actual destination tracing
                    const destination = this.getGlobalDestination(
                      `${calleeBase}.${calleeIdent}`,
                    );
                    const impact = this.getGlobalImpact(
                      `${calleeBase}.${calleeIdent}`,
                    );
                    nodes.set(toId, {
                      label: `${calleeBase}\\n${calleeIdent}\\n‚Üí ${destination}\\n${impact} [global]`,
                      file: "global",
                      name: `${calleeBase}.${calleeIdent}`,
                      startLine: 0,
                      endLine: 0,
                      destination: destination,
                      impact: impact,
                    });
                  }
                  // Mark as working global call with proper type
                  edges.push({
                    from: fromId,
                    to: toId,
                    broken: false,
                    type: "global-call",
                  });
                  return;
                }
              } else {
                // bare global functions - trace where they actually go
                const knownGlobalFns = new Set([
                  "parseInt",
                  "parseFloat",
                  "setTimeout",
                  "setInterval",
                  "clearTimeout",
                  "clearInterval",
                  "queueMicrotask",
                  "setImmediate",
                ]);
                if (knownGlobalFns.has(calleeIdent)) {
                  const toId = `global:${calleeIdent}`;
                  if (!nodes.has(toId)) {
                    // Enhanced global node with destination info
                    const destination = this.getGlobalDestination(calleeIdent);
                    nodes.set(toId, {
                      label: `global\\n${calleeIdent}\\n‚Üí ${destination} [global]`,
                      file: "global",
                      name: calleeIdent,
                      startLine: 0,
                      endLine: 0,
                      destination: destination,
                    });
                  }
                  // Mark as working global call, not broken
                  edges.push({
                    from: fromId,
                    to: toId,
                    broken: false,
                    type: "global-call",
                  });
                  return;
                }
              }
            }

            if (targetFn) {
              const toId = `${targetFile}::${targetFn.name}@${targetFn.startLine}`;
              edges.push({ from: fromId, to: toId, broken: false });
            } else {
              // Before marking as broken, check if it's a Node.js built-in
              const fullCallName = calleeBaseFull
                ? `${calleeBaseFull}.${calleeIdent}`
                : calleeIdent;
              if (
                this.isNodeBuiltin(fullCallName) ||
                this.isNodeBuiltin(calleeIdent)
              ) {
                const toId = `global:${fullCallName}`;
                if (!nodes.has(toId)) {
                  const destination = this.getGlobalDestination(fullCallName);
                  const impact = this.getGlobalImpact(fullCallName);
                  nodes.set(toId, {
                    label: `nodejs\\n${fullCallName}\\n‚Üí ${destination}\\n${impact} [builtin]`,
                    file: "nodejs-builtin",
                    name: fullCallName,
                    startLine: 0,
                    endLine: 0,
                    destination: destination,
                    impact: impact,
                  });
                }
                edges.push({
                  from: fromId,
                  to: toId,
                  broken: false,
                  type: "builtin-call",
                });
              } else {
                // Actually unresolved - mark as broken
                const toId = `${file}::${calleeIdent}@?`;
                if (!nodes.has(toId)) {
                  nodes.set(toId, {
                    label: `${path.basename(file)}\\n${calleeIdent} [unresolved] [${this.formatChicagoTimestamp()}]`,
                    file,
                    name: calleeIdent,
                    startLine: 0,
                    endLine: 0,
                  });
                }
                edges.push({ from: fromId, to: toId, broken: true });
              }
            }
          },
        );
      } catch (e) {
        console.error(`Error building edges for ${file}:`, e);
      }
    }

    return { nodes, edges };
  }

  findFunctionCalls(ast, filePath, callback) {
    walk.ancestor(
      ast,
      {
        CallExpression: (node, ancestors) => {
          const fullCallee = this.getNodeName(node.callee);
          let calleeName = fullCallee;
          let calleeBase = null;
          let calleeBaseFull = null;
          if (fullCallee && fullCallee.includes(".")) {
            const parts = fullCallee.split(".");
            calleeBase = parts[0];
            calleeName = parts[parts.length - 1];
            calleeBaseFull = parts.slice(0, parts.length - 1).join(".");
          }
          let callerName = null;

          for (let i = ancestors.length - 2; i >= 0; i--) {
            const ancestor = ancestors[i];
            if (ancestor.type === "FunctionDeclaration") {
              callerName = this.getNodeName(ancestor.id) || "anonymous";
              break;
            }
            if (
              ancestor.type === "FunctionExpression" ||
              ancestor.type === "ArrowFunctionExpression"
            ) {
              const maybeParent = ancestors[i - 1];
              if (
                maybeParent &&
                maybeParent.type === "VariableDeclarator" &&
                maybeParent.id
              ) {
                callerName = this.getNodeName(maybeParent.id) || "anonymous";
              } else if (
                maybeParent &&
                maybeParent.type === "Property" &&
                maybeParent.key
              ) {
                callerName = this.getNodeName(maybeParent.key) || "anonymous";
              } else {
                callerName = "anonymous";
              }
              break;
            }
            if (ancestor.type === "MethodDefinition" && ancestor.key) {
              callerName = this.getNodeName(ancestor.key) || "anonymous";
              break;
            }
          }

          if (calleeName) {
            callback(
              callerName || "anonymous",
              calleeName,
              node.loc,
              calleeBase,
              calleeBaseFull,
            );
          }
        },
      },
      {
        ...walk.base,
        MemberExpression: () => {},
      },
    );
  }

  getNodeName(node) {
    if (!node) return null;
    if (node.type === "Identifier") {
      return node.name;
    }
    if (node.type === "MemberExpression") {
      // return full base + property to detect namespace calls
      const base = this.getNodeName(node.object);
      const prop = this.getNodeName(node.property);
      if (base && prop) return `${base}.${prop}`;
      return prop || base || null;
    }
    return null;
  }

  // Trace where global calls actually go
  getGlobalDestination(globalCall) {
    const destinations = {
      "console.log": "stdout/terminal",
      "console.error": "stderr/terminal",
      "console.warn": "stderr/terminal",
      "console.info": "stdout/terminal",
      "process.exit": "system exit",
      "process.on": "event listener",
      "process.cwd": "file system",
      "process.env": "environment",
      "process.argv": "arguments",
      "process.pid": "process info",
      "JSON.stringify": "string output",
      "JSON.parse": "object creation",
      "Math.round": "number calc",
      "Math.floor": "number calc",
      "Math.ceil": "number calc",
      "Math.random": "random gen",
      "Date.now": "timestamp",
      setTimeout: "event queue",
      setInterval: "event queue",
      clearTimeout: "event queue",
      clearInterval: "event queue",
      parseInt: "number parse",
      parseFloat: "number parse",
      require: "module loader",
      "Buffer.from": "buffer creation",
      "Buffer.alloc": "buffer creation",
      __dirname: "directory path",
      __filename: "file path",
    };
    return destinations[globalCall] || "runtime";
  }

  // Check if a call is a Node.js built-in that should be treated as global, not broken
  isNodeBuiltin(callName) {
    const nodeBuiltins = [
      "console.log",
      "console.error",
      "console.warn",
      "console.info",
      "process.exit",
      "process.on",
      "process.cwd",
      "process.env",
      "process.argv",
      "process.pid",
      "JSON.stringify",
      "JSON.parse",
      "Math.round",
      "Math.floor",
      "Math.ceil",
      "Math.random",
      "Date.now",
      "setTimeout",
      "setInterval",
      "clearTimeout",
      "clearInterval",
      "parseInt",
      "parseFloat",
      "require",
      "Buffer.from",
      "Buffer.alloc",
      "__dirname",
      "__filename",
    ];

    // Also check for single-name built-ins (like 'require')
    const singleNameBuiltins = [
      "require",
      "setTimeout",
      "setInterval",
      "clearTimeout",
      "clearInterval",
      "parseInt",
      "parseFloat",
      "__dirname",
      "__filename",
    ];

    return (
      nodeBuiltins.includes(callName) || singleNameBuiltins.includes(callName)
    );
  }

  // Determine the impact/purpose of global calls
  getGlobalImpact(globalCall) {
    const impacts = {
      "console.log": "DEBUG/LOG",
      "console.error": "ERROR LOG",
      "console.warn": "WARNING",
      "console.info": "INFO LOG",
      "process.exit": "APP EXIT",
      "process.on": "LISTENER",
      "process.cwd": "PATH INFO",
      "JSON.stringify": "SERIALIZE",
      "JSON.parse": "DESERIALIZE",
      "Math.round": "MATH OP",
      "Math.floor": "MATH OP",
      "Math.ceil": "MATH OP",
      "Math.random": "RANDOM",
      "Date.now": "TIMESTAMP",
      setTimeout: "ASYNC TIMER",
      setInterval: "RECURRING",
      clearTimeout: "CANCEL TIMER",
      clearInterval: "CANCEL LOOP",
      parseInt: "TYPE CONVERT",
      parseFloat: "TYPE CONVERT",
    };
    return impacts[globalCall] || "RUNTIME";
  }

  generateDotGraph(nodes, edges, options = {}) {
    let dot = "digraph FunctionLevelGraph {\n";
    dot += "  rankdir=TB;\n"; // Top-to-bottom for hierarchical flow
    dot += '  fontname="Consolas";\n';
    dot += '  bgcolor="#FAFAFA";\n';
    dot += '  pad="0.5";\n';
    dot += '  nodesep="0.5";\n';
    dot += '  ranksep="1.5";\n'; // More space between ranks for hierarchy
    dot += "  compound=true;\n"; // Allow edges between clusters
    dot += "  \n";
    dot += "  // Default node styling\n";
    dot +=
      '  node [shape=box, style="rounded,filled", fontname="Consolas", fontsize=10, height=0.8, width=2.5];\n';
    dot +=
      '  edge [fontname="Consolas", fontsize=8, arrowsize=0.7, arrowhead=vee];\n';
    dot += "  \n";
    dot += "  // Title\n";
    dot += '  labelloc="t";\n';
    dot +=
      '  label="Chrome Container App - Function-Level Dependency Graph\\nGenerated: ' +
      this.formatChicagoTimestamp() +
      '";\n';
    dot += "  fontsize=16;\n";
    dot += '  fontname="Arial";\n';
    dot += "\n";
    dot +=
      "  // COMPREHENSIVE LEGEND - Matching COMPLETE-DEPENDENCY-GRAPH.svg\n";
    dot += "  subgraph cluster_legend {\n";
    dot += '    label="CONNECTION TYPES LEGEND";\n';
    dot += '    style="filled,rounded";\n';
    dot += '    color="#333333";\n';
    dot += '    bgcolor="#F9F9F9";\n';
    dot += '    fontname="Arial";\n';
    dot += "    fontsize=14;\n";
    dot += "    fontweight=bold;\n";
    dot += "    margin=20;\n";
    dot += "    \n";
    dot += "    // Connection type examples\n";
    dot +=
      '    direct_call_ex [label="Direct Call", shape=plaintext, fontsize=10];\n';
    dot +=
      '    ipc_call_ex [label="IPC Communication", shape=plaintext, fontsize=10];\n';
    dot +=
      '    event_bus_ex [label="Event-Driven", shape=plaintext, fontsize=10];\n';
    dot +=
      '    global_call_ex [label="Global/Runtime Call", shape=plaintext, fontsize=10, fontcolor="#9C27B0"];\n';
    dot +=
      '    builtin_call_ex [label="Node.js Built-in", shape=plaintext, fontsize=10, fontcolor="#607D8B"];\n';
    dot +=
      '    unanswered_ex [label="UNANSWERED Call", shape=plaintext, fontsize=10, fontcolor="#F44336"];\n';
    dot +=
      '    file_io_ex [label="File I/O Operation", shape=plaintext, fontsize=10];\n';
    dot += "    \n";
    dot += "    // Connection examples with proper styling\n";
    dot +=
      '    direct_call_ex -> ipc_call_ex [color="#1976D2", penwidth=2, style="solid", label=""];\n';
    dot +=
      '    ipc_call_ex -> event_bus_ex [color="#4CAF50", penwidth=2, style="solid", label=""];\n';
    dot +=
      '    event_bus_ex -> global_call_ex [color="#FF9800", penwidth=2, style="solid", label=""];\n';
    dot +=
      '    global_call_ex -> builtin_call_ex [color="#9C27B0", penwidth=2, style="dotted", label=""];\n';
    dot +=
      '    builtin_call_ex -> unanswered_ex [color="#607D8B", penwidth=2, style="dotted", label=""];\n';
    dot +=
      '    unanswered_ex -> file_io_ex [color="#F44336", penwidth=3, style="dashed", label=""];\n';
    dot += "    \n";
    dot += "  }\n";
    dot += "  \n";
    dot += "  // NODE TYPES LEGEND\n";
    dot += "  subgraph cluster_node_types {\n";
    dot += '    label="NODE TYPES";\n';
    dot += '    style="filled,rounded";\n';
    dot += '    color="#333333";\n';
    dot += '    bgcolor="#FFFFFF";\n';
    dot += '    fontname="Arial";\n';
    dot += "    fontsize=14;\n";
    dot += "    fontweight=bold;\n";
    dot += "    margin=20;\n";
    dot += "    \n";
    dot +=
      '    entry_point_type [label="Entry Point", fillcolor="#4CAF50", color="#2E7D32", fontcolor="white", style="rounded,filled"];\n';
    dot +=
      '    legacy_service_type [label="Legacy Service", fillcolor="#2196F3", color="#1565C0", fontcolor="white", style="rounded,filled"];\n';
    dot +=
      '    new_arch_type [label="New Architecture", fillcolor="#FF9800", color="#F57C00", fontcolor="white", style="rounded,filled"];\n';
    dot +=
      '    ui_controller_type [label="UI Controller", fillcolor="#9C27B0", color="#6A1B9A", fontcolor="white", style="rounded,filled"];\n';
    dot +=
      '    broken_type [label="Broken/Missing", fillcolor="#F44336", color="#C62828", fontcolor="white", style="rounded,filled,bold"];\n';
    dot +=
      '    html_type [label="HTML File", fillcolor="#795548", color="#5D4037", fontcolor="white", style="rounded,filled"];\n';
    dot +=
      '    external_type [label="External/Global", fillcolor="#607D8B", color="#455A64", fontcolor="white", style="rounded,filled"];\n';
    dot += "  }\n";

    // Create hierarchical layers matching COMPLETE-DEPENDENCY-GRAPH.svg categories
    const layers = {
      entryPoints: {
        rank: 0,
        files: [],
        color: "#4CAF50",
        bgColor: "#E8F5E9",
        nodeType: "entry-point",
      },
      legacyServices: {
        rank: 1,
        files: [],
        color: "#2196F3",
        bgColor: "#E3F2FD",
        nodeType: "legacy-service",
      },
      newArchitecture: {
        rank: 2,
        files: [],
        color: "#FF9800",
        bgColor: "#FFF3E0",
        nodeType: "new-architecture",
      },
      uiControllers: {
        rank: 3,
        files: [],
        color: "#9C27B0",
        bgColor: "#F3E5F5",
        nodeType: "ui-controller",
      },
      htmlFiles: {
        rank: 4,
        files: [],
        color: "#795548",
        bgColor: "#EFEBE9",
        nodeType: "html-file",
      },
      webPublic: {
        rank: 5,
        files: [],
        color: "#607D8B",
        bgColor: "#ECEFF1",
        nodeType: "web-public",
      },
      brokenServices: {
        rank: 6,
        files: [],
        color: "#F44336",
        bgColor: "#FFCDD2",
        nodeType: "broken-service",
      },
    };

    // Categorize files into hierarchical layers
    const fileNodes = new Map();
    for (const [id, info] of nodes.entries()) {
      if (!fileNodes.has(info.file)) fileNodes.set(info.file, []);
      fileNodes.get(info.file).push({ id, info });
    }

    // Enhanced categorization matching target SVG structure
    for (const [file, items] of fileNodes.entries()) {
      // Check if file has broken/unresolved functions
      const hasBrokenFunctions = items.some(
        (item) =>
          item.id.includes("@?") ||
          item.info.name === "unresolved" ||
          item.info.label?.includes("[UNRESOLVED]"),
      );

      if (hasBrokenFunctions) {
        layers.brokenServices.files.push({ file, items });
      } else if (
        file.includes("main.js") ||
        file.includes("preload.js") ||
        file.includes("bootstrap.js")
      ) {
        layers.entryPoints.files.push({ file, items });
      } else if (
        file.includes("src/core/") ||
        file.includes("src/i18n/") ||
        file.includes("src/chrome/") ||
        file.includes("src/terminal/") ||
        file.includes("src/ipc/")
      ) {
        layers.legacyServices.files.push({ file, items });
      } else if (
        file.includes("renderer/new-architecture") ||
        file.includes("strike-errors")
      ) {
        layers.newArchitecture.files.push({ file, items });
      } else if (
        file.includes("renderer/") &&
        !file.includes("new-architecture")
      ) {
        layers.uiControllers.files.push({ file, items });
      } else if (file.includes(".html")) {
        layers.htmlFiles.files.push({ file, items });
      } else if (file.includes("public/")) {
        layers.webPublic.files.push({ file, items });
      } else {
        // External/global dependencies
        layers.webPublic.files.push({ file, items });
      }
    }

    let clusterIndex = 0;

    // Generate hierarchical subgraphs with proper ranking
    for (const [layerName, layer] of Object.entries(layers)) {
      if (layer.files.length === 0) continue;

      dot += `  // LAYER: ${layerName.toUpperCase()}\n`;
      dot += `  subgraph cluster_${layerName} {\n`;
      dot += `    label="${layerName.replace(/([A-Z])/g, " $1").toUpperCase()}";\n`;
      dot += `    style="filled,rounded,bold";\n`;
      dot += `    color="${layer.color}";\n`;
      dot += `    fillcolor="${layer.bgColor}";\n`;
      dot += `    penwidth=3;\n`;
      dot += `    fontname="Arial";\n`;
      dot += `    fontsize=14;\n`;
      dot += `    margin=20;\n`;
      dot += `    \n`;

      // Create rank constraint for this layer
      if (layer.files.length > 0) {
        dot += `    { rank=same; `;
        for (const { file, items } of layer.files) {
          for (const { id } of items) {
            dot += `"${id}"; `;
          }
        }
        dot += `}\n\n`;
      }

      // Add files within this layer
      for (const { file, items } of layer.files) {
        dot += `    // File: ${file}\n`;

        for (const { id, info } of items) {
          const isUnresolved = id.includes("@?") || info.name === "unresolved";
          const isExternal = id.startsWith("ext:") || id.startsWith("global:");

          let fillColor = layer.bgColor;
          let borderColor = layer.color;
          let fontColor = "black";
          let style = "rounded,filled";

          if (isUnresolved) {
            fillColor = "#FFCDD2";
            borderColor = "#F44336";
            fontColor = "#B71C1C";
            style = "rounded,filled,bold";
          } else if (isExternal) {
            fillColor = "#CFD8DC";
            borderColor = "#607D8B";
            fontColor = "#455A64";
          }

          // Enhanced label with file and line info
          const fileName = path.basename(info.file);
          const functionLabel = `${fileName}\\n${info.name}\\nL:${info.startLine}${info.endLine !== info.startLine ? `-${info.endLine}` : ""}`;

          dot += `    "${id}" [label="${functionLabel}", fillcolor="${fillColor}", color="${borderColor}", fontcolor="${fontColor}", style="${style}", penwidth=2];\n`;
        }
        dot += `\n`;
      }

      dot += `  }\n\n`;
    }

    // Edges with enhanced styling for broken/resolved/global calls
    dot += "\n  // Function call edges\n";
    const brokenEdges = [];
    const globalCallEdges = [];
    const builtinCallEdges = [];
    const libraryCallEdges = [];
    const regularCallEdges = [];

    for (const e of edges) {
      if (e.broken) {
        brokenEdges.push(e);
      } else if (e.type === "global-call") {
        globalCallEdges.push(e);
      } else if (e.type === "builtin-call") {
        builtinCallEdges.push(e);
      } else if (e.type === "library-call") {
        libraryCallEdges.push(e);
      } else {
        regularCallEdges.push(e);
      }
    }

    // Draw regular working edges first (they go behind)
    for (const e of regularCallEdges) {
      const edgeLabel = e.label || "calls";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#1976D2", `;
      dot += `fontcolor="#1976D2", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=2, `;
      dot += `style="solid"`;
      dot += `];\n`;
    }

    // Draw global call edges with special styling
    dot += "\n  // GLOBAL CALL EDGES - Runtime/System Calls\n";
    for (const e of globalCallEdges) {
      const edgeLabel = e.label || "global call";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#9C27B0", `;
      dot += `fontcolor="#9C27B0", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=2, `;
      dot += `style="dotted", `;
      dot += `fontsize=8`;
      dot += `];\n`;
    }

    // Draw built-in call edges with distinct styling
    dot += "\n  // BUILTIN CALL EDGES - Node.js Built-ins\n";
    for (const e of builtinCallEdges) {
      const edgeLabel = e.label || "builtin";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#607D8B", `;
      dot += `fontcolor="#607D8B", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=2, `;
      dot += `style="dotted", `;
      dot += `fontsize=8`;
      dot += `];\n`;
    }

    // Draw library call edges (i18n, etc.)
    dot += "\n  // LIBRARY CALL EDGES - External Libraries\n";
    for (const e of libraryCallEdges) {
      const edgeLabel = e.label || "library";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#4CAF50", `;
      dot += `fontcolor="#4CAF50", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=2, `;
      dot += `style="solid", `;
      dot += `fontsize=8`;
      dot += `];\n`;
    }

    // Draw broken edges on top (more visible)
    dot += "\n  // BROKEN/UNRESOLVED CALLS - CRITICAL\n";
    for (const e of brokenEdges) {
      const edgeLabel = e.label || "BROKEN CALL";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#F44336", `;
      dot += `fontcolor="#F44336", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=3, `;
      dot += `style="dashed", `;
      dot += `fontsize=10, `;
      dot += `fontname="Arial Black"`;
      dot += `];\n`;
    }

    // Add comprehensive analysis summary including global calls
    const totalCalls = edges.length;
    const brokenCount = brokenEdges.length;
    const globalCount = globalCallEdges.length;
    const builtinCount = builtinCallEdges.length;
    const libraryCount = libraryCallEdges.length;
    const regularCount = regularCallEdges.length;
    const resolvedCount =
      globalCount + builtinCount + libraryCount + regularCount;
    const brokenPercent =
      totalCalls > 0 ? ((brokenCount / totalCalls) * 100).toFixed(1) : 0;
    const globalPercent =
      totalCalls > 0 ? ((globalCount / totalCalls) * 100).toFixed(1) : 0;
    const builtinPercent =
      totalCalls > 0 ? ((builtinCount / totalCalls) * 100).toFixed(1) : 0;
    const libraryPercent =
      totalCalls > 0 ? ((libraryCount / totalCalls) * 100).toFixed(1) : 0;
    const totalFiles = new Set(Array.from(nodes.values()).map((n) => n.file))
      .size;
    const totalFunctions = nodes.size;

    dot += "\n  // COMPLETE FUNCTION-LEVEL DEPENDENCY ANALYSIS SUMMARY\n";
    dot += "  subgraph cluster_analysis_summary {\n";
    dot += '    label="COMPLETE FUNCTION-LEVEL DEPENDENCY ANALYSIS";\n';
    dot += '    style="filled,rounded";\n';
    dot += '    color="#333333";\n';
    dot += '    bgcolor="#FFFFFF";\n';
    dot += '    fontname="Arial";\n';
    dot += "    fontsize=16;\n";
    dot += "    fontweight=bold;\n";
    dot += "    margin=25;\n";
    dot += "    penwidth=2;\n";
    dot += "    \n";
    dot += `    summary [shape=plaintext, label=<\n`;
    dot +=
      '      <TABLE BORDER="0" CELLBORDER="0" CELLSPACING="0" CELLPADDING="8">\n';
    dot += `        <TR><TD ALIGN="LEFT">‚Ä¢ ALL ${totalFiles} JavaScript files with EVERY function mapped with line numbers</TD></TR>\n`;
    dot +=
      '        <TR><TD ALIGN="LEFT">‚Ä¢ COMPLETE connection paths from backend services to frontend UI</TD></TR>\n';
    dot += `        <TR><TD ALIGN="LEFT">‚Ä¢ ${globalCount} Global/Runtime calls traced to destinations (${globalPercent}%)</TD></TR>\n`;
    dot += `        <TR><TD ALIGN="LEFT">‚Ä¢ ${builtinCount} Node.js built-in calls properly recognized (${builtinPercent}%)</TD></TR>\n`;
    dot += `        <TR><TD ALIGN="LEFT">‚Ä¢ ${libraryCount} Library calls resolved (i18n, etc.) (${libraryPercent}%)</TD></TR>\n`;
    dot += `        <TR><TD ALIGN="LEFT">‚Ä¢ ${brokenCount} ${brokenCount > 0 ? "CRITICAL broken dependency chains clearly marked in RED" : "No broken dependency chains - PERFECT CODE"}</TD></TR>\n`;
    dot +=
      '        <TR><TD ALIGN="LEFT">‚Ä¢ Function-to-function call mapping with precise line numbers</TD></TR>\n';
    dot +=
      '        <TR><TD ALIGN="LEFT">‚Ä¢ Real-time data flow: IPC bridges and event system analysis</TD></TR>\n';
    dot +=
      '        <TR><TD ALIGN="LEFT">‚Ä¢ Chrome management integration paths completely traced</TD></TR>\n';
    dot += `        <TR><TD ALIGN="LEFT">‚Ä¢ Total analysis: ${totalFunctions} functions, ${regularCount} direct, ${globalCount} global, ${builtinCount} builtin, ${libraryCount} library calls</TD></TR>\n`;
    dot += `        <TR><TD ALIGN="LEFT" BGCOLOR="${brokenCount > 0 ? "#FFCDD2" : "#C8E6C9"}"><FONT COLOR="${brokenCount > 0 ? "#B71C1C" : "#2E7D32"}"><B>DEPENDENCY HEALTH: ${brokenCount > 0 ? brokenPercent + "% BROKEN - REQUIRES IMMEDIATE FIXES" : "100% RESOLVED - EXCELLENT"}</B></FONT></TD></TR>\n`;
    dot += `        <TR><TD ALIGN="LEFT" BGCOLOR="#FFF9C4"><FONT POINT-SIZE="12">Generated: ${this.formatChicagoTimestamp()}</FONT></TD></TR>\n`;
    dot += "      </TABLE>\n";
    dot += "    >];\n";
    dot += "  }\n";

    dot += "}";
    return dot;
  }

  formatChicagoTimestamp() {
    try {
      const custom = this.config.aiTimestamp || null;
      const now = custom ? new Date(custom) : new Date();
      return now.toLocaleString("en-US", {
        timeZone: "America/Chicago",
        hour12: false,
      });
    } catch {
      return new Date().toISOString();
    }
  }

  generateActionableReport(nodes, edges, options = {}) {
    const ts = this.formatChicagoTimestamp();
    const unresolvedEdges = edges.filter((e) => e.broken);
    const totalEdges = edges.length;
    const brokenCount = unresolvedEdges.length;
    const brokenPercent =
      totalEdges > 0 ? ((brokenCount / totalEdges) * 100).toFixed(1) : 0;

    // Critical analysis - identify all problematic patterns
    const criticalIssues = {
      unresolved: unresolvedEdges,
      emptyFiles: [],
      missingHandlers: [],
      orphanedFunctions: [],
      circularDeps: [],
    };

    const byFile = new Map();
    const indexNode = (id) => nodes.get(id);
    const parseNodeId = (id) => {
      // pattern: file::name@line
      const atIdx = id.lastIndexOf("@");
      const base = atIdx >= 0 ? id.slice(0, atIdx) : id;
      const line = atIdx >= 0 ? id.slice(atIdx + 1) : "?";
      const parts = base.split("::");
      const file = parts[0];
      const name = parts[1] || "";
      return { file, name, line };
    };

    // Build reverse edges for upstream callers
    const targetsToSources = new Map();
    for (const e of edges) {
      if (!targetsToSources.has(e.to)) targetsToSources.set(e.to, new Set());
      targetsToSources.get(e.to).add(e.from);
    }

    // Map unresolved by caller file
    for (const e of unresolvedEdges) {
      const fromInfo = parseNodeId(e.from);
      if (!byFile.has(fromInfo.file)) byFile.set(fromInfo.file, []);
      byFile.get(fromInfo.file).push({ from: fromInfo, to: parseNodeId(e.to) });
    }

    let report = "";
    report += `# üö® CRITICAL: Function-Level Dependency Analysis Report\n\n`;
    report += `## ‚ö†Ô∏è BROKEN CALL ANALYSIS - IMMEDIATE ACTION REQUIRED\n\n`;
    report += `**Timestamp (Chicago):** ${ts}\n`;
    report += `**Project:** Chrome Container App\n`;
    report += `**Reference SOP:** chrome-container-app/END-TO-END.md\n\n`;
    report += `### üìä CRITICAL METRICS\n\n`;
    report += `| Metric | Value | Status |\n`;
    report += `|--------|-------|--------|\n`;
    report += `| **Total Function Calls** | ${totalEdges} | - |\n`;
    report += `| **‚úÖ Resolved Calls** | ${totalEdges - brokenCount} | ${totalEdges - brokenCount > 0 ? "‚úì" : "‚ùå"} |\n`;
    report += `| **‚ùå BROKEN/UNRESOLVED** | **${brokenCount}** | **${brokenCount > 0 ? "üî¥ CRITICAL" : "‚úÖ CLEAN"}** |\n`;
    report += `| **Broken Percentage** | **${brokenPercent}%** | ${brokenPercent > 10 ? "üî¥ UNACCEPTABLE" : brokenPercent > 0 ? "‚ö†Ô∏è NEEDS WORK" : "‚úÖ PERFECT"} |\n`;
    report += `\n`;

    if (brokenCount > 0) {
      report += `### üî¥ BROKEN CALLS REQUIRING IMMEDIATE FIX\n\n`;
      report += `**${brokenCount} broken function calls detected. These MUST be fixed:**\n\n`;
    } else {
      report += `### ‚úÖ NO BROKEN CALLS DETECTED\n\n`;
      report += `All function calls are properly resolved. Excellent!\n\n`;
    }

    for (const [file, items] of byFile.entries()) {
      report += `## File: ${file}\n\n`;
      const callers = new Map();
      items.forEach((it) => {
        const key = `${it.from.name}@${it.from.line}`;
        if (!callers.has(key)) callers.set(key, []);
        callers.get(key).push(it);
      });

      for (const [callerKey, list] of callers.entries()) {
        const [callerName, callerLine] = callerKey.split("@");
        report += `### Caller: ${callerName} (line ${callerLine})\n\n`;
        report += `- Unresolved callees (${list.length}):\n`;
        list.forEach((it) => {
          report += `  - ${it.to.name} (expected in ${it.to.file})\n`;
        });

        // Upstream callers of this caller
        const thisNodeIdPrefix = `${file}::${callerName}@`;
        const nodeIds = Array.from(nodes.keys()).filter((id) =>
          id.startsWith(thisNodeIdPrefix),
        );
        const upstream = new Set();
        for (const nid of nodeIds) {
          for (const [target, sources] of targetsToSources.entries()) {
            if (target === nid) {
              for (const s of sources) upstream.add(s);
            }
          }
        }
        if (upstream.size) {
          report += `- Upstream callers:\n`;
          for (const up of upstream) {
            const p = parseNodeId(up);
            report += `  - ${p.name} in ${p.file} (line ${p.line})\n`;
          }
        }

        // Downstream resolved callees
        const downstream = edges.filter(
          (ed) => !ed.broken && ed.from.startsWith(`${file}::${callerName}@`),
        );
        if (downstream.length) {
          report += `- Downstream resolved callees:\n`;
          downstream.forEach((ed) => {
            const p = parseNodeId(ed.to);
            report += `  - ${p.name} in ${p.file} (line ${p.line})\n`;
          });
        }

        // SOP task block
        report += `\n#### Required Tasks (per END-TO-END SOP)\n`;
        report += `- [ ] Implement missing functions or add correct imports for unresolved callees above.\n`;
        report += `- [ ] Validate upstream callers for signature/behavior impact.\n`;
        report += `- [ ] Verify complete path to frontend and update IPC/events as needed.\n`;
        report += `- [ ] Re-run codemap function-level graph; target 0 unresolved calls.\n\n`;
      }
    }

    // Combine SOP + plan for direct agent ingestion
    const sop = this.loadSOP();
    let agent = "";
    agent += `# END-TO-END SOP\n`;
    agent += sop
      ? sop
      : "SOP not found. Proceed with conservative, line-by-line end-to-end validation.";
    agent += `\n\n# ACTIONABLE PLAN (${ts})\n`;
    for (const [file, items] of byFile.entries()) {
      agent += `File ${file}:\n`;
      const callers = new Map();
      items.forEach((it) => {
        const key = `${it.from.name}@${it.from.line}`;
        if (!callers.has(key)) callers.set(key, []);
        callers.get(key).push(it);
      });
      for (const [callerKey, list] of callers.entries()) {
        const [callerName, callerLine] = callerKey.split("@");
        agent += `- Caller ${callerName} (line ${callerLine}): unresolved ‚Üí ${list.map((it) => it.to.name).join(", ")}\n`;
      }
    }
    agent += `\nAfter implementing, regenerate the function-level graph and ensure no [unresolved] nodes remain.`;

    return { reportMarkdown: report, agentMarkdown: agent };
  }

  loadSOP() {
    try {
      const candidates = [
        path.resolve(process.cwd(), "../chrome-container-app/END-TO-END.md"),
        path.resolve(process.cwd(), "chrome-container-app/END-TO-END.md"),
        path.resolve(process.cwd(), "../END-TO-END.md"),
      ];
      for (const p of candidates) {
        if (fs.existsSync(p)) {
          return fs.readFileSync(p, "utf8");
        }
      }
    } catch {}
    return "";
  }
}

