import fs from "fs";
import path from "path";
import * as acorn from "acorn";
import { execSync } from "child_process";
import * as walk from "acorn-walk";

export class FunctionLevelHook {
  constructor(config) {
    this.config = config;
  }

  run(analysisResult, options = {}) {
    const { modules } = analysisResult;

    // Enhanced configuration for comprehensive analysis
    const isComprehensive = options.comprehensive || this.config.alwaysRunFunctionAnalysis || true;
    const shouldDetectBroken = options.detectBrokenCalls || this.config.detectBrokenCalls || true;
    const shouldValidateDeps = options.validateDependencies || this.config.validateAllDependencies || true;
    const shouldGenerateReports = options.report !== false && (this.config.generateActionableReports || true);

    console.log("üîç Function-level analysis configuration:");
    console.log(`  - Comprehensive analysis: ${isComprehensive ? '‚úÖ' : '‚ùå'}`);
    console.log(`  - Broken call detection: ${shouldDetectBroken ? '‚úÖ' : '‚ùå'}`);
    console.log(`  - Dependency validation: ${shouldValidateDeps ? '‚úÖ' : '‚ùå'}`);
    console.log(`  - Actionable reports: ${shouldGenerateReports ? '‚úÖ' : '‚ùå'}`);

    const rules = this.loadRules();
    const fileIndex = this.buildIndex(modules, rules);
    const { nodes, edges } = this.buildFunctionGraphFromIndex(fileIndex, {
      detectBrokenCalls: shouldDetectBroken,
      validateDependencies: shouldValidateDeps,
      comprehensive: isComprehensive
    });

    // Add Chicago timezone timestamp if requested
    let timestamp = "";
    if (options.timestamp) {
      const now = new Date();
      const chicagoTime = now.toLocaleString("en-US", {
        timeZone: "America/Chicago",
        year: "numeric",
        month: "2-digit",
        day: "2-digit",
        hour: "2-digit",
        minute: "2-digit",
        second: "2-digit",
      });
      timestamp = chicagoTime.replace(/[\/\s:,]/g, "-");
    }

    const dotGraph = this.generateDotGraph(nodes, edges, options);

    // Generate timestamped filenames
    const baseName = timestamp
      ? `function-level-graph-${timestamp}`
      : "function-level-graph";
    const outputPath = path.join(this.config.outputDir, `${baseName}.svg`);

    if (!fs.existsSync(this.config.outputDir)) {
      fs.mkdirSync(this.config.outputDir, { recursive: true });
    }

    const dotPath = path.join(this.config.outputDir, `${baseName}.dot`);
    fs.writeFileSync(dotPath, dotGraph);
    execSync(`dot -Tsvg "${dotPath}" -o "${outputPath}"`);

    console.log(`Function-level dependency graph generated: ${outputPath}`);

    // Generate actionable report with Chicago timestamp (always enabled for comprehensive analysis)
    if (shouldGenerateReports) {
      const reportBaseName = timestamp
        ? `actionable-report-${timestamp}`
        : "actionable-report";
      const reportPath = path.join(
        this.config.outputDir,
        `${reportBaseName}.md`,
      );
      const agentPromptPath = path.join(
        this.config.outputDir,
        `agent-input-${timestamp || "latest"}.md`,
      );
      const { reportMarkdown, agentMarkdown } = this.generateActionableReport(
        nodes,
        edges,
        options,
      );
      fs.writeFileSync(reportPath, reportMarkdown, "utf8");
      fs.writeFileSync(agentPromptPath, agentMarkdown, "utf8");
      console.log(`Actionable report generated: ${reportPath}`);
      console.log(`Agent prompt generated: ${agentPromptPath}`);
    }
  }

  // Allow any AI/agent to steer function detection via config/env at runtime
  loadRules() {
    if (this.config.functionRules && Array.isArray(this.config.functionRules)) {
      return this.config.functionRules;
    }
    if (process.env.CODEMAP_RULES) {
      try {
        const parsed = JSON.parse(process.env.CODEMAP_RULES);
        if (Array.isArray(parsed)) return parsed;
      } catch {}
    }
    // Pragmatic defaults (overridable)
    return [
      { nodeType: "FunctionDeclaration", namePropPath: "id.name" },
      { nodeType: "VariableDeclarator", namePropPath: "id.name" },
      { nodeType: "MethodDefinition", namePropPath: "key.name" },
      { nodeType: "Property", namePropPath: "key.name" },
    ];
  }

  buildIndex(modules, rules) {
    const fileIndex = new Map();
    modules.forEach((module) => {
      if (
        !module.source ||
        module.coreModule ||
        module.source.includes("node_modules")
      )
        return;
      if (!/\.(m?js|ts)$/.test(module.source)) return;

      const modulePath = module.source;
      try {
        const code = fs.readFileSync(modulePath, "utf8");
        const ast = acorn.parse(code, {
          sourceType: "module",
          ecmaVersion: "latest",
          locations: true,
        });
        const entry = {
          path: modulePath,
          functions: [], // { name, startLine, endLine }
          exports: new Map(), // exportedName -> localName
          imports: new Map(), // localName -> { source, importedName }
        };

        // imports/exports
        walk.simple(ast, {
          ImportDeclaration: (node) => {
            const source = node.source && node.source.value;
            if (!source) return;
            node.specifiers?.forEach((spec) => {
              const local = spec.local?.name;
              if (!local) return;
              if (spec.type === "ImportNamespaceSpecifier") {
                entry.imports.set(local, {
                  source,
                  importedName: "*",
                  namespace: true,
                });
              } else if (spec.type === "ImportDefaultSpecifier") {
                entry.imports.set(local, { source, importedName: "default" });
              } else {
                const imported = spec.imported?.name || "default";
                entry.imports.set(local, { source, importedName: imported });
              }
            });
          },
          ExportNamedDeclaration: (node) => {
            if (
              node.declaration &&
              node.declaration.type === "FunctionDeclaration"
            ) {
              const name = node.declaration.id?.name;
              if (name) entry.exports.set(name, name);
            }
            node.specifiers?.forEach((spec) => {
              const exported = spec.exported?.name;
              const local = spec.local?.name || exported;
              if (exported) entry.exports.set(exported, local);
            });
          },
          ExportDefaultDeclaration: (node) => {
            if (node.declaration?.id?.name) {
              entry.exports.set("default", node.declaration.id.name);
            } else {
              entry.exports.set("default", "default");
            }
          },
        });

        // functions
        this.collectFunctions(ast, rules, (fnName, node) => {
          if (!fnName) return;
          const startLine = node.loc?.start?.line || 0;
          const endLine = node.loc?.end?.line || startLine;
          entry.functions.push({ name: fnName, startLine, endLine });
        });

        fileIndex.set(modulePath, entry);
      } catch (e) {
        console.error(`Error processing ${modulePath}:`, e);
      }
    });
    return fileIndex;
  }

  collectFunctions(ast, rules, onFunction) {
    const matchers = rules.map((r) => ({
      nodeType: r.nodeType,
      getName: (node) => {
        try {
          const parts = (r.namePropPath || "").split(".");
          let current = node;
          for (const p of parts) {
            if (!current) return null;
            current = current[p];
          }
          return typeof current === "string" ? current : current?.name || null;
        } catch {
          return null;
        }
      },
    }));

    walk.ancestor(ast, {
      FunctionDeclaration: (node) => {
        const rule = matchers.find((m) => m.nodeType === "FunctionDeclaration");
        const name = rule?.getName(node) || node.id?.name;
        onFunction(name, node);
      },
      VariableDeclarator: (node) => {
        const initType = node.init?.type;
        if (
          initType === "FunctionExpression" ||
          initType === "ArrowFunctionExpression"
        ) {
          const rule = matchers.find(
            (m) => m.nodeType === "VariableDeclarator",
          );
          const name = rule?.getName(node) || node.id?.name;
          onFunction(name, node);
        }
      },
      MethodDefinition: (node) => {
        const rule = matchers.find((m) => m.nodeType === "MethodDefinition");
        const name = rule?.getName(node) || node.key?.name;
        onFunction(name, node);
      },
      Property: (node) => {
        if (
          node.value?.type === "FunctionExpression" ||
          node.value?.type === "ArrowFunctionExpression"
        ) {
          const rule = matchers.find((m) => m.nodeType === "Property");
          const name = rule?.getName(node) || node.key?.name;
          onFunction(name, node);
        }
      },
    });
  }

  buildFunctionGraphFromIndex(fileIndex, options = {}) {
    const { detectBrokenCalls = true, validateDependencies = true, comprehensive = true } = options;
    const edges = [];
    const nodes = new Map();

    const fileToExports = new Map();
    const fileToFunctions = new Map();

    for (const [file, entry] of fileIndex.entries()) {
      const fnMap = new Map();
      entry.functions.forEach((fn) => fnMap.set(fn.name, fn));
      fileToFunctions.set(file, fnMap);
      fileToExports.set(file, entry.exports);
      entry.functions.forEach((fn) => {
        const id = `${file}::${fn.name}@${fn.startLine}`;
        nodes.set(id, {
          label: `${path.basename(file)}\\n${fn.name} [${this.formatChicagoTimestamp()}] [${fn.startLine}-${fn.endLine}]`,
          file,
          name: fn.name,
          startLine: fn.startLine,
          endLine: fn.endLine,
        });
      });
    }

    const resolveImport = (fromFile, imp) => {
      const src = imp.source;
      if (!src) return null;
      if (src.startsWith(".")) {
        const abs = path.resolve(path.dirname(fromFile), src);
        const candidates = [
          abs,
          `${abs}.js`,
          `${abs}.mjs`,
          `${abs}.ts`,
          path.join(abs, "index.js"),
        ];
        for (const c of candidates) {
          if (fileIndex.has(c)) return c;
        }
      }
      return null;
    };

    for (const [file, entry] of fileIndex.entries()) {
      try {
        const code = fs.readFileSync(file, "utf8");
        const ast = acorn.parse(code, {
          sourceType: "module",
          ecmaVersion: "latest",
          locations: true,
        });
        this.findFunctionCalls(
          ast,
          file,
          (callerName, calleeIdent, loc, calleeBase, calleeBaseFull) => {
            const callerFn = fileToFunctions.get(file)?.get(callerName);
            const fromId = callerFn
              ? `${file}::${callerFn.name}@${callerFn.startLine}`
              : `${file}::${callerName}@${loc?.start?.line || 0}`;

            let targetFile = file;
            let targetFn =
              fileToFunctions.get(targetFile)?.get(calleeIdent) || null;
            if (!targetFn) {
              let imp = entry.imports.get(calleeIdent);
              if (imp) {
                const resolved = resolveImport(file, imp);
                if (resolved) {
                  targetFile = resolved;
                  const exportedName = imp.importedName;
                  const exportedMap = fileToExports.get(targetFile);
                  const localExportName =
                    exportedMap?.get(exportedName) || exportedName;
                  targetFn =
                    fileToFunctions.get(targetFile)?.get(localExportName) ||
                    null;
                } else {
                  // non-relative import (npm/core) ‚Üí treat as external target
                  const toId = `ext:${imp.source}::${imp.importedName}`;
                  if (!nodes.has(toId)) {
                    nodes.set(toId, {
                      label: `${imp.source}\n${imp.importedName} [external]`,
                      file: imp.source,
                      name: imp.importedName,
                      startLine: 0,
                      endLine: 0,
                    });
                  }
                  edges.push({ from: fromId, to: toId, broken: false });
                  return;
                }
              } else if (calleeBase) {
                // namespace or global base
                const baseImp = entry.imports.get(calleeBase);
                if (baseImp) {
                  // module namespace import or default import used as namespace
                  const display = calleeBaseFull || calleeIdent;
                  const toId = `ext:${baseImp.source}::${display}`;
                  if (!nodes.has(toId)) {
                    nodes.set(toId, {
                      label: `${baseImp.source}\n${display} [external]`,
                      file: baseImp.source,
                      name: display,
                      startLine: 0,
                      endLine: 0,
                    });
                  }
                  edges.push({ from: fromId, to: toId, broken: false });
                  return;
                }
                // Check for i18n translation pattern (i18n.t calls)
                if (calleeBase === "i18n" && calleeIdent === "t") {
                  const toId = `i18n:translation`;
                  if (!nodes.has(toId)) {
                    nodes.set(toId, {
                      label: `i18n\\ntranslation\\n‚Üí localized strings\\nI18N [library]`,
                      file: "i18n-library",
                      name: "i18n.t",
                      startLine: 0,
                      endLine: 0,
                      destination: "localized strings",
                      impact: "I18N",
                    });
                  }
                  edges.push({
                    from: fromId,
                    to: toId,
                    broken: false,
                    type: "library-call",
                  });
                  return;
                }

                // known globals like console.log, JSON.parse, Math.max, etc. - trace their destinations
                const knownGlobals = new Set([
                  "console",
                  "JSON",
                  "Math",
                  "Date",
                  "process",
                  "global",
                  "globalThis",
                  "i18n",
                ]);
                if (knownGlobals.has(calleeBase)) {
                  const toId = `global:${calleeBase}.${calleeIdent}`;
                  if (!nodes.has(toId)) {
                    // Enhanced global node with actual destination tracing
                    const destination = this.getGlobalDestination(
                      `${calleeBase}.${calleeIdent}`,
                    );
                    const impact = this.getGlobalImpact(
                      `${calleeBase}.${calleeIdent}`,
                    );
                    nodes.set(toId, {
                      label: `${calleeBase}\\n${calleeIdent}\\n‚Üí ${destination}\\n${impact} [global]`,
                      file: "global",
                      name: `${calleeBase}.${calleeIdent}`,
                      startLine: 0,
                      endLine: 0,
                      destination: destination,
                      impact: impact,
                    });
                  }
                  // Mark as working global call with proper type
                  edges.push({
                    from: fromId,
                    to: toId,
                    broken: false,
                    type: "global-call",
                  });
                  return;
                }
              } else {
                // bare global functions - trace where they actually go
                const knownGlobalFns = new Set([
                  "parseInt",
                  "parseFloat",
                  "setTimeout",
                  "setInterval",
                  "clearTimeout",
                  "clearInterval",
                  "queueMicrotask",
                  "setImmediate",
                ]);
                if (knownGlobalFns.has(calleeIdent)) {
                  const toId = `global:${calleeIdent}`;
                  if (!nodes.has(toId)) {
                    // Enhanced global node with destination info
                    const destination = this.getGlobalDestination(calleeIdent);
                    nodes.set(toId, {
                      label: `global\\n${calleeIdent}\\n‚Üí ${destination} [global]`,
                      file: "global",
                      name: calleeIdent,
                      startLine: 0,
                      endLine: 0,
                      destination: destination,
                    });
                  }
                  // Mark as working global call, not broken
                  edges.push({
                    from: fromId,
                    to: toId,
                    broken: false,
                    type: "global-call",
                  });
                  return;
                }
              }
            }

            if (targetFn) {
              const toId = `${targetFile}::${targetFn.name}@${targetFn.startLine}`;
              edges.push({ from: fromId, to: toId, broken: false });
            } else {
              // Before marking as broken, check if it's a Node.js built-in
              const fullCallName = calleeBaseFull
                ? `${calleeBaseFull}.${calleeIdent}`
                : calleeIdent;
              if (
                this.isNodeBuiltin(fullCallName) ||
                this.isNodeBuiltin(calleeIdent)
              ) {
                const toId = `global:${fullCallName}`;
                if (!nodes.has(toId)) {
                  const destination = this.getGlobalDestination(fullCallName);
                  const impact = this.getGlobalImpact(fullCallName);
                  nodes.set(toId, {
                    label: `nodejs\\n${fullCallName}\\n‚Üí ${destination}\\n${impact} [builtin]`,
                    file: "nodejs-builtin",
                    name: fullCallName,
                    startLine: 0,
                    endLine: 0,
                    destination: destination,
                    impact: impact,
                  });
                }
                edges.push({
                  from: fromId,
                  to: toId,
                  broken: false,
                  type: "builtin-call",
                });
              } else {
                // Actually unresolved - mark as broken
                const toId = `${file}::${calleeIdent}@?`;
                if (!nodes.has(toId)) {
                  nodes.set(toId, {
                    label: `${path.basename(file)}\\n${calleeIdent} [unresolved] [${this.formatChicagoTimestamp()}]`,
                    file,
                    name: calleeIdent,
                    startLine: 0,
                    endLine: 0,
                  });
                }
                edges.push({ from: fromId, to: toId, broken: true });
              }
            }
          },
        );
      } catch (e) {
        console.error(`Error building edges for ${file}:`, e);
      }
    }

    return { nodes, edges };
  }

  findFunctionCalls(ast, filePath, callback) {
    walk.ancestor(
      ast,
      {
        CallExpression: (node, ancestors) => {
          const fullCallee = this.getNodeName(node.callee);
          let calleeName = fullCallee;
          let calleeBase = null;
          let calleeBaseFull = null;
          if (fullCallee && fullCallee.includes(".")) {
            const parts = fullCallee.split(".");
            calleeBase = parts[0];
            calleeName = parts[parts.length - 1];
            calleeBaseFull = parts.slice(0, parts.length - 1).join(".");
          }
          let callerName = null;

          for (let i = ancestors.length - 2; i >= 0; i--) {
            const ancestor = ancestors[i];
            if (ancestor.type === "FunctionDeclaration") {
              callerName = this.getNodeName(ancestor.id) || "anonymous";
              break;
            }
            if (
              ancestor.type === "FunctionExpression" ||
              ancestor.type === "ArrowFunctionExpression"
            ) {
              const maybeParent = ancestors[i - 1];
              if (
                maybeParent &&
                maybeParent.type === "VariableDeclarator" &&
                maybeParent.id
              ) {
                callerName = this.getNodeName(maybeParent.id) || "anonymous";
              } else if (
                maybeParent &&
                maybeParent.type === "Property" &&
                maybeParent.key
              ) {
                callerName = this.getNodeName(maybeParent.key) || "anonymous";
              } else {
                callerName = "anonymous";
              }
              break;
            }
            if (ancestor.type === "MethodDefinition" && ancestor.key) {
              callerName = this.getNodeName(ancestor.key) || "anonymous";
              break;
            }
          }

          if (calleeName) {
            callback(
              callerName || "anonymous",
              calleeName,
              node.loc,
              calleeBase,
              calleeBaseFull,
            );
          }
        },
      },
      {
        ...walk.base,
        MemberExpression: () => {},
      },
    );
  }

  getNodeName(node) {
    if (!node) return null;
    if (node.type === "Identifier") {
      return node.name;
    }
    if (node.type === "MemberExpression") {
      // return full base + property to detect namespace calls
      const base = this.getNodeName(node.object);
      const prop = this.getNodeName(node.property);
      if (base && prop) return `${base}.${prop}`;
      return prop || base || null;
    }
    return null;
  }

  // Trace where global calls actually go
  getGlobalDestination(globalCall) {
    const destinations = {
      "console.log": "stdout/terminal",
      "console.error": "stderr/terminal",
      "console.warn": "stderr/terminal",
      "console.info": "stdout/terminal",
      "process.exit": "system exit",
      "process.on": "event listener",
      "process.cwd": "file system",
      "process.env": "environment",
      "process.argv": "arguments",
      "process.pid": "process info",
      "JSON.stringify": "string output",
      "JSON.parse": "object creation",
      "Math.round": "number calc",
      "Math.floor": "number calc",
      "Math.ceil": "number calc",
      "Math.random": "random gen",
      "Date.now": "timestamp",
      setTimeout: "event queue",
      setInterval: "event queue",
      clearTimeout: "event queue",
      clearInterval: "event queue",
      parseInt: "number parse",
      parseFloat: "number parse",
      require: "module loader",
      "Buffer.from": "buffer creation",
      "Buffer.alloc": "buffer creation",
      __dirname: "directory path",
      __filename: "file path",
    };
    return destinations[globalCall] || "runtime";
  }

  // Check if a call is a Node.js built-in that should be treated as global, not broken
  isNodeBuiltin(callName) {
    const nodeBuiltins = [
      "console.log",
      "console.error",
      "console.warn",
      "console.info",
      "process.exit",
      "process.on",
      "process.cwd",
      "process.env",
      "process.argv",
      "process.pid",
      "JSON.stringify",
      "JSON.parse",
      "Math.round",
      "Math.floor",
      "Math.ceil",
      "Math.random",
      "Date.now",
      "setTimeout",
      "setInterval",
      "clearTimeout",
      "clearInterval",
      "parseInt",
      "parseFloat",
      "require",
      "Buffer.from",
      "Buffer.alloc",
      "__dirname",
      "__filename",
    ];

    // Also check for single-name built-ins (like 'require')
    const singleNameBuiltins = [
      "require",
      "setTimeout",
      "setInterval",
      "clearTimeout",
      "clearInterval",
      "parseInt",
      "parseFloat",
      "__dirname",
      "__filename",
    ];

    return (
      nodeBuiltins.includes(callName) || singleNameBuiltins.includes(callName)
    );
  }

  // Determine the impact/purpose of global calls
  getGlobalImpact(globalCall) {
    const impacts = {
      "console.log": "DEBUG/LOG",
      "console.error": "ERROR LOG",
      "console.warn": "WARNING",
      "console.info": "INFO LOG",
      "process.exit": "APP EXIT",
      "process.on": "LISTENER",
      "process.cwd": "PATH INFO",
      "JSON.stringify": "SERIALIZE",
      "JSON.parse": "DESERIALIZE",
      "Math.round": "MATH OP",
      "Math.floor": "MATH OP",
      "Math.ceil": "MATH OP",
      "Math.random": "RANDOM",
      "Date.now": "TIMESTAMP",
      setTimeout: "ASYNC TIMER",
      setInterval: "RECURRING",
      clearTimeout: "CANCEL TIMER",
      clearInterval: "CANCEL LOOP",
      parseInt: "TYPE CONVERT",
      parseFloat: "TYPE CONVERT",
    };
    return impacts[globalCall] || "RUNTIME";
  }

  generateDotGraph(nodes, edges, options = {}) {
    let dot = "digraph FunctionLevelGraph {\n";
    dot += "  rankdir=TB;\n"; // Top-to-bottom for hierarchical flow
    dot += '  fontname="Consolas";\n';
    dot += '  bgcolor="#FAFAFA";\n';
    dot += '  pad="0.5";\n';
