        dot += `}\n\n`;
      }

      // Add files within this layer
      for (const { file, items } of layer.files) {
        dot += `    // File: ${file}\n`;

        for (const { id, info } of items) {
          const isUnresolved = id.includes("@?") || info.name === "unresolved";
          const isExternal = id.startsWith("ext:") || id.startsWith("global:");

          let fillColor = layer.bgColor;
