          }

          // Enhanced label with file and line info
          const fileName = path.basename(info.file);
          const functionLabel = `${fileName}\\n${info.name}\\nL:${info.startLine}${info.endLine !== info.startLine ? `-${info.endLine}` : ""}`;

          dot += `    "${id}" [label="${functionLabel}", fillcolor="${fillColor}", color="${borderColor}", fontcolor="${fontColor}", style="${style}", penwidth=2];\n`;
        }
        dot += `\n`;
      }

      dot += `  }\n\n`;
    }

    // Edges with enhanced styling for broken/resolved/global calls
    dot += "\n  // Function call edges\n";
    const brokenEdges = [];
    const globalCallEdges = [];
    const builtinCallEdges = [];
    const libraryCallEdges = [];
    const regularCallEdges = [];

    for (const e of edges) {
      if (e.broken) {
        brokenEdges.push(e);
      } else if (e.type === "global-call") {
        globalCallEdges.push(e);
      } else if (e.type === "builtin-call") {
        builtinCallEdges.push(e);
      } else if (e.type === "library-call") {
        libraryCallEdges.push(e);
      } else {
        regularCallEdges.push(e);
      }
    }

    // Draw regular working edges first (they go behind)
    for (const e of regularCallEdges) {
      const edgeLabel = e.label || "calls";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#1976D2", `;
      dot += `fontcolor="#1976D2", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=2, `;
      dot += `style="solid"`;
      dot += `];\n`;
    }

    // Draw global call edges with special styling
    dot += "\n  // GLOBAL CALL EDGES - Runtime/System Calls\n";
    for (const e of globalCallEdges) {
      const edgeLabel = e.label || "global call";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#9C27B0", `;
      dot += `fontcolor="#9C27B0", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=2, `;
      dot += `style="dotted", `;
      dot += `fontsize=8`;
      dot += `];\n`;
    }

    // Draw built-in call edges with distinct styling
    dot += "\n  // BUILTIN CALL EDGES - Node.js Built-ins\n";
