      const edgeLabel = e.label || "builtin";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#607D8B", `;
      dot += `fontcolor="#607D8B", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=2, `;
      dot += `style="dotted", `;
      dot += `fontsize=8`;
      dot += `];\n`;
    }

    // Draw library call edges (i18n, etc.)
    dot += "\n  // LIBRARY CALL EDGES - External Libraries\n";
    for (const e of libraryCallEdges) {
      const edgeLabel = e.label || "library";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#4CAF50", `;
      dot += `fontcolor="#4CAF50", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=2, `;
      dot += `style="solid", `;
      dot += `fontsize=8`;
      dot += `];\n`;
    }

    // Draw broken edges on top (more visible)
    dot += "\n  // BROKEN/UNRESOLVED CALLS - CRITICAL\n";
    for (const e of brokenEdges) {
      const edgeLabel = e.label || "BROKEN CALL";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#F44336", `;
      dot += `fontcolor="#F44336", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=3, `;
      dot += `style="dashed", `;
      dot += `fontsize=10, `;
      dot += `fontname="Arial Black"`;
      dot += `];\n`;
    }

    // Add comprehensive analysis summary including global calls
    const totalCalls = edges.length;
    const brokenCount = brokenEdges.length;
    const globalCount = globalCallEdges.length;
    const builtinCount = builtinCallEdges.length;
    const libraryCount = libraryCallEdges.length;
    const regularCount = regularCallEdges.length;
    const resolvedCount =
      globalCount + builtinCount + libraryCount + regularCount;
    const brokenPercent =
      totalCalls > 0 ? ((brokenCount / totalCalls) * 100).toFixed(1) : 0;
    const globalPercent =
      totalCalls > 0 ? ((globalCount / totalCalls) * 100).toFixed(1) : 0;
    const builtinPercent =
      totalCalls > 0 ? ((builtinCount / totalCalls) * 100).toFixed(1) : 0;
    const libraryPercent =
      totalCalls > 0 ? ((libraryCount / totalCalls) * 100).toFixed(1) : 0;
    const totalFiles = new Set(Array.from(nodes.values()).map((n) => n.file))
      .size;
    const totalFunctions = nodes.size;

    dot += "\n  // COMPLETE FUNCTION-LEVEL DEPENDENCY ANALYSIS SUMMARY\n";
    dot += "  subgraph cluster_analysis_summary {\n";
