#!/usr/bin/env node

/**
 * CodeMap Tool - Visual Dependency Graph Generator
 * Generates interactive dependency graphs for codebases
 */

import fs from "fs";
import path from "path";
import { execSync } from "child_process";
import { pathToFileURL, fileURLToPath } from "url";
import { CodeMapAnalyzer } from "../lib/analyzer.js";
import { GraphGenerator } from "../lib/graph-generator.js";
import { HookManager } from "../lib/hook-manager.js";
import { FunctionLevelHook } from "../hooks/function-level-hook.js";

export class CodeMapTool {
  constructor() {
    this.config = null;
    this.analyzer = null;
    this.generator = null;
    this.hookManager = null;
  }

  async loadConfig() {
    // Resolve a temp output directory inside the codemap-tool package regardless of cwd
    const __filename = fileURLToPath(import.meta.url);
    const __dirname = path.dirname(__filename);
    const defaultOutputDir = path.join(__dirname, "..", "tmp");

    const defaultConfig = {
      outputDir: defaultOutputDir,
      format: "svg",
      depth: 5,
      excludePatterns: [
        "node_modules/**",
        "*.test.*",
        "*.spec.*",
        "dist/**",
        "build/**",
      ],
      includePatterns: [
        "**/*.js",
        "**/*.ts",
        "**/*.jsx",
        "**/*.tsx",
        "**/*.mjs",
      ],
      clustering: "directory",
      showExternals: false,
      // ALWAYS ENABLE: Comprehensive function analysis with broken call detection
      alwaysRunFunctionAnalysis: true,
      detectBrokenCalls: true,
      validateAllDependencies: true,
      generateActionableReports: true,
      interactive: true,
      theme: "default",
    };

    // Try to load project-specific config
    const configPath = path.join(process.cwd(), ".codemap.config.js");
    if (fs.existsSync(configPath)) {
      // support ESM dynamic import of user config
      try {
        const imported = await import(pathToFileURL(configPath).href);
        const userConfig = imported.default || imported;
        return { ...defaultConfig, ...userConfig };
      } catch (e) {
        console.warn(
          "Failed to import .codemap.config.js, using defaults:",
          e.message,
        );
      }
    }

    return defaultConfig;
  }

  async run(args) {
    const options = this.parseArgs(args);
    this.config = await this.loadConfig();
    // Allow AI steering via env/CLI
    if (options.aiMeta) {
      try {
        const meta = JSON.parse(options.aiMeta);
        this.config.aiMeta = meta;
      } catch (e) {
        console.warn("Failed to parse --ai-meta JSON:", e.message);
      }
    } else if (process.env.CODEMAP_META) {
      try {
        this.config.aiMeta = JSON.parse(process.env.CODEMAP_META);
      } catch {}
    }
    if (options.timestamp) {
      this.config.aiTimestamp = options.timestamp;
    } else if (process.env.CODEMAP_TIMESTAMP) {
      this.config.aiTimestamp = process.env.CODEMAP_TIMESTAMP;
    }
    this.analyzer = new CodeMapAnalyzer(this.config);
    this.generator = new GraphGenerator(this.config);
    this.hookManager = new HookManager(this.config);

    try {
      console.log("üó∫Ô∏è  CodeMap Tool - Analyzing dependencies...");

      // Analyze the codebase
      const analysisResult = await this.analyzer.analyze(
        options.path || process.cwd(),
        {
          depth: options.depth ?? this.config.depth,
          excludePatterns: options.exclude ?? this.config.excludePatterns,
          includePatterns: options.include ?? this.config.includePatterns,
          dependencyCruiserOptions: this.config.dependencyCruiserOptions,
        },
      );

      // Generate the graph
      const outputPath = await this.generator.generate(analysisResult, {
        format: options.format ?? this.config.format,
        output: options.output,
        clustering: options.clustering ?? this.config.clustering,
        baseDir: options.path || process.cwd(),
      });

      console.log(`‚úÖ Dependency graph generated: ${outputPath}`);

      // ALWAYS run comprehensive function-level analysis with broken call detection
      console.log("üîç Running comprehensive function-level analysis with broken call detection...");
      const functionHook = new FunctionLevelHook(this.config);
      functionHook.run(analysisResult, {
        timestamp: options.timestamp || true, // Always include timestamp
        report: true, // Always generate actionable report
        detectBrokenCalls: true, // Always detect broken calls
        validateDependencies: true, // Always validate dependencies
        baseDir: options.path || process.cwd(),
        comprehensive: true, // Enable comprehensive analysis
      });
      }

      // Update hooks if requested
      if (options.updateHooks) {
        await this.hookManager.updateHooks();
        console.log("üîó Claude Code hooks updated");
      }

      return outputPath;
    } catch (error) {
      console.error("‚ùå Error generating codemap:", error.message);
      process.exit(1);
    }
  }

  parseArgs(args) {
    const options = {};

    for (let i = 0; i < args.length; i++) {
      const arg = args[i];

      switch (arg) {
        case "--path":
          options.path = args[++i];
          break;
        case "--format":
          options.format = args[++i];
          break;
        case "--output":
          options.output = args[++i];
          break;
        case "--depth":
          options.depth = parseInt(args[++i]);
          break;
        case "--exclude":
          options.exclude = args[++i].split(",");
          break;
        case "--include":
          options.include = args[++i].split(",");
          break;
        case "--cluster-by":
          options.clustering = args[++i];
          break;
        case "--level":
          options.level = args[++i];
          break;
        case "--quick-update":
          options.quickUpdate = true;
          break;
        case "--update-hooks":
          options.updateHooks = true;
          break;
        case "--watch":
          options.watch = true;
          break;
        case "--interactive":
          options.interactive = true;
          break;
        case "--ai-meta":
          options.aiMeta = args[++i];
          break;
        case "--timestamp":
          options.timestamp = true;
          break;
        case "--report":
          options.report = true;
          break;
        case "--help":
          this.showHelp();
          process.exit(0);
          break;
      }
    }

    return options;
  }

  showHelp() {
    console.log(`
CodeMap Tool - Visual Dependency Graph Generator

Usage: codemap [options]

Options:
  --path <dir>           Target directory to analyze (default: current)
  --format <type>        Output format: svg, png, html, json (default: svg)
  --output <file>        Output filename (default: auto-generated)
  --depth <number>       Maximum dependency depth (default: 5)
  --exclude <patterns>   Comma-separated exclude patterns
  --include <patterns>   Comma-separated include patterns
  --cluster-by <type>    Grouping: directory, type, module (default: directory)
  --level <type>         Analysis level: file, function (default: file)
  --quick-update         Fast incremental update
  --update-hooks         Update Claude Code hooks
  --watch                Watch for changes and auto-update
  --interactive          Generate interactive HTML output (not yet implemented)
  --help                 Show this help message

Examples:
  codemap                                    # Analyze current directory
  codemap --path ./src --format svg         # Analyze src/ directory
  codemap --depth 3 --exclude "*.test.*"    # Limit depth and exclude tests
  codemap --interactive --format html       # Generate interactive HTML
`);
  }
}

