      timestampAllEntries: true,
      autoAnalyzeSVG: true,
      comprehensiveBrokenCallFlow: true,
      interactive: true,
      theme: "default",
    };

    // Try to load project-specific config
    const configPath = path.join(process.cwd(), ".codemap.config.js");
    if (fs.existsSync(configPath)) {
      // support ESM dynamic import of user config
      try {
        const imported = await import(pathToFileURL(configPath).href);
