  /**
   * Analyze SVG for broken calls and generate optimization recommendations
   */
  async analyzeSVGForBrokenCalls(analysisResult, timestamp, options = {}) {
    try {
      const svgPattern = path.join(this.config.outputDir, "*function-level-graph*.svg");
      const reportPattern = path.join(this.config.outputDir, "*actionable-report*.md");

      // Find the latest SVG and report files
      const { execSync } = await import('child_process');
      const svgFiles = execSync(`ls -t ${svgPattern} 2>/dev/null || true`, { encoding: 'utf8' }).trim().split('\n').filter(f => f);
      const reportFiles = execSync(`ls -t ${reportPattern} 2>/dev/null || true`, { encoding: 'utf8' }).trim().split('\n').filter(f => f);

      if (svgFiles.length === 0) {
        console.log("‚ö†Ô∏è No SVG files found for analysis");
        return null;
      }

      const latestSVG = svgFiles[0];
      const latestReport = reportFiles[0];

      console.log(`üìä Analyzing SVG: ${path.basename(latestSVG)}`);

      // Parse SVG to extract broken call information
      const svgContent = fs.readFileSync(latestSVG, 'utf8');
      const brokenCallNodes = this.extractBrokenCallsFromSVG(svgContent);
      const totalNodes = this.extractTotalNodesFromSVG(svgContent);

      // Generate comprehensive broken call analysis
      const analysisReport = this.generateBrokenCallAnalysis(brokenCallNodes, totalNodes, timestamp, {
        svgPath: latestSVG,
        reportPath: latestReport,
        baseDir: options.path || process.cwd()
      });

      // Save the analysis to a timestamped file
      const analysisPath = path.join(this.config.outputDir, `broken-call-analysis-${timestamp}.md`);
      fs.writeFileSync(analysisPath, analysisReport, 'utf8');

      console.log(`‚úÖ SVG broken call analysis complete: ${analysisPath}`);

      // Output immediate summary
      if (brokenCallNodes.length > 0) {
        console.log(`\nüî¥ CRITICAL SVG ANALYSIS RESULTS:`);
        console.log(`   üìà Total nodes: ${totalNodes}`);
        console.log(`   üí• Broken calls: ${brokenCallNodes.length}`);
        console.log(`   üìä Success rate: ${((totalNodes - brokenCallNodes.length) / totalNodes * 100).toFixed(1)}%`);
        console.log(`   ‚ö†Ô∏è  IMMEDIATE ACTION: Review ${analysisPath}`);
      } else {
        console.log(`\n‚úÖ SVG ANALYSIS: All calls properly resolved (${totalNodes} total nodes)`);
      }

      return {
        svgPath: latestSVG,
        analysisPath,
        brokenCallCount: brokenCallNodes.length,
        totalNodeCount: totalNodes,
        brokenCalls: brokenCallNodes
      };

    } catch (error) {
      console.error("‚ùå Error analyzing SVG for broken calls:", error.message);
      return null;
    }
  }

  /**
   * Extract broken call nodes from SVG content
   */
  extractBrokenCallsFromSVG(svgContent) {
    const brokenCalls = [];

    // Look for nodes marked as unresolved or broken
    const nodePattern = /<text[^>]*>(.*?unresolved.*?)<\/text>/gi;
    const brokenEdgePattern = /stroke="red"|stroke="#ff0000"|stroke="#red"/gi;

    let match;
    while ((match = nodePattern.exec(svgContent)) !== null) {
      const nodeText = match[1];
      const cleanText = nodeText.replace(/\\n/g, ' ').trim();
      brokenCalls.push({
        type: 'unresolved-node',
        description: cleanText,
        severity: 'critical'
      });
    }

    // Count red edges (broken connections)
    const redEdges = (svgContent.match(brokenEdgePattern) || []).length;
    for (let i = 0; i < redEdges; i++) {
      brokenCalls.push({
        type: 'broken-edge',
        description: 'Broken function call connection',
        severity: 'critical'
      });
    }

    return brokenCalls;
  }

  /**
   * Extract total node count from SVG
   */
  extractTotalNodesFromSVG(svgContent) {
    // Count all function nodes (rectangles with text)
    const nodePattern = /<g[^>]*class="node"[^>]*>/gi;
    const matches = svgContent.match(nodePattern) || [];
    return matches.length;
  }

  /**
   * Generate comprehensive broken call analysis report
   */
  generateBrokenCallAnalysis(brokenCalls, totalNodes, timestamp, context = {}) {
    const brokenCount = brokenCalls.length;
    const successRate = totalNodes > 0 ? ((totalNodes - brokenCount) / totalNodes * 100) : 100;

    let report = `# üî¥ BROKEN CALL ANALYSIS REPORT\n\n`;
    report += `**Generated:** ${new Date().toISOString()}\n`;
    report += `**Analysis Timestamp:** ${timestamp}\n`;
    report += `**SVG Source:** ${context.svgPath || 'Unknown'}\n`;
    report += `**Base Directory:** ${context.baseDir || 'Unknown'}\n\n`;

    report += `## üìä CRITICAL METRICS\n\n`;
    report += `| Metric | Value | Status |\n`;
    report += `|--------|-------|--------|\n`;
    report += `| **Total Function Nodes** | ${totalNodes} | - |\n`;
    report += `| **‚úÖ Working Calls** | ${totalNodes - brokenCount} | ${successRate > 90 ? "‚úÖ" : "‚ö†Ô∏è"} |\n`;
    report += `| **‚ùå BROKEN CALLS** | **${brokenCount}** | **${brokenCount === 0 ? "‚úÖ PERFECT" : brokenCount < 5 ? "‚ö†Ô∏è MINOR" : "üî¥ CRITICAL"}** |\n`;
    report += `| **Success Rate** | **${successRate.toFixed(1)}%** | ${successRate >= 95 ? "üü¢ EXCELLENT" : successRate >= 85 ? "üü° ACCEPTABLE" : "üî¥ UNACCEPTABLE"} |\n\n`;

    if (brokenCount > 0) {
      report += `## üö® BROKEN CALLS REQUIRING IMMEDIATE ATTENTION\n\n`;

      const unresolvedNodes = brokenCalls.filter(bc => bc.type === 'unresolved-node');
      const brokenEdges = brokenCalls.filter(bc => bc.type === 'broken-edge');

      if (unresolvedNodes.length > 0) {
        report += `### üìç Unresolved Function Nodes (${unresolvedNodes.length})\n\n`;
        unresolvedNodes.forEach((node, i) => {
          report += `${i + 1}. **${node.description}**\n`;
          report += `   - Type: Function definition not found\n`;
          report += `   - Severity: ${node.severity}\n`;
          report += `   - Action: Implement missing function or fix import\n\n`;
        });
      }

      if (brokenEdges.length > 0) {
        report += `### üîó Broken Function Call Connections (${brokenEdges.length})\n\n`;
        brokenEdges.forEach((edge, i) => {
          report += `${i + 1}. **${edge.description}**\n`;
          report += `   - Type: Call to missing/unavailable function\n`;
          report += `   - Severity: ${edge.severity}\n`;
          report += `   - Action: Fix function reference or implement missing function\n\n`;
        });
      }

      report += `## üéØ RECOMMENDED ACTIONS\n\n`;
      report += `1. **Immediate Priority**: Fix the ${brokenCount} broken calls listed above\n`;
      report += `2. **Validation**: Re-run codemap analysis after fixes to verify resolution\n`;
      report += `3. **Prevention**: Add comprehensive testing to catch broken calls before deployment\n`;
      report += `4. **Monitoring**: Implement continuous dependency validation in CI/CD pipeline\n\n`;

    } else {
      report += `## ‚úÖ EXCELLENT CODE HEALTH\n\n`;
      report += `All ${totalNodes} function nodes are properly connected with no broken calls detected.\n\n`;
      report += `### üéØ MAINTENANCE RECOMMENDATIONS\n\n`;
      report += `1. **Continue Excellence**: Maintain current coding standards\n`;
      report += `2. **Regular Analysis**: Run codemap analysis regularly to catch issues early\n`;
      report += `3. **Documentation**: Update architecture documentation based on current dependency graph\n\n`;
    }

    report += `## üìã NEXT STEPS\n\n`;
    if (context.reportPath) {
      report += `- Review detailed function analysis: ${context.reportPath}\n`;
    }
    report += `- Review SVG dependency graph: ${context.svgPath}\n`;
    report += `- Run targeted fixes using: \`./scripts/targetedfix [function-names]\`\n\n`;

    report += `---\n`;
    report += `*Report generated by CodeMap Tool with comprehensive broken call detection*\n`;

    return report;
  }
}

