      const edgeLabel = e.label || "global call";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#9C27B0", `;
      dot += `fontcolor="#9C27B0", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=2, `;
      dot += `style="dotted", `;
      dot += `fontsize=8`;
      dot += `];\n`;
    }

    // Draw built-in call edges with distinct styling
    dot += "\n  // BUILTIN CALL EDGES - Node.js Built-ins\n";
    for (const e of builtinCallEdges) {
      const edgeLabel = e.label || "builtin";
      dot += `  "${e.from}" -> "${e.to}" [`;
      dot += `color="#607D8B", `;
      dot += `fontcolor="#607D8B", `;
      dot += `label="${edgeLabel}", `;
      dot += `penwidth=2, `;
      dot += `style="dotted", `;
