// Global error handlers to prevent crashes from EPIPE and other stream errors
process.on('uncaughtException', error => {
  if (error.code === 'EPIPE' || error.errno === 'EPIPE') {
    console.error(i18n.t('errors.epipe.ignored', { message: error.message }));
    return;
  }
  console.error(i18n.t('errors.uncaught.exception', { error }));
});

process.on('unhandledRejection', (reason, promise) => {
  console.error(i18n.t('errors.unhandled.rejection', { promise, reason }));
});

