   489â†’    // Hit testing with enhanced handle detection
   490â†’    getShapeAtPoint(point, margin = 0) {
   491â†’        // First check if we're over a resize handle
   492â†’        const handleHit = this.getResizeHandleAtPoint(point);
   493â†’        if (handleHit) {
   494â†’            // Update cursor based on handle
   495â†’            if (this.renderer && this.renderer.updateCursorFromHandle) {
   496â†’                this.renderer.updateCursorFromHandle(handleHit);
   497â†’            }
   498â†’            return null; // Don't return shape when over handle
   499â†’        } else {
   500â†’            // Clear handle hover state
   501â†’            if (this.renderer && this.renderer.clearHandleHover) {
   502â†’                this.renderer.clearHandleHover();
   503â†’            }
   504â†’        }
   505â†’        
   506â†’        const shapes = this.getShapes();
   507â†’        const camera = this.store.getCurrentCamera();
   508â†’        
   509â†’        // Convert screen point to page point
   510â†’        const pagePoint = camera ? camera.screenToPage(point) : point;
   511â†’        
   512â†’        // Test shapes in reverse order (top to bottom)
   513â†’        for (let i = shapes.length - 1; i >= 0; i--) {
   514â†’            const shape = shapes[i];
   515â†’            if (this.isPointInShape(pagePoint, shape, margin)) {
   516â†’                return shape;
   517â†’            }
   518â†’        }
   519â†’        
   520â†’        return null;
   521â†’    }
