   523â†’    // RESIZE CHUNK 1: Add resize handle hit detection
   524â†’    getResizeHandleAtPoint(point) {
   525â†’        if (!point || typeof point.x === 'undefined' || typeof point.y === 'undefined') {
   526â†’            return null; // Invalid point
   527â†’        }
   528â†’        
   529â†’        const selectedShapes = this.getSelectedShapes();
   530â†’        if (selectedShapes.length !== 1) {
   531â†’            return null; // Only show handles for single selection
   532â†’        }
   533â†’        
   534â†’        const shape = selectedShapes[0];
   535â†’        const bounds = shape.getBounds();
   536â†’        const camera = this.store.getCurrentCamera();
   537â†’        const handleSize = 8;
   538â†’        const scale = camera ? 1 / camera.z : 1;
   539â†’        const scaledHandleSize = handleSize * scale;
   540â†’        const hitMargin = scaledHandleSize / 2;
   541â†’        
   542â†’        // Convert screen point to page point
   543â†’        const pagePoint = camera ? camera.screenToPage(point) : point;
   544â†’        
   545â†’        console.log('ğŸ”§ RESIZE CHUNK 1: Testing handle hit at point:', {
   546â†’            screenPoint: point,
   547â†’            pagePoint: pagePoint,
   548â†’            bounds: bounds,
   549â†’            handleSize: scaledHandleSize,
   550â†’            camera: camera ? { x: camera.x, y: camera.y, z: camera.z } : null
   551â†’        });
   552â†’        
   553â†’        // Define handle positions (same as in renderer)
   554â†’        const handles = [
   555â†’            { x: bounds.x, y: bounds.y, type: 'top-left' },
   556â†’            { x: bounds.x + bounds.w, y: bounds.y, type: 'top-right' },
   557â†’            { x: bounds.x, y: bounds.y + bounds.h, type: 'bottom-left' },
   558â†’            { x: bounds.x + bounds.w, y: bounds.y + bounds.h, type: 'bottom-right' },
   559â†’            { x: bounds.x + bounds.w / 2, y: bounds.y, type: 'top' },
   560â†’            { x: bounds.x + bounds.w / 2, y: bounds.y + bounds.h, type: 'bottom' },
   561â†’            { x: bounds.x, y: bounds.y + bounds.h / 2, type: 'left' },
   562â†’            { x: bounds.x + bounds.w, y: bounds.y + bounds.h / 2, type: 'right' }
   563â†’        ];
   564â†’        
   565â†’        // Test each handle
   566â†’        for (const handle of handles) {
   567â†’            const distance = Math.hypot(
   568â†’                pagePoint.x - handle.x,
   569â†’                pagePoint.y - handle.y
   570â†’            );
   571â†’            
   572â†’            if (distance <= hitMargin) {
   573â†’                console.log('âœ… RESIZE CHUNK 1: Handle hit detected:', handle.type);
   574â†’                return {
   575â†’                    shape: shape,
   576â†’                    handle: handle.type,
   577â†’                    handlePos: { x: handle.x, y: handle.y },
   578â†’                    distance: distance
   579â†’                };
   580â†’            }
   581â†’        }
   582â†’        
   583â†’        console.log('âŒ RESIZE CHUNK 1: No handle hit');
   584â†’        return null;
   585â†’    }
