   587â†’    isPointInShape(point, shape, margin = 0) {
   588â†’        const bounds = shape.getBounds();
   589â†’        
   590â†’        return point.x >= bounds.x - margin &&
   591â†’               point.x <= bounds.x + bounds.w + margin &&
   592â†’               point.y >= bounds.y - margin &&
   593â†’               point.y <= bounds.y + bounds.h + margin;
   594â†’    }
   595â†’
   596â†’    getShapesInBounds(bounds) {
   597â†’        const shapes = this.getShapes();
   598â†’        return shapes.filter(shape => {
   599â†’            const shapeBounds = shape.getBounds();
   600â†’            return this.boundsIntersect(bounds, shapeBounds);
   601â†’        });
   602â†’    }
   603â†’
   604â†’    boundsIntersect(a, b) {
   605â†’        return !(a.x + a.w < b.x || 
   606â†’                 b.x + b.w < a.x || 
   607â†’                 a.y + a.h < b.y || 
   608â†’                 b.y + b.h < a.y);
   609â†’    }
