  1106â†’    // ENHANCEMENT: Resize selected shapes by delta amounts
  1107â†’    resizeSelectedShapes(deltaW, deltaH) {
  1108â†’        const selectedShapes = this.editor.getSelectedShapes();
  1109â†’        if (selectedShapes.length === 0) return;
  1110â†’        
