  1112â†’        
  1113â†’        this.editor.store.transaction(() => {
  1114â†’            selectedShapes.forEach(shape => {
  1115â†’                const currentBounds = shape.getBounds();
  1116â†’                const newW = Math.max(20, currentBounds.w + deltaW);
  1117â†’                const newH = Math.max(20, currentBounds.h + deltaH);
  1118â†’                
  1119â†’                // Update shape properties
  1120â†’                if (shape.props) {
  1121â†’                    shape.props.w = newW;
  1122â†’                    shape.props.h = newH;
  1123â†’                } else {
