  1125â†’                    shape.h = newH;
  1126â†’                }
  1127â†’                
  1128â†’                this.editor.store.put(shape);
  1129â†’                
  1130â†’                // Update HTML container if it's an embed
  1131â†’                if ((shape.type === 'embed' || shape.type === 'youtube') && this.editor.htmlContainer) {
  1132â†’                    this.editor.htmlContainer.updateShapeContainer(shape.id);
  1133â†’                }
  1134â†’                
  1135â†’                console.log(`ğŸ“ Resized ${shape.id} to ${newW}Ã—${newH}`);
  1136â†’            });
  1137â†’        });
