  1330â†’    // UNIVERSAL SMART CONTEXTUAL INPUT SYSTEM
  1331â†’    analyzeFullContext(event) {
  1332â†’        const pointerPos = {
  1333â†’            x: event.clientX - this.editor.canvas.getBoundingClientRect().left,
  1334â†’            y: event.clientY - this.editor.canvas.getBoundingClientRect().top
  1335â†’        };
  1336â†’        
  1337â†’        const currentTool = this.editor.getCurrentTool();
  1338â†’        const selectedShapes = this.editor.getSelectedShapes();
  1339â†’        const shapeAtPoint = this.editor.getShapeAtPoint(pointerPos);
  1340â†’        const resizeHandle = this.editor.getResizeHandleAtPoint(pointerPos);
  1341â†’        
  1342â†’        return {
  1343â†’            pointer: pointerPos,
  1344â†’            currentTool: currentTool ? currentTool.id : 'select',
  1345â†’            selectedShapes: selectedShapes,
  1346â†’            selectedCount: selectedShapes.length,
  1347â†’            shapeAtPoint: shapeAtPoint,
  1348â†’            resizeHandle: resizeHandle,
  1349â†’            isOverShape: !!shapeAtPoint,
  1350â†’            isOverHandle: !!resizeHandle,
  1351â†’            isEmptyCanvas: !shapeAtPoint && !resizeHandle
  1352â†’        };
  1353â†’    }
  1354â†’
  1355â†’    determineContextualAction(context) {
  1356â†’        // Priority-based contextual decision system
  1357â†’        
  1358â†’        // HIGHEST PRIORITY: Resize handle detected
  1359â†’        if (context.isOverHandle && context.selectedCount > 0) {
  1360â†’            return {
  1361â†’                type: 'resize',
  1362â†’                target: context.selectedShapes,
  1363â†’                handle: context.resizeHandle,
  1364â†’                description: 'Resize selected shapes'
  1365â†’            };
  1366â†’        }
  1367â†’        
  1368â†’        // HIGH PRIORITY: Shape selected and pointer over selected shape = move
  1369â†’        if (context.selectedCount > 0 && context.shapeAtPoint && 
  1370â†’            context.selectedShapes.some(s => s.id === context.shapeAtPoint.id)) {
  1371â†’            return {
  1372â†’                type: 'move',
  1373â†’                target: context.selectedShapes,
  1374â†’                description: 'Move selected shapes'
  1375â†’            };
  1376â†’        }
  1377â†’        
  1378â†’        // MEDIUM PRIORITY: Shape creation tools
  1379â†’        if (['rectangle', 'ellipse', 'draw'].includes(context.currentTool)) {
  1380â†’            return {
  1381â†’                type: 'create',
  1382â†’                tool: context.currentTool,
  1383â†’                description: `Create ${context.currentTool} shape`
  1384â†’            };
  1385â†’        }
  1386â†’        
  1387â†’        // MEDIUM PRIORITY: Eraser tool over shape
  1388â†’        if (context.currentTool === 'eraser' && context.isOverShape) {
  1389â†’            return {
  1390â†’                type: 'erase',
  1391â†’                target: context.shapeAtPoint,
  1392â†’                description: 'Erase shape'
  1393â†’            };
  1394â†’        }
  1395â†’        
  1396â†’        // LOW PRIORITY: Shape at point but different tool = select shape then use tool
  1397â†’        if (context.isOverShape) {
  1398â†’            return {
  1399â†’                type: 'select_and_tool',
  1400â†’                target: context.shapeAtPoint,
  1401â†’                tool: context.currentTool,
  1402â†’                description: `Select shape and apply ${context.currentTool} tool`
  1403â†’            };
  1404â†’        }
  1405â†’        
  1406â†’        // DEFAULT: Empty canvas = use current tool
