    // UNIVERSAL SMART CONTEXTUAL INPUT SYSTEM
    analyzeFullContext(event) {
        const pointerPos = {
            x: event.clientX - this.editor.canvas.getBoundingClientRect().left,
            y: event.clientY - this.editor.canvas.getBoundingClientRect().top
        };

        const currentTool = this.editor.getCurrentTool();
        const selectedShapes = this.editor.getSelectedShapes();
        const shapeAtPoint = this.editor.getShapeAtPoint(pointerPos);
        const resizeHandle = this.editor.getResizeHandleAtPoint(pointerPos);

        return {
            pointer: pointerPos,
            currentTool: currentTool ? currentTool.id : 'select',
            selectedShapes: selectedShapes,
            selectedCount: selectedShapes.length,
            shapeAtPoint: shapeAtPoint,
            resizeHandle: resizeHandle,
            isOverShape: !!shapeAtPoint,
            isOverHandle: !!resizeHandle,
            isEmptyCanvas: !shapeAtPoint && !resizeHandle
        };
    }

    determineContextualAction(context) {
        // Priority-based contextual decision system

        // HIGHEST PRIORITY: Resize handle detected
        if (context.isOverHandle && context.selectedCount > 0) {
            return {
                type: 'resize',
                target: context.selectedShapes,
                handle: context.resizeHandle,
                description: 'Resize selected shapes'
            };
        }

        // HIGH PRIORITY: Shape selected and pointer over selected shape = move
        if (context.selectedCount > 0 && context.shapeAtPoint && 
            context.selectedShapes.some(s => s.id === context.shapeAtPoint.id)) {
            return {
                type: 'move',
                target: context.selectedShapes,
                description: 'Move selected shapes'
            };
        }

        // MEDIUM PRIORITY: Shape creation tools
        if (['rectangle', 'ellipse', 'draw'].includes(context.currentTool)) {
            return {
                type: 'create',
                tool: context.currentTool,
                description: `Create ${context.currentTool} shape`
            };
        }

        // MEDIUM PRIORITY: Eraser tool over shape
        if (context.currentTool === 'eraser' && context.isOverShape) {
            return {
                type: 'erase',
                target: context.shapeAtPoint,
                description: 'Erase shape'
            };
        }

        // LOW PRIORITY: Shape at point but different tool = select shape then use tool
        if (context.isOverShape) {
            return {
                type: 'select_and_tool',
                target: context.shapeAtPoint,
                tool: context.currentTool,
                description: `Select shape and apply ${context.currentTool} tool`
            };
        }

        // DEFAULT: Empty canvas = use current tool
        return {
            type: 'tool_default',
            tool: context.currentTool,
            description: `Use ${context.currentTool} tool on empty canvas`
        };
    }

    executeContextualAction(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Executing action:', action.type);

        // SIMPLIFIED APPROACH: Just ensure correct tool is active
        // Let the normal pointer event flow handle the actual interaction
        switch (action.type) {
            case 'resize':
            case 'move':
                // For resize/move, ensure select tool is active
                this.editor.setCurrentTool('select');
                this.updateToolbarUI('select');
                break;

            case 'create':
                // For shape creation, ensure correct tool is active
                this.editor.setCurrentTool(action.tool);
                this.updateToolbarUI(action.tool);
                break;

            case 'erase':
                // For erasing, ensure eraser tool is active
                this.editor.setCurrentTool('eraser');
                this.updateToolbarUI('eraser');
                break;

            case 'select_and_tool':
                // Select the shape first
                this.editor.setSelectedShapes([action.target]);
                // Set the tool
                this.editor.setCurrentTool(action.tool);
                this.updateToolbarUI(action.tool);
                break;

            case 'tool_default':
                // Ensure correct tool is active
                this.editor.setCurrentTool(action.tool);
                this.updateToolbarUI(action.tool);
                break;
        }

        // The normal pointer event flow will handle the actual interaction
        console.log('ğŸ–ï¸ CONTEXTUAL: Tool set to', action.tool || 'select', '- letting normal event flow handle interaction');
    }

