     1â†’/**
     2â†’ * Complete Canvas System Integration
     3â†’ * Main editor class that orchestrates all canvas functionality
     4â†’ */
     5â†’
     6â†’// =====================================================
     7â†’// MAIN CANVAS EDITOR
     8â†’// =====================================================
     9â†’
    10â†’class CanvasEditor extends EventTarget {
    11â†’    constructor(container, options = {}) {
    12â†’        super();
    13â†’        
    14â†’        // Core setup
    15â†’        this.container = container;
    16â†’        this.options = { 
    17â†’            persistenceKey: 'tldraw-canvas',
    18â†’            autoSave: true,
    19â†’            ...options 
    20â†’        };
    21â†’        
    22â†’        // Initialize systems
    23â†’        this.initializeCanvas();
    24â†’        this.initializeSystems();
    25â†’        this.setupEventListeners();
    26â†’        this.setupPersistence();
    27â†’        
    28â†’        // Start the editor
    29â†’        this.start();
    30â†’    }
    31â†’
    32â†’    // =====================================================
    33â†’    // INITIALIZATION
    34â†’    // =====================================================
    35â†’
    36â†’    initializeCanvas() {
    37â†’        // Create canvas element
    38â†’        this.canvas = document.createElement('canvas');
    39â†’        this.canvas.className = 'tldraw-canvas';
    40â†’        this.canvas.style.width = '100%';
    41â†’        this.canvas.style.height = '100%';
    42â†’        this.canvas.style.display = 'block';
    43â†’        this.canvas.style.touchAction = 'none';
    44â†’        
    45â†’        // Set size
    46â†’        const rect = this.container.getBoundingClientRect();
    47â†’        this.canvas.width = rect.width;
    48â†’        this.canvas.height = rect.height;
    49â†’        
    50â†’        // Add to container
    51â†’        this.container.appendChild(this.canvas);
    52â†’    }
    53â†’
    54â†’    initializeSystems() {
    55â†’        // Core systems
    56â†’        this.store = new ReactiveStore();
    57â†’        this.camera = new CameraSystem(this.store, this.canvas);
    58â†’        this.drawingEngine = new DrawingEngine(this.canvas, this.store, this.camera);
    59â†’        this.toolManager = new SimpleToolManager(this);
    60â†’        
    61â†’        // UI state
    62â†’        this.currentColor = '#000000';
    63â†’        this.currentFill = 'none';
    64â†’        this.currentStroke = '#000000';
    65â†’        this.currentStrokeWidth = 2;
    66â†’        this.currentSize = 'medium';
    67â†’        this.currentOpacity = 1;
    68â†’        
    69â†’        // Interaction state
    70â†’        this.brush = null;
    71â†’        this.contextMenu = null;
    72â†’        this.isEditingText = false;
    73â†’        
    74â†’        // Performance tracking
    75â†’        this.renderStats = {
    76â†’            frameCount: 0,
    77â†’            lastFrameTime: 0,
    78â†’            averageFPS: 60
    79â†’        };
    80â†’    }
    81â†’
    82â†’    setupEventListeners() {
    83â†’        // Only setup canvas event handlers if UniversalInputSystem is not active
    84â†’        // This prevents event handler conflicts
    85â†’        if (!window.UniversalInputSystem || !window.tlEditor?.universalInput) {
    86â†’            console.log('CanvasSystem: Setting up event handlers (UniversalInputSystem not active)');
    87â†’            
    88â†’            // Canvas events - comprehensive interaction support
    89â†’            this.canvas.addEventListener('pointerdown', this.handlePointerDown.bind(this));
    90â†’            this.canvas.addEventListener('pointermove', this.handlePointerMove.bind(this));
    91â†’            this.canvas.addEventListener('pointerup', this.handlePointerUp.bind(this));
    92â†’            this.canvas.addEventListener('pointercancel', this.handlePointerUp.bind(this));
    93â†’            this.canvas.addEventListener('dblclick', this.handleDoubleClick.bind(this));
    94â†’            this.canvas.addEventListener('contextmenu', this.handleContextMenu.bind(this));
    95â†’            this.canvas.addEventListener('wheel', this.handleWheel.bind(this), { passive: false });
    96â†’            
    97â†’            // Touch events for multi-touch gestures
    98â†’            this.canvas.addEventListener('touchstart', this.handleTouchStart.bind(this), { passive: false });
    99â†’            this.canvas.addEventListener('touchmove', this.handleTouchMove.bind(this), { passive: false });
   100â†’            this.canvas.addEventListener('touchend', this.handleTouchEnd.bind(this), { passive: false });
   101â†’            this.canvas.addEventListener('touchcancel', this.handleTouchEnd.bind(this), { passive: false });
   102â†’        } else {
   103â†’            console.log('CanvasSystem: Skipping canvas event handlers (UniversalInputSystem is active)');
   104â†’        }
   105â†’        
   106â†’        // Keyboard events
   107â†’        document.addEventListener('keydown', this.handleKeyDown.bind(this));
   108â†’        document.addEventListener('keyup', this.handleKeyUp.bind(this));
   109â†’        
   110â†’        // Window events
   111â†’        window.addEventListener('resize', this.handleResize.bind(this));
   112â†’        window.addEventListener('beforeunload', this.handleBeforeUnload.bind(this));
   113â†’        
   114â†’        // Store events
   115â†’        this.store.addEventListener('change', this.handleStoreChange.bind(this));
   116â†’        
   117â†’        // Drawing engine events
   118â†’        this.drawingEngine.canvas.addEventListener('render', this.handleRender.bind(this));
   119â†’        
   120â†’        // Interaction state
   121â†’        this.pointers = new Map(); // Track multiple pointers for multi-touch
   122â†’        this.lastPinchDistance = 0;
   123â†’        this.isPanning = false;
   124â†’        this.isResizing = false;
   125â†’        this.isDragging = false;
   126â†’    }
   127â†’
   128â†’    setupPersistence() {
   129â†’        if (this.options.persistenceKey) {
   130â†’            this.persistence = new PersistenceManager(this.store, this.options.persistenceKey);
   131â†’            
   132â†’            // Try to load saved state
   133â†’            this.persistence.load();
   134â†’            
   135â†’            // Listen for save events
   136â†’            this.store.addEventListener('saved', (event) => {
   137â†’                this.emit('saved', event.detail);
   138â†’                this.updateLastSaved();
   139â†’            });
   140â†’        }
   141â†’    }
   142â†’
   143â†’    // =====================================================
   144â†’    // LIFECYCLE
   145â†’    // =====================================================
   146â†’
   147â†’    start() {
   148â†’        // Build spatial index
   149â†’        this.drawingEngine.buildSpatialIndex();
   150â†’        
   151â†’        // Start rendering
   152â†’        this.drawingEngine.scheduleRender();
   153â†’        
   154â†’        // Initialize tools
   155â†’        this.setTool(TLToolType.SELECT);
   156â†’        
   157â†’        // Emit ready event
   158â†’        this.emit('ready');
   159â†’    }
   160â†’
   161â†’    destroy() {
   162â†’        // Clean up event listeners
   163â†’        this.canvas.removeEventListener('pointerdown', this.handlePointerDown);
   164â†’        this.canvas.removeEventListener('pointermove', this.handlePointerMove);
   165â†’        this.canvas.removeEventListener('pointerup', this.handlePointerUp);
   166â†’        this.canvas.removeEventListener('dblclick', this.handleDoubleClick);
   167â†’        this.canvas.removeEventListener('contextmenu', this.handleContextMenu);
   168â†’        this.canvas.removeEventListener('wheel', this.handleWheel);
   169â†’        
   170â†’        document.removeEventListener('keydown', this.handleKeyDown);
   171â†’        document.removeEventListener('keyup', this.handleKeyUp);
   172â†’        window.removeEventListener('resize', this.handleResize);
   173â†’        window.removeEventListener('beforeunload', this.handleBeforeUnload);
   174â†’        
   175â†’        // Remove canvas
   176â†’        if (this.canvas.parentNode) {
   177â†’            this.canvas.parentNode.removeChild(this.canvas);
   178â†’        }
   179â†’        
   180â†’        // Clean up systems
   181â†’        this.toolManager = null;
   182â†’        this.drawingEngine = null;
   183â†’        this.camera = null;
   184â†’        this.store = null;
   185â†’    }
   186â†’
   187â†’    // =====================================================
   188â†’    // EVENT HANDLERS
   189â†’    // =====================================================
   190â†’
   191â†’    handlePointerDown(event) {
   192â†’        event.preventDefault();
   193â†’        this.canvas.setPointerCapture(event.pointerId);
   194â†’        
   195â†’        const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
   196â†’        this.toolManager.handleEvent(eventInfo);
   197â†’    }
   198â†’
   199â†’    handlePointerMove(event) {
   200â†’        const eventInfo = this.createEventInfo(TLEventType.POINTER_MOVE, event);
   201â†’        this.toolManager.handleEvent(eventInfo);
   202â†’        
   203â†’        // Update hover state and cursor
   204â†’        this.updateHover(eventInfo.currentPagePoint);
   205â†’        this.updateCursor(eventInfo.currentPagePoint);
   206â†’    }
   207â†’
   208â†’    updateCursor(point) {
   209â†’        let cursor = 'default';
   210â†’        
   211â†’        if (this.getActiveToolId() === 'select') {
   212â†’            // Check for resize handles first
   213â†’            const handle = this.drawingEngine.getHandleAtPoint(point);
   214â†’            if (handle) {
   215â†’                cursor = handle.cursor;
   216â†’            } else {
   217â†’                // Check if hovering over a shape
   218â†’                const hitShape = this.drawingEngine.getShapeAtPoint(point);
   219â†’                if (hitShape) {
   220â†’                    cursor = 'move';
   221â†’                }
   222â†’            }
   223â†’        } else if (this.getActiveToolId() === 'hand') {
   224â†’            cursor = 'grab';
   225â†’        } else if (['rectangle', 'ellipse', 'arrow', 'line'].includes(this.getActiveToolId())) {
   226â†’            cursor = 'crosshair';
   227â†’        } else if (this.getActiveToolId() === 'text') {
   228â†’            cursor = 'text';
   229â†’        } else if (this.getActiveToolId() === 'draw') {
   230â†’            cursor = 'crosshair';
   231â†’        }
   232â†’        
   233â†’        this.canvas.style.cursor = cursor;
   234â†’    }
   235â†’
   236â†’    handlePointerUp(event) {
   237â†’        event.preventDefault();
   238â†’        this.canvas.releasePointerCapture(event.pointerId);
   239â†’        
   240â†’        const eventInfo = this.createEventInfo(TLEventType.POINTER_UP, event);
   241â†’        this.toolManager.handleEvent(eventInfo);
   242â†’    }
   243â†’
   244â†’    handleDoubleClick(event) {
   245â†’        event.preventDefault();
   246â†’        
   247â†’        const eventInfo = this.createEventInfo(TLEventType.DOUBLE_CLICK, event);
   248â†’        this.toolManager.handleEvent(eventInfo);
   249â†’    }
   250â†’
   251â†’    handleContextMenu(event) {
   252â†’        console.log('CanvasEditor: handleContextMenu called', event);
   253â†’        event.preventDefault();
   254â†’        
   255â†’        // Delegate to SimpleToolManager's context menu
   256â†’        if (this.toolManager && this.toolManager.showContextMenu) {
   257â†’            console.log('CanvasEditor: Delegating to SimpleToolManager');
   258â†’            this.toolManager.showContextMenu(event);
   259â†’        } else {
   260â†’            console.log('CanvasEditor: No SimpleToolManager found, using fallback');
   261â†’            // Fallback to emit event
   262â†’            const rect = this.canvas.getBoundingClientRect();
   263â†’            const screenPoint = {
   264â†’                x: event.clientX - rect.left,
   265â†’                y: event.clientY - rect.top
   266â†’            };
   267â†’            
   268â†’            const pagePoint = this.camera.screenToPage(screenPoint);
   269â†’            const hitShape = this.drawingEngine.getShapeAtPoint(screenPoint);
   270â†’            
   271â†’            this.showContextMenu(screenPoint, hitShape);
   272â†’        }
   273â†’    }
   274â†’
   275â†’    handleWheel(event) {
   276â†’        event.preventDefault();
   277â†’        
   278â†’        const rect = this.canvas.getBoundingClientRect();
   279â†’        const screenPoint = {
   280â†’            x: event.clientX - rect.left,
   281â†’            y: event.clientY - rect.top
   282â†’        };
   283â†’        
   284â†’        const camera = this.store.getCurrentCamera();
   285â†’        if (!camera) return;
   286â†’        
   287â†’        // Handle zoom with wheel (Ctrl+scroll or trackpad pinch)
   288â†’        if (event.ctrlKey || event.metaKey || Math.abs(event.deltaY) > Math.abs(event.deltaX)) {
   289â†’            const zoomDelta = event.deltaY > 0 ? -0.1 : 0.1;
   290â†’            const pagePoint = this.camera.screenToPage(screenPoint);
   291â†’            camera.zoom(zoomDelta, pagePoint);
   292â†’            this.store.put(camera);
   293â†’        } else {
   294â†’            // Pan with wheel (horizontal scroll or Shift+scroll)
   295â†’            const panDelta = {
   296â†’                x: -event.deltaX * 0.5,
   297â†’                y: -event.deltaY * 0.5
   298â†’            };
   299â†’            camera.pan(panDelta);
   300â†’            this.store.put(camera);
   301â†’        }
   302â†’    }
   303â†’
   304â†’    // Touch event handlers for multi-touch gestures
   305â†’    handleTouchStart(event) {
   306â†’        event.preventDefault();
   307â†’        
   308â†’        // Check for multi-finger touch gestures
   309â†’        if (event.touches.length === 3) {
   310â†’            console.log('Three-finger touch detected - switching to draw tool');
   311â†’            this.setTool('draw');
   312â†’            return;
   313â†’        }
   314â†’        
   315â†’        // CRITICAL FIX: Check for four-finger touch to activate eraser tool
   316â†’        if (event.touches.length === 4) {
   317â†’            console.log('Four-finger touch detected - switching to eraser tool');
   318â†’            this.setTool('eraser');
   319â†’            return;
   320â†’        }
   321â†’        
   322â†’        // Update pointer tracking
   323â†’        for (const touch of event.changedTouches) {
   324â†’            this.pointers.set(touch.identifier, {
   325â†’                x: touch.clientX,
   326â†’                y: touch.clientY,
   327â†’                pageX: touch.pageX,
   328â†’                pageY: touch.pageY
   329â†’            });
   330â†’        }
   331â†’
   332â†’        // Handle multi-touch gestures
   333â†’        if (event.touches.length === 2) {
   334â†’            const touch1 = event.touches[0];
   335â†’            const touch2 = event.touches[1];
   336â†’            this.lastPinchDistance = this.getTouchDistance(touch1, touch2);
   337â†’        }
   338â†’    }
   339â†’
   340â†’    handleTouchMove(event) {
   341â†’        event.preventDefault();
   342â†’        
   343â†’        if (event.touches.length === 1) {
   344â†’            // Single touch - pan the canvas
   345â†’            const touch = event.touches[0];
   346â†’            const lastPointer = this.pointers.get(touch.identifier);
   347â†’            
   348â†’            if (lastPointer) {
   349â†’                const deltaX = touch.clientX - lastPointer.x;
   350â†’                const deltaY = touch.clientY - lastPointer.y;
   351â†’                
   352â†’                const camera = this.store.getCurrentCamera();
   353â†’                if (camera) {
   354â†’                    camera.pan({ x: deltaX, y: deltaY });
   355â†’                    this.store.put(camera);
   356â†’                }
   357â†’                
   358â†’                // Update pointer position
   359â†’                this.pointers.set(touch.identifier, {
   360â†’                    x: touch.clientX,
   361â†’                    y: touch.clientY,
   362â†’                    pageX: touch.pageX,
   363â†’                    pageY: touch.pageY
   364â†’                });
   365â†’            }
   366â†’        } else if (event.touches.length === 2) {
   367â†’            // Two fingers - pinch to zoom
   368â†’            const touch1 = event.touches[0];
   369â†’            const touch2 = event.touches[1];
   370â†’            const currentDistance = this.getTouchDistance(touch1, touch2);
   371â†’            
   372â†’            if (this.lastPinchDistance > 0) {
   373â†’                const zoomDelta = (currentDistance - this.lastPinchDistance) * 0.01;
   374â†’                const centerX = (touch1.clientX + touch2.clientX) / 2;
   375â†’                const centerY = (touch1.clientY + touch2.clientY) / 2;
   376â†’                
   377â†’                const rect = this.canvas.getBoundingClientRect();
   378â†’                const screenPoint = {
   379â†’                    x: centerX - rect.left,
   380â†’                    y: centerY - rect.top
   381â†’                };
   382â†’                
   383â†’                const camera = this.store.getCurrentCamera();
   384â†’                if (camera) {
   385â†’                    const pagePoint = this.camera.screenToPage(screenPoint);
   386â†’                    camera.zoom(zoomDelta, pagePoint);
   387â†’                    this.store.put(camera);
   388â†’                }
   389â†’            }
   390â†’            
   391â†’            this.lastPinchDistance = currentDistance;
   392â†’        }
   393â†’    }
   394â†’
   395â†’    handleTouchEnd(event) {
   396â†’        event.preventDefault();
   397â†’        
   398â†’        // Remove ended touches from tracking
   399â†’        for (const touch of event.changedTouches) {
   400â†’            this.pointers.delete(touch.identifier);
   401â†’        }
   402â†’        
   403â†’        // Reset pinch tracking if no touches remain
   404â†’        if (event.touches.length < 2) {
   405â†’            this.lastPinchDistance = 0;
   406â†’        }
   407â†’    }
   408â†’
   409â†’    getTouchDistance(touch1, touch2) {
   410â†’        const dx = touch2.clientX - touch1.clientX;
   411â†’        const dy = touch2.clientY - touch1.clientY;
   412â†’        return Math.sqrt(dx * dx + dy * dy);
   413â†’    }
   414â†’
   415â†’    handleKeyDown(event) {
   416â†’        // Check for tool shortcuts first
   417â†’        if (this.toolManager.handleKeyboardShortcut(event.key, event.shiftKey)) {
   418â†’            event.preventDefault();
   419â†’            return;
   420â†’        }
   421â†’        
   422â†’        const eventInfo = this.createEventInfo(TLEventType.KEY_DOWN, event);
   423â†’        
   424â†’        // Handle global shortcuts
   425â†’        if (event.metaKey || event.ctrlKey) {
   426â†’            switch (event.key) {
   427â†’                case 'z':
   428â†’                    if (event.shiftKey) {
   429â†’                        this.redo();
   430â†’                    } else {
   431â†’                        this.undo();
   432â†’                    }
   433â†’                    event.preventDefault();
   434â†’                    break;
   435â†’                case 'y':
   436â†’                    this.redo();
   437â†’                    event.preventDefault();
   438â†’                    break;
   439â†’                case 's':
   440â†’                    this.save();
   441â†’                    event.preventDefault();
   442â†’                    break;
   443â†’                case 'a':
   444â†’                    this.selectAll();
   445â†’                    event.preventDefault();
   446â†’                    break;
   447â†’                case 'c':
   448â†’                    this.copy();
   449â†’                    event.preventDefault();
   450â†’                    break;
   451â†’                case 'v':
   452â†’                    this.paste();
   453â†’                    event.preventDefault();
   454â†’                    break;
   455â†’                case 'x':
   456â†’                    this.cut();
   457â†’                    event.preventDefault();
   458â†’                    break;
   459â†’                case 'd':
   460â†’                    this.duplicate();
   461â†’                    event.preventDefault();
   462â†’                    break;
   463â†’            }
   464â†’        }
   465â†’        
   466â†’        // Pass to current tool
   467â†’        this.toolManager.handleEvent(eventInfo);
   468â†’    }
   469â†’
   470â†’    handleKeyUp(event) {
   471â†’        const eventInfo = this.createEventInfo(TLEventType.KEY_UP, event);
   472â†’        this.toolManager.handleEvent(eventInfo);
   473â†’    }
   474â†’
   475â†’    handleResize() {
   476â†’        const rect = this.container.getBoundingClientRect();
   477â†’        this.canvas.width = rect.width;
   478â†’        this.canvas.height = rect.height;
   479â†’        
   480â†’        this.drawingEngine.handleResize();
   481â†’        this.camera.updateViewport();
   482â†’    }
   483â†’
   484â†’    handleBeforeUnload(event) {
   485â†’        if (this.persistence && this.options.autoSave) {
   486â†’            this.persistence.save();
   487â†’        }
   488â†’    }
   489â†’
   490â†’    handleStoreChange(event) {
   491â†’        const changeInfo = event.detail;
   492â†’        
   493â†’        // Update UI based on store changes
   494â†’        if (changeInfo.record?.type === TLRecordType.INSTANCE) {
   495â†’            this.updateUI();
   496â†’        }
   497â†’        
   498â†’        // Emit change event
   499â†’        this.emit('change', changeInfo);
   500â†’    }
   501â†’
   502â†’    handleRender(event) {
