   517â†’    createEventInfo(type, domEvent) {
   518â†’        const rect = this.canvas.getBoundingClientRect();
   519â†’        const camera = this.store.getCurrentCamera();
   520â†’        
   521â†’        let currentPagePoint = { x: 0, y: 0 };
   522â†’        
   523â†’        if (domEvent && domEvent.clientX !== undefined) {
   524â†’            const screenPoint = {
   525â†’                x: domEvent.clientX - rect.left,
   526â†’                y: domEvent.clientY - rect.top
   527â†’            };
   528â†’            
   529â†’            if (camera) {
   530â†’                currentPagePoint = camera.screenToPage(screenPoint);
   531â†’            }
   532â†’        }
   533â†’        
   534â†’        return new TLEventInfo(type, {
   535â†’            currentPagePoint,
   536â†’            originPagePoint: currentPagePoint,
   537â†’            shiftKey: domEvent?.shiftKey || false,
   538â†’            ctrlKey: domEvent?.ctrlKey || false,
   539â†’            altKey: domEvent?.altKey || false,
   540â†’            metaKey: domEvent?.metaKey || false,
   541â†’            pointerId: domEvent?.pointerId || 0,
   542â†’            button: domEvent?.button || 0,
   543â†’            pressure: domEvent?.pressure || 0.5,
   544â†’            isPen: domEvent?.pointerType === 'pen',
   545â†’            target: domEvent?.target,
   546â†’            key: domEvent?.key || '',
   547â†’            code: domEvent?.code || '',
   548â†’            delta: domEvent?.deltaX !== undefined ? 
   549â†’                { x: domEvent.deltaX, y: domEvent.deltaY } : { x: 0, y: 0 }
   550â†’        });
   551â†’    }
