/**
 * Simplified Working Tool System
 * Direct implementation that actually works
 */

class SimpleToolManager {
    constructor(editor) {
        this.editor = editor;
        this.currentTool = 'select';
        this.isDrawing = false;
        this.currentShape = null;
        this.startPoint = null;

        console.log('SimpleToolManager initialized');
        this.setupEventListeners();
    }

    setupEventListeners() {
        // Only setup event handlers if UniversalInputSystem is not active
        // This prevents event handler conflicts
        if (!window.UniversalInputSystem || !window.tlEditor?.universalInput) {
            console.log('SimpleToolManager: Setting up event listeners (UniversalInputSystem not active)');

            this.editor.canvas.addEventListener('pointerdown', (e) => {
                this.handlePointerDown(e);
            });

            this.editor.canvas.addEventListener('pointermove', (e) => {
                this.handlePointerMove(e);
            });

            this.editor.canvas.addEventListener('pointerup', (e) => {
                this.handlePointerUp(e);
            });
        } else {
            console.log('SimpleToolManager: Skipping canvas event handlers (UniversalInputSystem is active)');
        }

        // Hide context menu on click elsewhere
        document.addEventListener('click', (e) => {
            this.hideContextMenu();
        });
    }

    setTool(toolId) {
        console.log('SimpleToolManager: Setting tool to', toolId);
        this.currentTool = toolId;

        // Update cursor
        this.updateCursor();

        // Update UI
        document.querySelectorAll('.toolbar-btn').forEach(btn => {
            btn.classList.remove('active');
            if (btn.dataset.tool === toolId) {
                btn.classList.add('active');
            }
        });

        this.editor.emit('tool-changed', { tool: toolId });
    }

    updateCursor() {
        const cursors = {
            select: 'default',
            draw: 'crosshair',
            hand: 'grab',
            text: 'text',
            rectangle: 'crosshair',
            ellipse: 'crosshair',
            arrow: 'crosshair',
            line: 'crosshair',
            eraser: 'crosshair'
        };

        this.editor.canvas.style.cursor = cursors[this.currentTool] || 'default';
    }

    getPagePoint(event) {
        const rect = this.editor.canvas.getBoundingClientRect();
        const camera = this.editor.store.getCurrentCamera();

        const screenPoint = {
            x: event.clientX - rect.left,
            y: event.clientY - rect.top
        };

        if (camera) {
            return camera.screenToPage(screenPoint);
        }

        return screenPoint;
    }

    handlePointerDown(event) {
        event.preventDefault();
        const point = this.getPagePoint(event);

        console.log('Pointer down with tool:', this.currentTool, 'at point:', point);

        switch (this.currentTool) {
            case 'select':
                this.handleSelectDown(point, event);
                break;
            case 'draw':
                this.handleDrawDown(point);
                break;
            case 'rectangle':
                this.handleShapeDown(point, 'rectangle');
                break;
            case 'ellipse':
                this.handleShapeDown(point, 'ellipse');
                break;
            case 'text':
                this.handleTextDown(point);
                break;
            case 'hand':
                this.handlePanDown(point);
                break;
            case 'eraser':
                this.handleEraserDown(point);
                break;
        }
    }

    handlePointerMove(event) {
        const point = this.getPagePoint(event);

        if (this.isDrawing && this.currentShape) {
            switch (this.currentTool) {
                case 'draw':
                    this.handleDrawMove(point);
                    break;
                case 'rectangle':
                case 'ellipse':
                    this.handleShapeMove(point);
                    break;
            }
        }
    }

    handlePointerUp(event) {
        console.log('Pointer up');
        this.isDrawing = false;
        this.currentShape = null;
        this.startPoint = null;
    }

    handleSelectDown(point, event) {
        // Simple selection - just log for now
        const hitShape = this.editor.drawingEngine.getShapeAtPoint(point);
        if (hitShape) {
            console.log('Selected shape:', hitShape.id);
            this.editor.select(hitShape.id);
        } else {
            this.editor.deselect();
        }
    }

    handleDrawDown(point) {
        console.log('Starting draw at:', point);
        this.isDrawing = true;
        this.startPoint = point;

        // Create a draw shape
        const shapes = this.editor.createShapes([{
            type: 'draw',
            x: point.x,
            y: point.y,
            props: {
                segments: [{
                    points: [{ x: 0, y: 0, z: 0.5 }]
                }],
                color: '#000000',
                size: 'medium',
                opacity: 1,
                isComplete: false
            }
        }]);

        this.currentShape = shapes[0];
        console.log('Created draw shape:', this.currentShape.id);
    }

    handleDrawMove(point) {
        if (!this.currentShape) return;

        const shape = this.editor.getShape(this.currentShape.id);
        if (shape) {
            const newPoint = {
                x: point.x - shape.x,
                y: point.y - shape.y,
                z: 0.5
            };

            shape.props.segments[0].points.push(newPoint);
            this.editor.updateShapes([{ id: shape.id, props: shape.props }]);
        }
    }

    handleShapeDown(point, shapeType) {
        console.log('Starting', shapeType, 'at:', point);
        this.isDrawing = true;
        this.startPoint = point;

        // Create shape
        const shapes = this.editor.createShapes([{
            type: shapeType,
            x: point.x,
            y: point.y,
            props: {
                w: 10,
                h: 10,
                color: '#000000',
                fill: 'none'
            }
        }]);

        this.currentShape = shapes[0];
        console.log('Created', shapeType, 'shape:', this.currentShape.id);
    }

    handleShapeMove(point) {
        if (!this.currentShape || !this.startPoint) return;

        const width = Math.abs(point.x - this.startPoint.x);
        const height = Math.abs(point.y - this.startPoint.y);
        const x = Math.min(point.x, this.startPoint.x);
        const y = Math.min(point.y, this.startPoint.y);

        this.editor.updateShapes([{
            id: this.currentShape.id,
            x: x,
            y: y,
            props: {
                w: Math.max(width, 10),
                h: Math.max(height, 10)
            }
        }]);
    }

    handleTextDown(point) {
        console.log('Creating text at:', point);

        const shapes = this.editor.createShapes([{
            type: 'text',
            x: point.x,
            y: point.y,
            props: {
                text: 'Text',
                fontSize: 16,
                color: '#000000',
                w: 100,
                h: 24
            }
        }]);

        console.log('Created text shape:', shapes[0].id);
    }

    handlePanDown(point) {
        // Pan logic would go here
        console.log('Pan down at:', point);
    }

    handleEraserDown(point) {
        const hitShape = this.editor.drawingEngine.getShapeAtPoint(point);
        if (hitShape) {
            console.log('Erasing shape:', hitShape.id);
            this.editor.deleteShapes([hitShape.id]);
        }
    }

    showContextMenu(event) {
        console.log('SimpleToolManager: showContextMenu called', event);
        this.hideContextMenu(); // Hide any existing menu

        const contextMenu = document.createElement('div');
        contextMenu.className = 'canvas-context-menu';
        contextMenu.id = 'canvas-context-menu';

        const tools = [
            { id: 'select', name: 'Select Tool', icon: 'â†–ï¸', shortcut: 'V' },
            { id: 'draw', name: 'Draw Tool', icon: 'âœï¸', shortcut: 'D' },
            { id: 'hand', name: 'Pan Tool', icon: 'âœ‹', shortcut: 'H' },
            { id: 'rectangle', name: 'Rectangle', icon: 'â¬œ', shortcut: 'R' },
            { id: 'ellipse', name: 'Ellipse', icon: 'â­•', shortcut: 'O' },
            { id: 'arrow', name: 'Arrow', icon: 'â¡ï¸', shortcut: 'A' },
            { id: 'line', name: 'Line', icon: 'ğŸ“', shortcut: 'L' },
            { id: 'text', name: 'Text', icon: 'ğŸ“', shortcut: 'T' },
            { id: 'eraser', name: 'Eraser', icon: 'ğŸ—‘ï¸', shortcut: 'X' }
        ];

        contextMenu.innerHTML = `
            <div class="context-menu-item" onclick="this.parentElement.style.display='none'">
                <strong>Tools</strong>
            </div>
            ${tools.map(tool => `
                <div class="context-menu-item ${this.currentTool === tool.id ? 'active' : ''}" 
                     onclick="window.canvasEditor.toolManager.setTool('${tool.id}'); this.parentElement.style.display='none'">
                    <span>${tool.icon}</span>
                    <span>${tool.name}</span>
                    <span style="margin-left: auto; color: var(--text-muted);">${tool.shortcut}</span>
                </div>
            `).join('')}
            <div style="border-top: 1px solid var(--border-color); margin: 4px 0;"></div>
            <div class="context-menu-item" onclick="this.showEmbedMenu(event)">
                <span>ğŸ“‹</span>
                <span>Embed Content</span>
                <span style="margin-left: auto;">â–¶</span>
            </div>
            <div class="context-menu-item" onclick="window.canvasEditor.clearCanvas(); this.parentElement.style.display='none'">
                <span>ğŸ—‘ï¸</span>
                <span>Clear Canvas</span>
            </div>
            <div class="context-menu-item" onclick="window.canvasEditor.exportCanvas(); this.parentElement.style.display='none'">
                <span>ğŸ’¾</span>
                <span>Export Canvas</span>
            </div>
        `;

        // Position the menu
        const x = Math.min(event.clientX, window.innerWidth - 280);
        const y = Math.min(event.clientY, window.innerHeight - 400);

        contextMenu.style.left = x + 'px';
        contextMenu.style.top = y + 'px';

        document.body.appendChild(contextMenu);

        // Add embed submenu functionality
        const embedItem = contextMenu.querySelector('[onclick*="showEmbedMenu"]');
        if (embedItem) {
            embedItem.onclick = (e) => {
                e.stopPropagation();
                this.showEmbedSubmenu(e, contextMenu);
            };
        }
    }

    showEmbedSubmenu(event, parentMenu) {
        const embedMenu = document.createElement('div');
        embedMenu.className = 'canvas-context-menu';
        embedMenu.id = 'embed-submenu';
        embedMenu.style.zIndex = '4001';

        embedMenu.innerHTML = `
            <div class="context-menu-item" onclick="this.parentElement.style.display='none'; this.parentElement.parentElement.querySelector('#canvas-context-menu').style.display='none'">
                <strong>Embed Content</strong>
            </div>
            <div class="context-menu-item" onclick="this.embedYouTube(); this.closeAllMenus()">
                <span>ğŸ“º</span>
                <span>YouTube Video</span>
            </div>
            <div class="context-menu-item" onclick="this.embedImage(); this.closeAllMenus()">
                <span>ğŸ–¼ï¸</span>
                <span>Image</span>
            </div>
            <div class="context-menu-item" onclick="this.embedText(); this.closeAllMenus()">
                <span>ğŸ“„</span>
                <span>Text Document</span>
            </div>
            <div class="context-menu-item" onclick="this.embedWeb(); this.closeAllMenus()">
                <span>ğŸŒ</span>
                <span>Web Page</span>
            </div>
            <div class="context-menu-item" onclick="this.embedMusic(); this.closeAllMenus()">
                <span>ğŸµ</span>
                <span>Music/Audio</span>
            </div>
        `;

        // Position submenu to the right of parent
        const parentRect = parentMenu.getBoundingClientRect();
        embedMenu.style.left = (parentRect.right + 5) + 'px';
        embedMenu.style.top = (parentRect.top + 60) + 'px';

        document.body.appendChild(embedMenu);

        // Add embed functionality
        const items = embedMenu.querySelectorAll('.context-menu-item');
        items[1].onclick = () => { this.embedYouTube(); this.closeAllMenus(); };
        items[2].onclick = () => { this.embedImage(); this.closeAllMenus(); };
        items[3].onclick = () => { this.embedText(); this.closeAllMenus(); };
        items[4].onclick = () => { this.embedWeb(); this.closeAllMenus(); };
        items[5].onclick = () => { this.embedMusic(); this.closeAllMenus(); };
    }

    hideContextMenu() {
        const existing = document.getElementById('canvas-context-menu');
        if (existing) existing.remove();

        const embedMenu = document.getElementById('embed-submenu');
        if (embedMenu) embedMenu.remove();
    }

    closeAllMenus() {
        this.hideContextMenu();
    }

    embedYouTube() {
        const url = prompt('Enter YouTube URL:');
        if (url) {
            console.log('Embedding YouTube:', url);
            // Create YouTube embed shape
            this.editor.createShapes([{
                type: 'embed',
                x: 100,
                y: 100,
                props: {
                    url: url,
                    w: 560,
                    h: 315,
                    embedType: 'youtube'
                }
            }]);
        }
    }

    embedImage() {
        const url = prompt('Enter Image URL:');
        if (url) {
            console.log('Embedding Image:', url);
            this.editor.createShapes([{
                type: 'image',
                x: 100,
                y: 100,
                props: {
                    url: url,
                    w: 300,
                    h: 200
                }
            }]);
        }
    }

    embedText() {
        const text = prompt('Enter text content:');
        if (text) {
            console.log('Creating text:', text);
            this.editor.createShapes([{
                type: 'text',
                x: 100,
                y: 100,
                props: {
                    text: text,
                    fontSize: 16,
                    color: '#000000',
                    w: 200,
                    h: 50
                }
            }]);
        }
    }

    embedWeb() {
        const url = prompt('Enter Website URL:');
        if (url) {
            console.log('Embedding Website:', url);
            this.editor.createShapes([{
                type: 'embed',
                x: 100,
                y: 100,
                props: {
                    url: url,
                    w: 800,
                    h: 600,
                    embedType: 'webpage'
                }
            }]);
        }
    }

    embedMusic() {
        const url = prompt('Enter Music/Audio URL (Spotify, SoundCloud, etc.):');
        if (url) {
            console.log('Embedding Music:', url);
            this.editor.createShapes([{
                type: 'embed',
                x: 100,
                y: 100,
                props: {
                    url: url,
                    w: 400,
                    h: 200,
                    embedType: 'audio'
                }
            }]);
        }
    }
}

// Export for use
if (typeof window !== 'undefined') {
    window.SimpleToolManager = SimpleToolManager;
}

