    91â†’    }
    92â†’    
    93â†’    handlePointerMove(event) {
    94â†’        if (!this.activePointers.has(event.pointerId)) return;
    95â†’        
    96â†’        event.preventDefault();
    97â†’        event.stopPropagation();
    98â†’        
    99â†’        // Update pointer position
   100â†’        const pointer = this.activePointers.get(event.pointerId);
   101â†’        pointer.x = event.clientX;
