/**
 * Universal Input System for Canvas
 * Handles all input types (mouse, touch, three-finger drag) in a unified way
 */

class UniversalInputSystem {
    constructor(editor) {
        this.editor = editor;
        this.canvas = null;
        this.activePointers = new Map();
        this.isDragging = false;
        this.dragStart = null;
        this.currentTool = 'select';

        // Bind methods
        this.handlePointerDown = this.handlePointerDown.bind(this);
        this.handlePointerMove = this.handlePointerMove.bind(this);
        this.handlePointerUp = this.handlePointerUp.bind(this);

        // Validate and set canvas reference
        if (!editor) {
            throw new Error('UniversalInputSystem requires a valid editor instance');
        }

        if (!editor.canvas) {
            throw new Error('UniversalInputSystem requires editor to have a canvas instance');
        }

        this.canvas = editor.canvas;
        this.initialize();
    }

    initialize() {
        // Use pointer events for unified handling
        this.canvas.addEventListener('pointerdown', this.handlePointerDown);
        this.canvas.addEventListener('pointermove', this.handlePointerMove);
        this.canvas.addEventListener('pointerup', this.handlePointerUp);
        this.canvas.addEventListener('pointercancel', this.handlePointerUp);

        // Add wheel event for zoom functionality
        this.canvas.addEventListener('wheel', this.handleWheel.bind(this), { passive: false });

        // Add context menu handling
        this.canvas.addEventListener('contextmenu', this.handleContextMenu.bind(this));

        // Prevent default touch behaviors
        this.canvas.style.touchAction = 'none';

        console.log('ğŸ® Universal Input System initialized with zoom and context menu support');
    }

    setTool(toolId) {
        this.currentTool = toolId;
        console.log('ğŸ® Tool set to:', toolId);

        // Synchronize with StateChart system
        if (this.editor.rootState && this.editor.rootState.setCurrentTool) {
            this.editor.rootState.setCurrentTool(toolId);
        }
    }

    handlePointerDown(event) {
        event.preventDefault();
        event.stopPropagation();

        // Track pointer
        this.activePointers.set(event.pointerId, {
            x: event.clientX,
            y: event.clientY,
            startX: event.clientX,
            startY: event.clientY
        });

        console.log('ğŸ® Pointer down, active:', this.activePointers.size);

        // Convert to canvas coordinates
        const rect = this.canvas.getBoundingClientRect();
        const point = {
            x: event.clientX - rect.left,
            y: event.clientY - rect.top
        };

        // Check pointer count for different behaviors
        if (this.activePointers.size === 1) {
            // Single pointer - normal tool behavior
            this.handleSinglePointerDown(point, event);
        } else if (this.activePointers.size === 3) {
            // Three pointers - drag mode
            this.handleThreePointerDown(point, event);
        }
    }

    handlePointerMove(event) {
        if (!this.activePointers.has(event.pointerId)) return;

        event.preventDefault();
        event.stopPropagation();

        // Update pointer position
        const pointer = this.activePointers.get(event.pointerId);
        pointer.x = event.clientX;
        pointer.y = event.clientY;

        // Convert to canvas coordinates
        const rect = this.canvas.getBoundingClientRect();
        const point = {
            x: event.clientX - rect.left,
            y: event.clientY - rect.top
        };

        if (this.isDragging) {
            if (this.activePointers.size === 1) {
                this.handleSinglePointerMove(point, event);
            } else if (this.activePointers.size === 3) {
                this.handleThreePointerMove(point, event);
            }
        }
    }

    handlePointerUp(event) {
        event.preventDefault();
        event.stopPropagation();

        const hadPointer = this.activePointers.has(event.pointerId);
        this.activePointers.delete(event.pointerId);

        console.log('ğŸ® Pointer up, remaining:', this.activePointers.size);

        if (hadPointer && this.activePointers.size === 0) {
            // Last pointer released
            this.handleAllPointersUp(event);
        }
    }

    handleSinglePointerDown(point, event) {
        console.log('ğŸ® Single pointer down at:', point);
        this.isDragging = true;
        this.dragStart = { ...point };

        // Tool-specific behavior
        switch (this.currentTool) {
            case 'rectangle':
                this.startRectangle(point);
                break;
            case 'ellipse':
                this.startEllipse(point);
                break;
            case 'draw':
                this.startDrawing(point);
                break;
            case 'text':
                this.createText(point);
                break;
            case 'eraser':
                this.startErasing(point);
                break;
            case 'select':
            default:
                this.startSelection(point);
                break;
        }
    }

    handleThreePointerDown(point, event) {
        console.log('ğŸ® Three pointer down - drag mode activated');
        this.isDragging = true;
        this.dragStart = { ...point };

        // Three-finger drag always uses current tool
        // This makes it work universally
        this.handleSinglePointerDown(point, event);
    }

    handleSinglePointerMove(point, event) {
        if (!this.isDragging || !this.dragStart) return;

        // Tool-specific drag behavior
        switch (this.currentTool) {
            case 'rectangle':
                this.updateRectangle(point);
                break;
            case 'ellipse':
                this.updateEllipse(point);
                break;
            case 'draw':
                this.continueDrawing(point);
                break;
            case 'eraser':
                this.continueErasing(point);
                break;
            case 'select':
                this.updateSelection(point);
                break;
        }
    }

    handleThreePointerMove(point, event) {
        // Three-finger move uses same behavior as single
        this.handleSinglePointerMove(point, event);
    }

    handleAllPointersUp(event) {
        console.log('ğŸ® All pointers up');

        if (this.isDragging) {
            // Finalize current operation
            switch (this.currentTool) {
                case 'rectangle':
                case 'ellipse':
                    this.finalizeShape();
                    break;
                case 'draw':
                    this.finalizeDraw();
                    break;
                case 'select':
                    this.finalizeSelection();
                    break;
            }
        }

        this.isDragging = false;
        this.dragStart = null;
        this.currentShape = null;
    }

    // Tool implementations
    startRectangle(point) {
        console.log('ğŸ® Starting rectangle at:', point);

        const shapes = this.editor.createShapes([{
            type: 'geo',
            x: point.x,
            y: point.y,
            props: {
                geo: 'rectangle',
                w: 1,
                h: 1,
                color: 'black',
                fill: 'none'
            }
        }]);

        if (shapes && shapes.length > 0) {
            this.currentShape = shapes[0];
            console.log('ğŸ® Rectangle created:', this.currentShape.id);
        } else {
            console.error('ğŸ® Failed to create rectangle');
        }
    }

    updateRectangle(point) {
        if (!this.currentShape || !this.dragStart) return;

        const w = Math.abs(point.x - this.dragStart.x);
        const h = Math.abs(point.y - this.dragStart.y);
        const x = Math.min(point.x, this.dragStart.x);
        const y = Math.min(point.y, this.dragStart.y);

        this.editor.updateShapes([{
            id: this.currentShape.id,
            type: this.currentShape.type,
            x: x,
            y: y,
            props: {
                ...this.currentShape.props,
                w: w,
                h: h
            }
        }]);
    }

    startEllipse(point) {
        console.log('ğŸ® Starting ellipse at:', point);

        const shapes = this.editor.createShapes([{
            type: 'geo',
            x: point.x,
            y: point.y,
            props: {
                geo: 'ellipse',
                w: 1,
                h: 1,
                color: 'black',
                fill: 'none'
            }
        }]);

        if (shapes && shapes.length > 0) {
            this.currentShape = shapes[0];
            console.log('ğŸ® Ellipse created:', this.currentShape.id);
        } else {
            console.error('ğŸ® Failed to create ellipse');
        }
    }

    updateEllipse(point) {
        // Same as rectangle for bounding box
        this.updateRectangle(point);
    }

    startDrawing(point) {
        console.log('ğŸ® Starting draw at:', point);

        // Clear any previous shape
        this.currentShape = null;

        const shape = new TLShape(TLShapeType.DRAW, null, {
            x: 0,
            y: 0,
            props: {
                segments: [{
                    points: [{ x: point.x, y: point.y, z: 0.5 }]
                }],
                color: '#000000',
                size: 'medium',
                opacity: 1,
                isClosed: false,
                isComplete: false
            }
        });

        console.log('ğŸ® Created draw shape:', shape.id);
        this.editor.store.put(shape);

        const page = this.editor.store.getCurrentPage();
        if (page) {
            page.addShape(shape.id);
            this.editor.store.put(page);
            console.log('ğŸ® Added shape to page:', page.id);
        } else {
            console.error('ğŸ® No current page found');
        }

        this.currentShape = shape;

        // Force initial render
        if (this.editor.renderer && this.editor.renderer.scheduleRender) {
            this.editor.renderer.scheduleRender();
        }
    }

    continueDrawing(point) {
        if (!this.currentShape) {
            console.warn('ğŸ® continueDrawing called but no currentShape');
            return;
        }

        console.log('ğŸ® Adding point to draw path:', point);

        // Add point to the current segment
        if (this.currentShape.props.segments && this.currentShape.props.segments[0]) {
            this.currentShape.props.segments[0].points.push({ 
                x: point.x, 
                y: point.y, 
                z: 0.5 
            });
        }

        // Update the shape in store
        this.editor.store.put(this.currentShape);

        // Ensure rendering is triggered
        if (this.editor.renderer && this.editor.renderer.scheduleRender) {
            this.editor.renderer.scheduleRender();
        }

        const pointCount = this.currentShape.props.segments[0]?.points?.length || 0;
        console.log('ğŸ® Path now has', pointCount, 'points');
    }

    finalizeDraw() {
        if (!this.currentShape) {
            console.warn('ğŸ® finalizeDraw called but no currentShape');
            return;
        }

        const pointCount = this.currentShape.props.segments[0]?.points?.length || 0;
        console.log('ğŸ® Finalizing draw with', pointCount, 'points');

        this.currentShape.props.isComplete = true;
        this.editor.store.put(this.currentShape);

        // Final render
        if (this.editor.renderer && this.editor.renderer.scheduleRender) {
            this.editor.renderer.scheduleRender();
        }

        this.currentShape = null;
    }

    handleWheel(event) {
        event.preventDefault();
        event.stopPropagation();

        console.log('ğŸ® Wheel event - deltaY:', event.deltaY);

        // Get mouse position for zoom center
        const rect = this.canvas.getBoundingClientRect();
        const mouseX = event.clientX - rect.left;
        const mouseY = event.clientY - rect.top;

        // Calculate zoom delta (negative deltaY = zoom in)
        const zoomDelta = event.deltaY > 0 ? 0.9 : 1.1;

        // Apply zoom through editor camera
        if (this.editor.camera) {
            const oldZ = this.editor.camera.z;
            const newZ = Math.max(0.1, Math.min(8, oldZ * zoomDelta));

            console.log('ğŸ® Zoom from', oldZ.toFixed(3), 'to', newZ.toFixed(3));

            // Update camera zoom
            this.editor.camera.z = newZ;

            // Trigger render with new zoom
            if (this.editor.renderer && this.editor.renderer.scheduleRender) {
                this.editor.renderer.scheduleRender();
            }
        } else {
            console.warn('ğŸ® No camera available for zoom');
        }
    }

    handleContextMenu(event) {
        console.log('ğŸ® Context menu requested at:', event.clientX, event.clientY);

        event.preventDefault();
        event.stopPropagation();

        // Convert to canvas coordinates
        const rect = this.canvas.getBoundingClientRect();
        const point = {
            x: event.clientX - rect.left,
            y: event.clientY - rect.top
        };

        // ğŸ¨ PRIORITY 1: Use Notion Context Menu System if available
        if (this.editor.notionContextMenu) {
            console.log('ğŸ¨ Using Notion Context Menu System');
            this.editor.notionContextMenu.show(event, {
                clickPoint: point,
                canvasRect: rect
            });
            return;
        }

        // PRIORITY 2: Check if editor has InputManager with context menu support
        if (this.editor.inputManager && this.editor.inputManager.showContextMenu) {
            console.log('ğŸ® Delegating to InputManager context menu');
            this.editor.inputManager.showContextMenu(event);
        } else if (this.editor.showContextMenu) {
            console.log('ğŸ® Using editor context menu');
            this.editor.showContextMenu(event);
        } else {
            console.log('ğŸ® No context menu system available');
        }
    }

    createText(point) {
        console.log('ğŸ® Creating text at:', point);

        const shape = new TLShape(TLShapeType.TEXT, null, {
            x: point.x,
            y: point.y,
            props: {
                text: 'Text',
                w: 100,
                h: 24,
                fontSize: 16,
                color: '#000000'
            }
        });

        this.editor.store.put(shape);

        const page = this.editor.store.getCurrentPage();
        if (page) {
            page.addShape(shape.id);
            this.editor.store.put(page);
        }

        // TODO: Open text editor
        console.log('ğŸ® Text created:', shape.id);
    }

    startErasing(point) {
        console.log('ğŸ® Starting eraser at:', point);
        this.eraseAt(point);
    }

    continueErasing(point) {
        this.eraseAt(point);
    }

    eraseAt(point) {
        const shapes = this.editor.getCurrentPageShapes();

        shapes.forEach(shape => {
            // Simple point-in-bounds check
            if (point.x >= shape.x && 
                point.x <= shape.x + (shape.props.w || 0) &&
                point.y >= shape.y && 
                point.y <= shape.y + (shape.props.h || 0)) {

                console.log('ğŸ® Erasing shape:', shape.id);
                this.editor.deleteShapes([shape.id]);
            }
        });
    }

    startSelection(point) {
        console.log('ğŸ® Starting selection at:', point);
        // TODO: Implement selection
    }

    updateSelection(point) {
        // TODO: Implement selection box
    }

    finalizeSelection() {
        // TODO: Finalize selection
    }

    finalizeShape() {
        console.log('ğŸ® Finalizing shape');
        this.currentShape = null;
    }

    destroy() {
        this.canvas.removeEventListener('pointerdown', this.handlePointerDown);
        this.canvas.removeEventListener('pointermove', this.handlePointerMove);
        this.canvas.removeEventListener('pointerup', this.handlePointerUp);
        this.canvas.removeEventListener('pointercancel', this.handlePointerUp);
    }
}

// Export
if (typeof window !== 'undefined') {
    window.UniversalInputSystem = UniversalInputSystem;
}

