#!/usr/bin/env python3
"""
SNAPE Three-Finger Tools Test System
===================================
Comprehensive testing of three-finger drag functionality across all canvas tools
"""

import time
import json
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.common.action_chains import ActionChains
from selenium.webdriver.chrome.options import Options
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from selenium.common.exceptions import TimeoutException, NoSuchElementException

class SNAPEThreeFingerToolsTest:
    def __init__(self):
        self.driver = None
        self.test_results = {}
        self.screenshots = []

        # Tools to test
        self.tools_to_test = [
            'select',
            'draw', 
            'rectangle',
            'ellipse',
            'text',
            'eraser',
            'hand'
        ]

        # Test positions on canvas
        self.test_positions = [
            (300, 200),  # Top left area
            (600, 350),  # Center
            (900, 500),  # Bottom right area
        ]

    def setup_chrome(self):
        print("ğŸš€ Setting up Chrome for three-finger testing...")

        chrome_options = Options()
        chrome_options.add_argument("--disable-web-security")
        chrome_options.add_argument("--disable-features=VizDisplayCompositor")
        chrome_options.add_argument("--enable-logging")
        chrome_options.add_argument("--v=1")

        self.driver = webdriver.Chrome(options=chrome_options)
        self.driver.set_window_size(1400, 900)

        return self.driver

    def load_canvas(self):
        print("ğŸŒ Loading canvas.html...")
        self.driver.get("file:///Users/dezmondhollins/my-web-app/public/canvas.html")

        # Wait for canvas to be ready
        try:
            WebDriverWait(self.driver, 10).until(
                EC.presence_of_element_located((By.TAG_NAME, "canvas"))
            )
            print("âœ… Canvas loaded successfully")
            time.sleep(2)  # Allow systems to initialize
            return True
        except TimeoutException:
            print("âŒ Canvas failed to load")
            return False

    def inject_three_finger_simulator(self):
        """Inject JavaScript to simulate three-finger touch events"""
        print("ğŸ”§ Injecting three-finger touch simulator...")

        simulator_js = """
        window.simulateThreeFingerTouch = function(x, y, action = 'down') {
            const canvas = document.querySelector('canvas');
            if (!canvas) return false;

            const rect = canvas.getBoundingClientRect();
            const touches = [];

            // Create three touch points in a triangular pattern
            for (let i = 0; i < 3; i++) {
                const offsetX = (i - 1) * 20; // Spread touches horizontally
                const offsetY = i === 0 ? -15 : 10; // Top touch higher

                touches.push({
                    identifier: i,
                    clientX: x + offsetX,
                    clientY: y + offsetY,
                    pageX: x + offsetX,
                    pageY: y + offsetY,
                    screenX: x + offsetX,
                    screenY: y + offsetY,
                    target: canvas
                });
            }

            let eventType;
            switch(action) {
                case 'down': eventType = 'touchstart'; break;
                case 'move': eventType = 'touchmove'; break;
                case 'up': eventType = 'touchend'; break;
                default: eventType = 'touchstart';
            }

            const touchEvent = new TouchEvent(eventType, {
                touches: action === 'up' ? [] : touches,
                targetTouches: action === 'up' ? [] : touches,
                changedTouches: touches,
                bubbles: true,
                cancelable: true
            });

            console.log('ğŸ–ï¸ Simulating', eventType, 'at', x, y, 'with', touches.length, 'touches');
            canvas.dispatchEvent(touchEvent);

            return true;
        };

        window.getCurrentTool = function() {
            if (window.tlEditor && window.tlEditor.getCurrentTool) {
                return window.tlEditor.getCurrentTool();
            } else if (window.canvasEditor && window.canvasEditor.getCurrentTool) {
                return window.canvasEditor.getCurrentTool();
            } else if (window.canvasEditor && window.canvasEditor.toolManager) {
                return window.canvasEditor.toolManager.currentTool;
            }
            return 'unknown';
        };

        window.setTool = function(toolId) {
            console.log('ğŸ¯ Setting tool to:', toolId);

            if (window.tlEditor && window.tlEditor.setCurrentTool) {
                window.tlEditor.setCurrentTool(toolId);
                return true;
            } else if (window.canvasEditor && window.canvasEditor.setTool) {
                window.canvasEditor.setTool(toolId);
                return true;
            } else if (window.canvasEditor && window.canvasEditor.toolManager) {
                window.canvasEditor.toolManager.setTool(toolId);
                return true;
            }

            return false;
        };

        window.getShapeCount = function() {
            if (window.tlEditor && window.tlEditor.getShapes) {
                return window.tlEditor.getShapes().length;
            } else if (window.canvasEditor && window.canvasEditor.getShapes) {
                return window.canvasEditor.getShapes().length;
            }
            return 0;
        };

        console.log('âœ… Three-finger simulator injected');
        """

        self.driver.execute_script(simulator_js)
        time.sleep(1)

    def test_tool_with_three_finger(self, tool_name):
        """Test a specific tool with three-finger drag"""
        print(f"\nğŸ¯ Testing {tool_name} tool with three-finger drag...")

        tool_results = {
            'tool': tool_name,
            'success': False,
            'tool_switch_success': False,
            'three_finger_detected': False,
            'shapes_created': 0,
            'errors': [],
            'positions_tested': []
        }

        try:
            # 1. Set the tool
            print(f"ğŸ”§ Setting tool to {tool_name}...")
            set_success = self.driver.execute_script(f"return window.setTool('{tool_name}');")

            if set_success:
                tool_results['tool_switch_success'] = True
                print(f"âœ… Tool switched to {tool_name}")

                # Verify tool was set
                current_tool = self.driver.execute_script("return window.getCurrentTool();")
                print(f"ğŸ” Current tool: {current_tool}")

                time.sleep(0.5)

                # 2. Test three-finger drag at different positions
                for position in self.test_positions:
                    x, y = position
                    print(f"ğŸ–ï¸ Testing three-finger at position ({x}, {y})...")

                    # Get initial shape count
                    initial_shapes = self.driver.execute_script("return window.getShapeCount();")

                    # Simulate three-finger touch sequence
                    self.driver.execute_script(f"window.simulateThreeFingerTouch({x}, {y}, 'down');")
                    time.sleep(0.1)

                    # Move slightly to simulate drag
                    self.driver.execute_script(f"window.simulateThreeFingerTouch({x + 50}, {y + 30}, 'move');")
                    time.sleep(0.1)

                    # Release
                    self.driver.execute_script(f"window.simulateThreeFingerTouch({x + 50}, {y + 30}, 'up');")
                    time.sleep(0.5)

                    # Check if shapes were created (for creation tools)
                    final_shapes = self.driver.execute_script("return window.getShapeCount();")
                    shapes_created = final_shapes - initial_shapes

                    position_result = {
                        'position': position,
                        'shapes_created': shapes_created,
                        'success': shapes_created > 0 if tool_name in ['draw', 'rectangle', 'ellipse', 'text'] else True
                    }

                    tool_results['positions_tested'].append(position_result)
                    tool_results['shapes_created'] += shapes_created

                    print(f"ğŸ“Š Position ({x}, {y}): {shapes_created} shapes created")

                    if shapes_created > 0:
                        tool_results['three_finger_detected'] = True

                # Determine overall success
                if tool_name in ['draw', 'rectangle', 'ellipse', 'text']:
                    # Creation tools should create shapes
                    tool_results['success'] = tool_results['shapes_created'] > 0
                else:
                    # Non-creation tools (select, eraser, hand) success if no errors
                    tool_results['success'] = tool_results['tool_switch_success'] and len(tool_results['errors']) == 0

            else:
                tool_results['errors'].append(f"Failed to switch to {tool_name} tool")
                print(f"âŒ Failed to switch to {tool_name} tool")

        except Exception as e:
            error_msg = f"Error testing {tool_name}: {str(e)}"
            tool_results['errors'].append(error_msg)
            print(f"âŒ {error_msg}")

        return tool_results

    def run_comprehensive_test(self):
        """Run comprehensive three-finger test for all tools"""
        print("\nğŸš€ SNAPE Three-Finger Tools Comprehensive Test")
        print("=" * 60)

        if not self.setup_chrome():
            return False

        if not self.load_canvas():
            return False

        self.inject_three_finger_simulator()

        # Test each tool
        for tool in self.tools_to_test:
            result = self.test_tool_with_three_finger(tool)
            self.test_results[tool] = result

            # Take screenshot after each tool test
            screenshot_path = f"three_finger_{tool}_test.png"
            self.driver.save_screenshot(screenshot_path)
            self.screenshots.append(screenshot_path)
            print(f"ğŸ“· Screenshot saved: {screenshot_path}")

        # Generate report
        self.generate_report()

        self.driver.quit()
        return True

    def generate_report(self):
        """Generate comprehensive test report"""
        print("\nğŸ“‹ SNAPE Three-Finger Test Report")
        print("=" * 50)

        total_tools = len(self.tools_to_test)
        successful_tools = 0
        failed_tools = []

        for tool, results in self.test_results.items():
            status = "âœ… PASS" if results['success'] else "âŒ FAIL"
            successful_tools += 1 if results['success'] else 0

            if not results['success']:
                failed_tools.append(tool)

            print(f"\nğŸ”§ {tool.upper()} TOOL {status}")
            print(f"   Tool Switch: {'âœ…' if results['tool_switch_success'] else 'âŒ'}")
            print(f"   Three-Finger Detected: {'âœ…' if results['three_finger_detected'] else 'âŒ'}")
            print(f"   Shapes Created: {results['shapes_created']}")
            print(f"   Positions Tested: {len(results['positions_tested'])}")

            if results['errors']:
                print(f"   Errors: {', '.join(results['errors'])}")

        print(f"\nğŸ“Š SUMMARY")
        print(f"Total Tools Tested: {total_tools}")
        print(f"Successful: {successful_tools}")
        print(f"Failed: {len(failed_tools)}")
        print(f"Success Rate: {(successful_tools/total_tools)*100:.1f}%")

        if failed_tools:
            print(f"\nâŒ Failed Tools: {', '.join(failed_tools)}")
            print("\nğŸ”§ RECOMMENDED FIXES:")

            for tool in failed_tools:
                results = self.test_results[tool]
                print(f"\n{tool.upper()}:")

                if not results['tool_switch_success']:
                    print(f"  - Fix tool switching mechanism for {tool}")

                if not results['three_finger_detected'] and tool in ['draw', 'rectangle', 'ellipse', 'text']:
                    print(f"  - Fix three-finger gesture detection for {tool}")

                if results['shapes_created'] == 0 and tool in ['draw', 'rectangle', 'ellipse', 'text']:
                    print(f"  - Fix shape creation with three-finger drag for {tool}")

        # Save detailed results to JSON
        with open('snape_three_finger_test_results.json', 'w') as f:
            json.dump({
                'timestamp': time.strftime('%Y-%m-%d %H:%M:%S'),
                'summary': {
                    'total_tools': total_tools,
                    'successful': successful_tools,
                    'failed': len(failed_tools),
                    'success_rate': (successful_tools/total_tools)*100
                },
                'results': self.test_results,
                'screenshots': self.screenshots
            }, f, indent=2)

        print(f"\nğŸ’¾ Detailed results saved to: snape_three_finger_test_results.json")
        print(f"ğŸ“· Screenshots: {', '.join(self.screenshots)}")

if __name__ == "__main__":
    tester = SNAPEThreeFingerToolsTest()
    tester.run_comprehensive_test()

