/**
 * Universal Input System for Canvas
 * Handles all input types (mouse, touch, three-finger drag) in a unified way
 */

class UniversalInputSystem {
    constructor(editor) {
        this.editor = editor;
        this.canvas = null;
        this.activePointers = new Map();
        this.isDragging = false;
        this.dragStart = null;
        this.currentTool = 'select';

        // Bind methods
        this.handlePointerDown = this.handlePointerDown.bind(this);
        this.handlePointerMove = this.handlePointerMove.bind(this);
        this.handlePointerUp = this.handlePointerUp.bind(this);

        // Validate and set canvas reference
        if (!editor) {
            throw new Error('UniversalInputSystem requires a valid editor instance');
        }

        if (!editor.canvas) {
            throw new Error('UniversalInputSystem requires editor to have a canvas instance');
        }

        this.canvas = editor.canvas;
        this.initialize();
    }

    initialize() {
        // Use pointer events for unified handling
        this.canvas.addEventListener('pointerdown', this.handlePointerDown);
        this.canvas.addEventListener('pointermove', this.handlePointerMove);
        this.canvas.addEventListener('pointerup', this.handlePointerUp);
        this.canvas.addEventListener('pointercancel', this.handlePointerUp);

        // Add wheel event for zoom functionality
        this.canvas.addEventListener('wheel', this.handleWheel.bind(this), { passive: false });

        // Add context menu handling
        this.canvas.addEventListener('contextmenu', this.handleContextMenu.bind(this));

        // Prevent default touch behaviors
        this.canvas.style.touchAction = 'none';

        console.log('ğŸ® Universal Input System initialized with zoom and context menu support');
    }

    setTool(toolId) {
        this.currentTool = toolId;
        console.log('ğŸ® Tool set to:', toolId);

        // Synchronize with StateChart system
        if (this.editor.rootState && this.editor.rootState.setCurrentTool) {
            this.editor.rootState.setCurrentTool(toolId);
        }
    }

    handlePointerDown(event) {
        event.preventDefault();
        event.stopPropagation();

        // Track pointer
        this.activePointers.set(event.pointerId, {
            x: event.clientX,
            y: event.clientY,
            startX: event.clientX,
            startY: event.clientY
        });

        console.log('ğŸ® Pointer down, active:', this.activePointers.size);

        // Convert to canvas coordinates
        const rect = this.canvas.getBoundingClientRect();
        const point = {
            x: event.clientX - rect.left,
            y: event.clientY - rect.top
        };

        // Check pointer count for different behaviors
        if (this.activePointers.size === 1) {
            // Single pointer - normal tool behavior
            this.handleSinglePointerDown(point, event);
        } else if (this.activePointers.size === 3) {
            // Three pointers - drag mode
            this.handleThreePointerDown(point, event);
        }
    }

    handlePointerMove(event) {
        if (!this.activePointers.has(event.pointerId)) return;

        event.preventDefault();
        event.stopPropagation();

        // Update pointer position
        const pointer = this.activePointers.get(event.pointerId);
        pointer.x = event.clientX;
        pointer.y = event.clientY;

        // Convert to canvas coordinates
        const rect = this.canvas.getBoundingClientRect();
        const point = {
            x: event.clientX - rect.left,
            y: event.clientY - rect.top
        };

        if (this.isDragging) {
            if (this.activePointers.size === 1) {
                this.handleSinglePointerMove(point, event);
            } else if (this.activePointers.size === 3) {
