            console.log('ğŸ® Zoom from', oldZ.toFixed(3), 'to', newZ.toFixed(3));

            // Update camera zoom
            this.editor.camera.z = newZ;

            // Trigger render with new zoom
            if (this.editor.renderer && this.editor.renderer.scheduleRender) {
                this.editor.renderer.scheduleRender();
            }
        } else {
            console.warn('ğŸ® No camera available for zoom');
        }
    }

    handleContextMenu(event) {
        console.log('ğŸ® Context menu requested at:', event.clientX, event.clientY);

        event.preventDefault();
        event.stopPropagation();

        // Convert to canvas coordinates
        const rect = this.canvas.getBoundingClientRect();
        const point = {
            x: event.clientX - rect.left,
            y: event.clientY - rect.top
        };

        // ğŸ¨ PRIORITY 1: Use Notion Context Menu System if available
        if (this.editor.notionContextMenu) {
            console.log('ğŸ¨ Using Notion Context Menu System');
            this.editor.notionContextMenu.show(event, {
                clickPoint: point,
                canvasRect: rect
            });
            return;
        }

        // PRIORITY 2: Check if editor has InputManager with context menu support
        if (this.editor.inputManager && this.editor.inputManager.showContextMenu) {
            console.log('ğŸ® Delegating to InputManager context menu');
            this.editor.inputManager.showContextMenu(event);
        } else if (this.editor.showContextMenu) {
            console.log('ğŸ® Using editor context menu');
            this.editor.showContextMenu(event);
        } else {
            console.log('ğŸ® No context menu system available');
        }
    }

    createText(point) {
        console.log('ğŸ® Creating text at:', point);

        const shape = new TLShape(TLShapeType.TEXT, null, {
            x: point.x,
            y: point.y,
            props: {
                text: 'Text',
                w: 100,
                h: 24,
                fontSize: 16,
                color: '#000000'
            }
        });

        this.editor.store.put(shape);

        const page = this.editor.store.getCurrentPage();
        if (page) {
            page.addShape(shape.id);
            this.editor.store.put(page);
        }

        // TODO: Open text editor
        console.log('ğŸ® Text created:', shape.id);
    }

    startErasing(point) {
        console.log('ğŸ® Starting eraser at:', point);
        this.eraseAt(point);
    }

    continueErasing(point) {
        this.eraseAt(point);
    }

    eraseAt(point) {
        const shapes = this.editor.getCurrentPageShapes();

        shapes.forEach(shape => {
            // Simple point-in-bounds check
            if (point.x >= shape.x && 
                point.x <= shape.x + (shape.props.w || 0) &&
                point.y >= shape.y && 
                point.y <= shape.y + (shape.props.h || 0)) {

                console.log('ğŸ® Erasing shape:', shape.id);
                this.editor.deleteShapes([shape.id]);
            }
        });
    }

    startSelection(point) {
        console.log('ğŸ® Starting selection at:', point);
        // TODO: Implement selection
    }

    updateSelection(point) {
        // TODO: Implement selection box
    }

    finalizeSelection() {
        // TODO: Finalize selection
    }

    finalizeShape() {
        console.log('ğŸ® Finalizing shape');
        this.currentShape = null;
    }

    destroy() {
        this.canvas.removeEventListener('pointerdown', this.handlePointerDown);
        this.canvas.removeEventListener('pointermove', this.handlePointerMove);
        this.canvas.removeEventListener('pointerup', this.handlePointerUp);
        this.canvas.removeEventListener('pointercancel', this.handlePointerUp);
    }
}

// Export
if (typeof window !== 'undefined') {
    window.UniversalInputSystem = UniversalInputSystem;
}

