        */
    }

    registerContextMenuSection(id, config) {
        this.contextMenuSections.set(id, config);
    }

    unregisterContextMenuSection(id) {
        this.contextMenuSections.delete(id);
    }

    getContextMenuSections(context = {}) {
        // Return all sections for now
        // FUTURE: Can filter based on context (e.g., shape selected, empty space, etc.)
        return Array.from(this.contextMenuSections.values());
    }

    handleGlobalShortcuts(event) {
        // Tool shortcuts
        const toolShortcuts = {
            'v': 'select',
            'd': 'draw', 
            'h': 'hand',
            'r': 'rectangle',
            'o': 'ellipse',
            't': 'text',
            'x': 'eraser'
        };

        if (toolShortcuts[event.key.toLowerCase()]) {
            event.preventDefault();
            this.editor.setCurrentTool(toolShortcuts[event.key.toLowerCase()]);
            return;
        }

        // ENHANCEMENT: Resize shortcuts for selected shapes
        const selectedShapes = this.editor.getSelectedShapes();
        if (selectedShapes.length > 0) {
            const resizeAmount = 10;
            let handled = false;

            if (event.altKey && event.shiftKey) {
                // Alt+Shift+Arrow: Resize selected shapes
                switch (event.key) {
                    case 'ArrowUp':
                        this.resizeSelectedShapes(0, -resizeAmount);
                        handled = true;
                        break;
                    case 'ArrowDown':
                        this.resizeSelectedShapes(0, resizeAmount);
                        handled = true;
                        break;
                    case 'ArrowLeft':
                        this.resizeSelectedShapes(-resizeAmount, 0);
                        handled = true;
                        break;
                    case 'ArrowRight':
                        this.resizeSelectedShapes(resizeAmount, 0);
                        handled = true;
                        break;
                }
            } else if (event.altKey) {
                // Alt+Arrow: Resize proportionally
                switch (event.key) {
                    case 'ArrowUp':
                        this.resizeSelectedShapes(-resizeAmount, -resizeAmount);
                        handled = true;
                        break;
                    case 'ArrowDown':
                        this.resizeSelectedShapes(resizeAmount, resizeAmount);
                        handled = true;
                        break;
                }
            }

            if (handled) {
                event.preventDefault();
                return;
            }
        }

        // Action shortcuts
        if (event.ctrlKey || event.metaKey) {
            switch (event.key.toLowerCase()) {
                case 'z':
                    event.preventDefault();
                    if (event.shiftKey) {
                        this.editor.redo();
                    } else {
                        this.editor.undo();
                    }
                    break;
                case 'a':
                    event.preventDefault();
                    this.editor.selectAll();
                    break;
                case '0':
                    // Cmd/Ctrl+0: Reset size to original
                    if (selectedShapes.length > 0) {
                        event.preventDefault();
                        this.resetSelectedShapesToOriginalSize();
                    }
                    break;
            }
        }
    }

    // ENHANCEMENT: Resize selected shapes by delta amounts
    resizeSelectedShapes(deltaW, deltaH) {
        const selectedShapes = this.editor.getSelectedShapes();
        if (selectedShapes.length === 0) return;

        console.log('âŒ¨ï¸ Keyboard resize:', { deltaW, deltaH, count: selectedShapes.length });

        this.editor.store.transaction(() => {
            selectedShapes.forEach(shape => {
                const currentBounds = shape.getBounds();
                const newW = Math.max(20, currentBounds.w + deltaW);
                const newH = Math.max(20, currentBounds.h + deltaH);

                // Update shape properties
                if (shape.props) {
                    shape.props.w = newW;
                    shape.props.h = newH;
                } else {
                    shape.w = newW;
                    shape.h = newH;
                }

                this.editor.store.put(shape);

                // Update HTML container if it's an embed
                if ((shape.type === 'embed' || shape.type === 'youtube') && this.editor.htmlContainer) {
                    this.editor.htmlContainer.updateShapeContainer(shape.id);
                }

                console.log(`ğŸ“ Resized ${shape.id} to ${newW}Ã—${newH}`);
            });
        });
    }

    // ENHANCEMENT: Reset shapes to their original/default size
    resetSelectedShapesToOriginalSize() {
        const selectedShapes = this.editor.getSelectedShapes();
        if (selectedShapes.length === 0) return;

        console.log('ğŸ”„ Resetting shapes to original size');

        this.editor.store.transaction(() => {
            selectedShapes.forEach(shape => {
                let defaultW = 100;
                let defaultH = 100;

                // Shape-specific default sizes
                if (shape.type === 'embed' || shape.type === 'youtube') {
                    defaultW = 800;
                    defaultH = 450;
                } else if (shape.type === 'text') {
                    defaultW = 200;
                    defaultH = 50;
                } else if (shape.type === 'arrow' || shape.type === 'line') {
                    defaultW = 200;
                    defaultH = 0;
                }

                // Update shape properties
                if (shape.props) {
                    shape.props.w = defaultW;
                    shape.props.h = defaultH;
                } else {
                    shape.w = defaultW;
                    shape.h = defaultH;
                }

                this.editor.store.put(shape);

                // Update HTML container if it's an embed
                if ((shape.type === 'embed' || shape.type === 'youtube') && this.editor.htmlContainer) {
                    this.editor.htmlContainer.updateShapeContainer(shape.id);
                }

                console.log(`ğŸ”„ Reset ${shape.id} to ${defaultW}Ã—${defaultH}`);
            });
        });
    }

    showContextMenu(event) {
        // Create context menu
        const contextMenu = document.createElement('div');
        contextMenu.className = 'canvas-context-menu';
        contextMenu.id = 'tldraw-context-menu';

        // Build menu content from modular sections
        const sections = this.getContextMenuSections({ event });
        const menuContent = [];

        sections.forEach((section, index) => {
            // Add separator if needed
            if (section.separator && index > 0) {
                menuContent.push('<div style="border-top: 1px solid var(--border-color); margin: 4px 0;"></div>');
            }

            // Add section title if provided
            if (section.title) {
                menuContent.push(`<div class="context-menu-item"><strong>${section.title}</strong></div>`);
            }

            // Add section items
            const items = section.items();
            items.forEach(item => {
                const activeClass = item.active ? 'active' : '';
                const shortcutSpan = item.shortcut ? 
                    `<span style="margin-left: auto; color: var(--text-muted);">${item.shortcut}</span>` : '';

                menuContent.push(`
                    <div class="context-menu-item ${activeClass}" data-menu-action="${item.label}">
                        <span>${item.icon || ''}</span>
                        <span>${item.label}</span>
                        ${shortcutSpan}
                    </div>
                `);
            });
        });

        contextMenu.innerHTML = menuContent.join('');

        // Attach event handlers
        contextMenu.addEventListener('click', (e) => {
            const menuItem = e.target.closest('.context-menu-item');
            if (menuItem) {
                const label = menuItem.getAttribute('data-menu-action');

                // Find and execute the action
                sections.forEach(section => {
                    const items = section.items();
                    const item = items.find(i => i.label === label);
                    if (item && item.onClick) {
                        item.onClick();
                        contextMenu.remove();
                    }
                });
            }
        });

        // Position menu
        const x = Math.min(event.clientX, window.innerWidth - 200);
        const y = Math.min(event.clientY, window.innerHeight - 300);

        contextMenu.style.position = 'fixed';
        contextMenu.style.left = x + 'px';
        contextMenu.style.top = y + 'px';
        contextMenu.style.zIndex = '4000';

        document.body.appendChild(contextMenu);

        // Remove on click elsewhere
        const removeMenu = (e) => {
            if (!contextMenu.contains(e.target)) {
                contextMenu.remove();
                document.removeEventListener('click', removeMenu);
            }
        };

        setTimeout(() => {
            document.addEventListener('click', removeMenu);
        }, 0);
    }

    handleThreeFingerTouch(event) {
        console.log('ğŸ–ï¸ SMART CONTEXTUAL: Three finger touch detected!');

        // Clear any existing timeout
        if (this.threeFingerTimeout) {
            clearTimeout(this.threeFingerTimeout);
        }

        // UNIVERSAL SMART CONTEXTUAL INPUT SYSTEM
        const context = this.analyzeFullContext(event);
        const action = this.determineContextualAction(context);

        console.log('ğŸ–ï¸ SMART CONTEXTUAL: Full context analysis:', context);
        console.log('ğŸ–ï¸ SMART CONTEXTUAL: Determined action:', action);

        // Execute the contextually appropriate action
        this.executeContextualAction(action, context, event);

        // Show contextual feedback
        this.showContextualFeedback(action);

        // Set timeout to clear pointers after gesture
        this.threeFingerTimeout = setTimeout(() => {
            this.activePointers.clear();
            console.log('Three finger gesture completed, cleared pointers');
        }, 500);
    }

    showThreeFingerFeedback() {
        // Create visual feedback for three-finger gesture
        const feedback = document.createElement('div');
        feedback.textContent = 'âœï¸ Draw Tool Activated';
        feedback.style.cssText = `
            position: fixed;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            background: var(--accent-green);
            color: var(--bg-primary);
            padding: 12px 24px;
            border-radius: 8px;
            font-weight: 600;
            z-index: 5000;
            box-shadow: 0 4px 20px rgba(0,0,0,0.3);
            pointer-events: none;
        `;

        document.body.appendChild(feedback);

        // Remove after animation
        setTimeout(() => {
            feedback.style.opacity = '0';
            feedback.style.transform = 'translate(-50%, -50%) scale(0.8)';
            feedback.style.transition = 'all 0.3s ease';

            setTimeout(() => {
                if (feedback.parentNode) {
                    feedback.parentNode.removeChild(feedback);
                }
            }, 300);
        }, 1500);
    }

    // UNIVERSAL SMART CONTEXTUAL INPUT SYSTEM
    analyzeFullContext(event) {
        const pointerPos = {
            x: event.clientX - this.editor.canvas.getBoundingClientRect().left,
            y: event.clientY - this.editor.canvas.getBoundingClientRect().top
        };

        const currentTool = this.editor.getCurrentTool();
        const selectedShapes = this.editor.getSelectedShapes();
        const shapeAtPoint = this.editor.getShapeAtPoint(pointerPos);
        const resizeHandle = this.editor.getResizeHandleAtPoint(pointerPos);

        return {
            pointer: pointerPos,
            currentTool: currentTool ? currentTool.id : 'select',
            selectedShapes: selectedShapes,
            selectedCount: selectedShapes.length,
            shapeAtPoint: shapeAtPoint,
            resizeHandle: resizeHandle,
            isOverShape: !!shapeAtPoint,
            isOverHandle: !!resizeHandle,
            isEmptyCanvas: !shapeAtPoint && !resizeHandle
        };
    }

    determineContextualAction(context) {
        // Priority-based contextual decision system

        // HIGHEST PRIORITY: Resize handle detected
        if (context.isOverHandle && context.selectedCount > 0) {
            return {
                type: 'resize',
                target: context.selectedShapes,
                handle: context.resizeHandle,
                description: 'Resize selected shapes'
            };
        }

        // HIGH PRIORITY: Shape selected and pointer over selected shape = move
        if (context.selectedCount > 0 && context.shapeAtPoint && 
            context.selectedShapes.some(s => s.id === context.shapeAtPoint.id)) {
            return {
                type: 'move',
                target: context.selectedShapes,
                description: 'Move selected shapes'
            };
        }

        // MEDIUM PRIORITY: Shape creation tools
        if (['rectangle', 'ellipse', 'draw'].includes(context.currentTool)) {
            return {
                type: 'create',
                tool: context.currentTool,
                description: `Create ${context.currentTool} shape`
            };
        }

        // MEDIUM PRIORITY: Eraser tool over shape
        if (context.currentTool === 'eraser' && context.isOverShape) {
            return {
                type: 'erase',
                target: context.shapeAtPoint,
                description: 'Erase shape'
            };
        }

        // LOW PRIORITY: Shape at point but different tool = select shape then use tool
        if (context.isOverShape) {
            return {
                type: 'select_and_tool',
                target: context.shapeAtPoint,
                tool: context.currentTool,
                description: `Select shape and apply ${context.currentTool} tool`
            };
        }

        // DEFAULT: Empty canvas = use current tool
        return {
            type: 'tool_default',
            tool: context.currentTool,
            description: `Use ${context.currentTool} tool on empty canvas`
        };
    }

    executeContextualAction(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Executing action:', action.type);

        // SIMPLIFIED APPROACH: Just ensure correct tool is active
        // Let the normal pointer event flow handle the actual interaction
        switch (action.type) {
            case 'resize':
            case 'move':
                // For resize/move, ensure select tool is active
                this.editor.setCurrentTool('select');
                this.updateToolbarUI('select');
                break;

            case 'create':
                // For shape creation, ensure correct tool is active
                this.editor.setCurrentTool(action.tool);
                this.updateToolbarUI(action.tool);
                break;

            case 'erase':
                // For erasing, ensure eraser tool is active
                this.editor.setCurrentTool('eraser');
                this.updateToolbarUI('eraser');
                break;

            case 'select_and_tool':
                // Select the shape first
                this.editor.setSelectedShapes([action.target]);
                // Set the tool
                this.editor.setCurrentTool(action.tool);
                this.updateToolbarUI(action.tool);
                break;

            case 'tool_default':
                // Ensure correct tool is active
                this.editor.setCurrentTool(action.tool);
                this.updateToolbarUI(action.tool);
                break;
        }

        // The normal pointer event flow will handle the actual interaction
        console.log('ğŸ–ï¸ CONTEXTUAL: Tool set to', action.tool || 'select', '- letting normal event flow handle interaction');
    }

    startContextualResize(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Starting resize operation');
        // Switch to select tool and trigger resize
        this.editor.setCurrentTool('select');
        this.updateToolbarUI('select');

        // Trigger resize state directly
        const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
        this.editor.handleEvent(eventInfo);
    }

    startContextualMove(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Starting move operation');
        // Switch to select tool for moving
        this.editor.setCurrentTool('select');
        this.updateToolbarUI('select');

        // Trigger move via pointer down
        const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
        this.editor.handleEvent(eventInfo);
    }

    startContextualCreate(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Starting shape creation with', action.tool);
        // Ensure correct tool is active
        this.editor.setCurrentTool(action.tool);
        this.updateToolbarUI(action.tool);

        // Trigger creation via pointer down
        const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
        this.editor.handleEvent(eventInfo);
    }

    startContextualErase(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Starting erase operation');
        // Ensure eraser tool is active
        this.editor.setCurrentTool('eraser');
        this.updateToolbarUI('eraser');

        // Trigger erase via pointer down
        const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
        this.editor.handleEvent(eventInfo);
    }

    selectAndApplyTool(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Selecting shape and applying tool');
        // Select the shape first
        this.editor.setSelectedShapes([action.target]);

        // Then apply the tool behavior
        this.applyDefaultToolBehavior(action, context, event);
    }

    applyDefaultToolBehavior(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Applying default tool behavior for', action.tool);
        // Ensure correct tool is active
        this.editor.setCurrentTool(action.tool);
        this.updateToolbarUI(action.tool);

        // Trigger tool behavior
        const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
        this.editor.handleEvent(eventInfo);
    }

    updateToolbarUI(toolId) {
        const toolButtons = document.querySelectorAll('.toolbar-btn[data-tool]');
        toolButtons.forEach(btn => btn.classList.remove('active'));

        const activeBtn = document.querySelector(`.toolbar-btn[data-tool="${toolId}"]`);
        if (activeBtn) {
            activeBtn.classList.add('active');
        }
    }

    showContextualFeedback(action) {
        const feedback = document.createElement('div');
        feedback.textContent = `ğŸ–ï¸ ${action.description}`;
        feedback.style.cssText = `
            position: fixed;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            background: var(--accent-green);
            color: var(--bg-primary);
            padding: 12px 24px;
            border-radius: 8px;
            font-weight: 600;
            z-index: 5000;
            box-shadow: 0 4px 20px rgba(0,0,0,0.3);
            pointer-events: none;
        `;

        document.body.appendChild(feedback);

        // Remove after animation
        setTimeout(() => {
            feedback.style.opacity = '0';
            feedback.style.transform = 'translate(-50%, -50%) scale(0.8)';
            feedback.style.transition = 'all 0.3s ease';

            setTimeout(() => {
                if (feedback.parentNode) {
                    feedback.parentNode.removeChild(feedback);
                }
            }, 300);
        }, 1500);
    }

    // TLDRAW-STYLE ASSET DROPDOWN MENU SYSTEM
    showAssetDropdown(shape, event) {
        // Remove any existing dropdown
        this.hideAssetDropdown();

        const dropdown = document.createElement('div');
        dropdown.id = 'asset-dropdown';
        dropdown.className = 'asset-dropdown';

        // Position near the shape or cursor
        const rect = this.editor.canvas.getBoundingClientRect();
        const x = shape.x + rect.left + 20;
        const y = shape.y + rect.top - 10;

        dropdown.style.cssText = `
            position: fixed;
            left: ${x}px;
            top: ${y}px;
            background: var(--bg-secondary);
            border: 1px solid var(--border-color);
            border-radius: 8px;
            box-shadow: 0 8px 24px rgba(0,0,0,0.2);
            z-index: 5000;
            font-family: 'Inter', sans-serif;
            font-size: 14px;
            min-width: 200px;
            max-width: 280px;
        `;

        // Create header
        const header = document.createElement('div');
        header.style.cssText = `
            padding: 12px 16px;
            border-bottom: 1px solid var(--border-color);
            color: var(--text-primary);
            font-weight: 600;
            display: flex;
            align-items: center;
            gap: 8px;
        `;

        const shapeIcon = this.getShapeIcon(shape.type);
        header.innerHTML = `${shapeIcon} ${this.getShapeName(shape.type)} Properties`;
        dropdown.appendChild(header);

        // Create sections
        this.addTransformSection(dropdown, shape);
        this.addStyleSection(dropdown, shape);
        this.addActionsSection(dropdown, shape);

        document.body.appendChild(dropdown);

        // Add click outside to close
        setTimeout(() => {
            document.addEventListener('click', this.handleDropdownOutsideClick.bind(this), { once: true });
        }, 0);

        console.log('ğŸ“‹ Asset dropdown shown for shape:', shape.type, shape.id);
    }

    addTransformSection(dropdown, shape) {
        const section = this.createDropdownSection('Transform', 'ğŸ”„');

        const options = [
            { label: 'Move Forward', icon: 'â¬†ï¸', action: () => this.moveShapeLayer(shape, 'forward') },
            { label: 'Move Backward', icon: 'â¬‡ï¸', action: () => this.moveShapeLayer(shape, 'backward') },
            { label: 'Move to Front', icon: 'â«', action: () => this.moveShapeLayer(shape, 'front') },
            { label: 'Move to Back', icon: 'â¬', action: () => this.moveShapeLayer(shape, 'back') },
            { label: 'Duplicate', icon: 'ğŸ“‹', action: () => this.duplicateShape(shape) },
            { label: 'Reset Size', icon: 'ğŸ“', action: () => this.resetShapeSize(shape) }
        ];

        options.forEach(option => {
            const item = this.createDropdownItem(option.label, option.icon, option.action);
            section.appendChild(item);
        });

        dropdown.appendChild(section);
    }

    addStyleSection(dropdown, shape) {
        const section = this.createDropdownSection('Style', 'ğŸ¨');

        const options = [
            { label: 'Change Color', icon: 'ğŸŒˆ', action: () => this.showColorPicker(shape) },
            { label: 'Fill Style', icon: 'ğŸ–Œï¸', action: () => this.toggleFillStyle(shape) },
            { label: 'Stroke Width', icon: 'âœï¸', action: () => this.adjustStrokeWidth(shape) },
            { label: 'Opacity', icon: 'ğŸ‘»', action: () => this.adjustOpacity(shape) }
        ];

        if (shape.type === 'text') {
            options.push(
                { label: 'Font Size', icon: 'ğŸ“', action: () => this.adjustFontSize(shape) },
                { label: 'Text Align', icon: 'ğŸ“„', action: () => this.changeTextAlign(shape) }
            );
        }

        options.forEach(option => {
            const item = this.createDropdownItem(option.label, option.icon, option.action);
            section.appendChild(item);
        });

        dropdown.appendChild(section);
    }

    addActionsSection(dropdown, shape) {
        const section = this.createDropdownSection('Actions', 'âš¡');

        const options = [
            { label: 'Group', icon: 'ğŸ“¦', action: () => this.groupSelectedShapes() },
            { label: 'Lock', icon: 'ğŸ”’', action: () => this.toggleShapeLock(shape) },
            { label: 'Export', icon: 'ğŸ“¤', action: () => this.exportShape(shape) },
            { label: 'Delete', icon: 'ğŸ—‘ï¸', action: () => this.deleteShape(shape), danger: true }
        ];

        options.forEach(option => {
            const item = this.createDropdownItem(option.label, option.icon, option.action, option.danger);
            section.appendChild(item);
        });

        dropdown.appendChild(section);
    }

    createDropdownSection(title, icon) {
        const section = document.createElement('div');
        section.style.cssText = `
            border-bottom: 1px solid var(--border-color);
        `;

        const header = document.createElement('div');
        header.style.cssText = `
            padding: 8px 16px;
            color: var(--text-muted);
            font-size: 12px;
            font-weight: 600;
            text-transform: uppercase;
            letter-spacing: 0.5px;
            background: var(--bg-primary);
        `;
        header.textContent = `${icon} ${title}`;
        section.appendChild(header);

        return section;
    }

    createDropdownItem(label, icon, action, danger = false) {
        const item = document.createElement('div');
        item.className = 'dropdown-item';
        item.style.cssText = `
            padding: 10px 16px;
            cursor: pointer;
            display: flex;
            align-items: center;
            gap: 10px;
            color: ${danger ? '#ef4444' : 'var(--text-primary)'};
            transition: background 0.15s ease;
        `;

        item.innerHTML = `<span style="min-width: 20px;">${icon}</span>${label}`;

        item.addEventListener('mouseenter', () => {
            item.style.background = danger ? 'rgba(239, 68, 68, 0.1)' : 'var(--bg-hover)';
        });

        item.addEventListener('mouseleave', () => {
            item.style.background = 'transparent';
        });

        item.addEventListener('click', (e) => {
            e.stopPropagation();
            action();
            this.hideAssetDropdown();
        });

        return item;
    }

    hideAssetDropdown() {
        const existing = document.getElementById('asset-dropdown');
        if (existing) {
            existing.remove();
        }
    }

    handleDropdownOutsideClick(event) {
        const dropdown = document.getElementById('asset-dropdown');
        if (dropdown && !dropdown.contains(event.target)) {
            this.hideAssetDropdown();
        }
    }

    getShapeIcon(type) {
        const icons = {
            'rectangle': 'â¬œ',
            'ellipse': 'â­•', 
            'text': 'ğŸ“',
            'arrow': 'â¡ï¸',
            'line': 'ğŸ“',
            'draw': 'âœï¸',
            'embed': 'ğŸ“º',
            'youtube': 'ğŸ“º'
        };
        return icons[type] || 'ğŸ”·';
    }

    getShapeName(type) {
        const names = {
            'rectangle': 'Rectangle',
            'ellipse': 'Ellipse',
            'text': 'Text',
            'arrow': 'Arrow', 
            'line': 'Line',
            'draw': 'Drawing',
            'embed': 'Embed',
            'youtube': 'YouTube'
        };
        return names[type] || 'Shape';
    }

    // MODULAR TRANSFORMATION ACTIONS
    moveShapeLayer(shape, direction) {
        console.log('ğŸ”„ Moving shape layer:', direction);
        // Implementation for layer management
    }

    duplicateShape(shape) {
        console.log('ğŸ“‹ Duplicating shape:', shape.id);
        const newShape = { ...shape, id: generateId(), x: shape.x + 20, y: shape.y + 20 };
        this.editor.store.put(newShape);
        const page = this.editor.store.getCurrentPage();
        if (page) {
            page.addShape(newShape.id);
            this.editor.store.put(page);
        }
    }

    resetShapeSize(shape) {
        console.log('ğŸ“ Resetting shape size');
        // Reset to default size based on shape type
    }

    showColorPicker(shape) {
        console.log('ğŸŒˆ Showing color picker');
        // Implementation for color picker
    }

    toggleFillStyle(shape) {
        console.log('ğŸ–Œï¸ Toggling fill style');
        // Cycle through fill styles: none, solid, pattern
    }

    deleteShape(shape) {
        console.log('ğŸ—‘ï¸ Deleting shape:', shape.id);
        this.editor.store.remove(shape.id);
        const page = this.editor.store.getCurrentPage();
        if (page) {
            page.removeShape(shape.id);
            this.editor.store.put(page);
        }
    }
}

// Export
if (typeof window !== 'undefined') {
    window.TLDrawEditor = TLDrawEditor;
    window.InputManager = InputManager;
}

