/**
 * TLDraw-style Editor - Main orchestrator class
 * Combines Store, StateChart, and Rendering like tldraw's Editor
 */

// Tool definitions
const tools = [
    { id: 'select', name: 'Select Tool', icon: 'â†–ï¸', shortcut: 'V' },
    { id: 'draw', name: 'Draw Tool', icon: 'âœï¸', shortcut: 'D' },
    { id: 'hand', name: 'Pan Tool', icon: 'âœ‹', shortcut: 'H' },
    { id: 'rectangle', name: 'Rectangle', icon: 'â¬œ', shortcut: 'R' },
    { id: 'ellipse', name: 'Ellipse', icon: 'â­•', shortcut: 'O' },
    { id: 'arrow', name: 'Arrow', icon: 'â¡ï¸', shortcut: 'A' },
    { id: 'line', name: 'Line', icon: 'ğŸ“', shortcut: 'L' },
    { id: 'text', name: 'Text', icon: 'ğŸ“', shortcut: 'T' },
    { id: 'eraser', name: 'Eraser', icon: 'ğŸ—‘ï¸', shortcut: 'X' }
];

class TLDrawEditor extends EventTarget {
    constructor(container) {
        super();

        this.container = container;
        this.canvas = null;
        this.ctx = null;

        // Core systems
        this.store = null;
        this.rootState = null;
        this.renderer = null;

        // Initialize input handling AFTER canvas creation
        this.inputManager = null;
        this.universalInput = null;

        // Performance tracking
        this.renderLoop = null;
        this.isRendering = false;

        this.initialize();
    }

    initialize() {
        console.log('TLDrawEditor: Initializing...');

        // Create canvas
        this.createCanvas();

        // Initialize store
        this.store = new TLStore();
        this.store.addEventListener('change', this.handleStoreChange.bind(this));

        // Initialize shape utilities registry
        this.shapeUtils = new ShapeUtilRegistry();
        console.log('ğŸ“Š Shape utilities registered:', this.shapeUtils.getAllTypes());

        // Initialize state system
        console.log('TLDrawEditor: Creating RootState...');
        this.rootState = new RootState(this);
        console.log('TLDrawEditor: RootState created:', !!this.rootState);
        console.log('TLDrawEditor: geoTool exists:', !!this.rootState.geoTool);

        if (this.rootState.geoTool) {
            console.log('TLDrawEditor: geoTool.setGeoType exists:', !!this.rootState.geoTool.setGeoType);
        }

        this.rootState.enter();
        console.log('TLDrawEditor: RootState entered');

        // Initialize renderer with camera for HTML container positioning
        this.renderer = new TLRenderer(this.canvas, this.store, this.getCamera());

        // Initialize HTML container system for embeds
        this.htmlContainer = new HTMLContainer(this.container, this.getCamera(), this.store);
        this.embedManager = new EmbedManager(this.htmlContainer, this.store);

        // Setup input handling AFTER canvas and systems are ready
        this.initializeInputSystems();

        // Start render loop
        this.startRenderLoop();

        console.log('TLDrawEditor: Initialized successfully');
    }

    /**
     * Initialize input systems with proper error handling and validation
     * Enterprise-grade approach with dependency checking
     */
    initializeInputSystems() {
        console.log('ğŸ® Initializing input systems...');

        try {
            // Validate prerequisites
            if (!this.canvas) {
                throw new Error('Canvas not available for input system initialization');
            }

            if (!this.container) {
                throw new Error('Container not available for input system initialization');
            }

            // Initialize Universal Input System with proper error handling
            if (window.UniversalInputSystem) {
                console.log('ğŸ® Using Universal Input System for three-finger drag support');

                try {
                    this.universalInput = new UniversalInputSystem(this);

                    // Create compatibility wrapper for inputManager
                    this.inputManager = {
                        isEnabled: true,
                        initialize: () => {
                            console.log('âœ… Input manager compatibility wrapper initialized');
                        },
                        destroy: () => {
                            if (this.universalInput && typeof this.universalInput.destroy === 'function') {
                                this.universalInput.destroy();
                            }
                        },
                        registerContextMenuSection: () => {},
                        unregisterContextMenuSection: () => {},
                        showContextMenu: () => {},
                        setTool: (toolId) => {
                            if (this.universalInput && typeof this.universalInput.setTool === 'function') {
                                this.universalInput.setTool(toolId);
                            }
                        }
                    };

                    // Initialize the input manager
                    this.inputManager.initialize();
                    console.log('âœ… Universal Input System initialized successfully');

                } catch (universalInputError) {
                    console.error('âŒ Universal Input System failed to initialize:', universalInputError);
                    this.fallbackToStandardInput();
                }

            } else {
                console.log('ğŸ“± Universal Input System not available, using standard Input Manager');
                this.fallbackToStandardInput();
            }

        } catch (error) {
            console.error('âŒ Input system initialization failed:', error);
            this.fallbackToStandardInput();
        }
    }

    /**
     * Fallback to standard input manager with error recovery
     */
    fallbackToStandardInput() {
        console.log('ğŸ”„ Falling back to standard input manager...');

        try {
            if (window.InputManager) {
                this.inputManager = new InputManager(this);
                this.inputManager.initialize();
                console.log('âœ… Standard input manager initialized successfully');
            } else {
                // Create minimal input manager if none available
                this.inputManager = {
                    isEnabled: true,
                    initialize: () => console.log('âš ï¸ Using minimal input manager'),
                    destroy: () => {},
                    registerContextMenuSection: () => {},
                    unregisterContextMenuSection: () => {},
                    showContextMenu: () => {},
                    setTool: () => {}
                };
                this.inputManager.initialize();
                console.log('âš ï¸ Using minimal input manager as fallback');
            }
        } catch (fallbackError) {
            console.error('âŒ Even fallback input manager failed:', fallbackError);
            // Last resort: create empty input manager
            this.inputManager = {
                isEnabled: false,
                initialize: () => {},
                destroy: () => {},
                registerContextMenuSection: () => {},
                unregisterContextMenuSection: () => {},
                showContextMenu: () => {},
                setTool: () => {}
            };
        }
    }

    createCanvas() {
        this.canvas = document.createElement('canvas');
        this.canvas.style.width = '100%';
        this.canvas.style.height = '100%';
        this.canvas.style.display = 'block';
        this.canvas.style.touchAction = 'none';
        this.canvas.style.cursor = 'default';

        // Set actual size
        const rect = this.container.getBoundingClientRect();
        this.canvas.width = rect.width * window.devicePixelRatio;
        this.canvas.height = rect.height * window.devicePixelRatio;
        this.canvas.style.width = rect.width + 'px';
        this.canvas.style.height = rect.height + 'px';

        this.ctx = this.canvas.getContext('2d');
        this.ctx.scale(window.devicePixelRatio, window.devicePixelRatio);

        this.container.appendChild(this.canvas);

        // Handle resize
        window.addEventListener('resize', () => {
            this.handleResize();
        });
    }

    handleResize() {
        const rect = this.container.getBoundingClientRect();
        this.canvas.width = rect.width * window.devicePixelRatio;
        this.canvas.height = rect.height * window.devicePixelRatio;
        this.canvas.style.width = rect.width + 'px';
        this.canvas.style.height = rect.height + 'px';

        this.ctx.scale(window.devicePixelRatio, window.devicePixelRatio);
        this.render();
    }

    handleStoreChange(event) {
        // Re-render when store changes
        this.render();

        // Emit change event
        this.dispatchEvent(new CustomEvent('change', { 
            detail: event.detail 
        }));
    }

    startRenderLoop() {
        if (this.renderLoop) return;

        console.log('TLDrawEditor: Starting render loop');

        const renderFrame = () => {
            if (this.isRendering) {
                this.render();
            }
            this.renderLoop = requestAnimationFrame(renderFrame);
        };

        this.isRendering = true;
        this.renderLoop = requestAnimationFrame(renderFrame);
    }

    stopRenderLoop() {
        if (this.renderLoop) {
            cancelAnimationFrame(this.renderLoop);
            this.renderLoop = null;
        }
        this.isRendering = false;
    }

    render() {
        if (!this.renderer) {
            console.warn('TLDrawEditor: No renderer available');
            return;
        }

        // Add occasional debug logging (every 60 frames)
        if (this.renderer.renderStats.frameCount % 60 === 0) {
            console.log('TLDrawEditor: Rendering frame', this.renderer.renderStats.frameCount);
        }

        // Render canvas shapes
        this.renderer.render();

        // Render HTML embeds
        this.renderEmbeds();
    }

    renderEmbeds() {
        if (!this.embedManager || !this.htmlContainer) {
            return;
        }

        // Get all embed shapes from store
        const shapes = this.store.getCurrentPageShapes();
        const embedShapes = shapes.filter(shape => 
            shape.type === 'embed' || shape.type === 'youtube'
        );

        // Update HTML container positions for camera changes
        this.htmlContainer.updateCamera(this.getCamera());

        // Render each embed shape
        embedShapes.forEach(shape => {
            const existingElement = this.htmlContainer.getHTMLElement(shape.id);

            if (!existingElement) {
                // Create new embed
                console.log('ğŸ¥ Creating embed for shape:', shape.id, shape.type);
                this.embedManager.createEmbed(shape);
            } else {
                // Update existing embed if needed
                this.embedManager.updateEmbed(shape);
            }
        });

        // Clean up removed embeds
        this.htmlContainer.cleanup();
    }

    // Tool management
    setCurrentTool(toolId) {
        console.log('TLDrawEditor: Setting tool to', toolId);

        // Update universal input system if available
        if (this.universalInput) {
            console.log('ğŸ® Updating universal input tool to:', toolId);
            this.universalInput.setTool(toolId);
        }

        // CRITICAL FIX: Check if rootState and geoTool exist
        if (!this.rootState) {
            console.error('TLDrawEditor: rootState is undefined!');
            return false;
        }

        // Handle special geo tool cases - FIX: Call the actual tool names, not 'geo'
        if (toolId === 'rectangle' || toolId === 'ellipse') {
            // Directly set the tool to rectangle or ellipse (these exist in RootState)
            return this.rootState.setTool(toolId);
        }

        return this.rootState.setTool(toolId);
    }

    // Alias for API compatibility
    setTool(toolId) {
        return this.setCurrentTool(toolId);
    }

    getCurrentTool() {
        console.log('ğŸ” TLDrawEditor.getCurrentTool called');
        console.log('ğŸ” rootState exists:', !!this.rootState);

        if (!this.rootState) {
            console.error('TLDrawEditor: rootState is undefined in getCurrentTool!');
            return 'select'; // Default fallback
        }

        console.log('ğŸ” rootState.getCurrentToolId exists:', typeof this.rootState.getCurrentToolId);

        if (!this.rootState.getCurrentToolId) {
            console.error('TLDrawEditor: rootState.getCurrentToolId is undefined!');
            return 'select'; // Default fallback
        }

        const result = this.rootState.getCurrentToolId();
        console.log('ğŸ” getCurrentToolId returned:', result);
        return result;
    }

    // Shape management (TLDraw-style API)
    createShapes(shapes) {
        const createdShapes = [];

        this.store.transaction(() => {
            shapes.forEach(shapeData => {
                const shape = new TLShape(shapeData.type, shapeData.id, shapeData);
                this.store.put(shape);

                // Add to current page
                const page = this.store.getCurrentPage();
                if (page) {
                    page.addShape(shape.id);
                    this.store.put(page);
                }

                // Call shape util's onShapeCreate if available
                const shapeUtil = this.shapeUtils.get(shape.type);
                if (shapeUtil && shapeUtil.onShapeCreate) {
                    shapeUtil.onShapeCreate(shape, this);
                }

                createdShapes.push(shape);
            });
        });

        return createdShapes;
    }

    // TLDraw API: createShape() - creates a single shape
    createShape(shapeData) {
        return this.createShapes([shapeData])[0];
    }

    updateShapes(updates) {
        this.store.transaction(() => {
            updates.forEach(update => {
                const shape = this.store.get(update.id);
                if (shape) {
                    Object.assign(shape, update);
                    this.store.put(shape);

                    // Call shape util's onShapeUpdate if available
                    const shapeUtil = this.shapeUtils.get(shape.type);
                    if (shapeUtil && shapeUtil.onShapeUpdate) {
                        shapeUtil.onShapeUpdate(shape, this);
                    }
                }
            });
        });
        return this;
    }

    deleteShapes(shapeIds) {
        this.store.transaction(() => {
            shapeIds.forEach(id => {
                const shape = this.store.get(id);

                // Call shape util's onShapeDelete if available
                if (shape) {
                    const shapeUtil = this.shapeUtils.get(shape.type);
                    if (shapeUtil && shapeUtil.onShapeDelete) {
                        shapeUtil.onShapeDelete(shape, this);
                    }
                }

                this.store.remove(id);

                // Remove from page
                const page = this.store.getCurrentPage();
                if (page) {
                    page.removeShape(id);
                    this.store.put(page);
                }
            });
        });
        return this;
    }

    getShape(id) {
        return this.store.get(id);
    }

    getShapes() {
        return this.store.getCurrentPageShapes();
    }

    // Alias for compatibility - many components expect this method
    getCurrentPageShapes() {
        return this.store.getCurrentPageShapes();
    }

    // Selection management
    select(...shapeIds) {
        const instance = this.store.getCurrentInstance();
        if (instance) {
            instance.setSelectedShapes(shapeIds);
            this.store.put(instance);
            // HTML containers will update selection visuals automatically
        }
        return this;
    }

    selectNone() {
        return this.select();
    }

    selectAll() {
        const shapes = this.getShapes();
        return this.select(...shapes.map(s => s.id));
    }

    getSelectedShapes() {
        return this.store.getSelectedShapes();
    }

    getSelectedShapeIds() {
        const instance = this.store.getCurrentInstance();
        return instance ? instance.selectedShapeIds : [];
    }

    // Camera management
    getCamera() {
        return this.store.getCurrentCamera();
    }

    // Hit testing with enhanced handle detection
    getShapeAtPoint(point, margin = 0) {
        // First check if we're over a resize handle
        const handleHit = this.getResizeHandleAtPoint(point);
        if (handleHit) {
            // Update cursor based on handle
            if (this.renderer && this.renderer.updateCursorFromHandle) {
                this.renderer.updateCursorFromHandle(handleHit);
            }
            return null; // Don't return shape when over handle
        } else {
            // Clear handle hover state
            if (this.renderer && this.renderer.clearHandleHover) {
                this.renderer.clearHandleHover();
            }
        }

        const shapes = this.getShapes();
        const camera = this.store.getCurrentCamera();

        // Convert screen point to page point
        const pagePoint = camera ? camera.screenToPage(point) : point;

        // Test shapes in reverse order (top to bottom)
        for (let i = shapes.length - 1; i >= 0; i--) {
            const shape = shapes[i];
            if (this.isPointInShape(pagePoint, shape, margin)) {
                return shape;
            }
        }

        return null;
    }

    // RESIZE CHUNK 1: Add resize handle hit detection
    getResizeHandleAtPoint(point) {
        if (!point || typeof point.x === 'undefined' || typeof point.y === 'undefined') {
            return null; // Invalid point
        }

        const selectedShapes = this.getSelectedShapes();
        if (selectedShapes.length !== 1) {
            return null; // Only show handles for single selection
        }

        const shape = selectedShapes[0];
        const bounds = shape.getBounds();
        const camera = this.store.getCurrentCamera();
        const handleSize = 8;
        const scale = camera ? 1 / camera.z : 1;
        const scaledHandleSize = handleSize * scale;
        const hitMargin = scaledHandleSize / 2;

        // Convert screen point to page point
        const pagePoint = camera ? camera.screenToPage(point) : point;

        console.log('ğŸ”§ RESIZE CHUNK 1: Testing handle hit at point:', {
            screenPoint: point,
            pagePoint: pagePoint,
            bounds: bounds,
            handleSize: scaledHandleSize,
            camera: camera ? { x: camera.x, y: camera.y, z: camera.z } : null
        });

        // Define handle positions (same as in renderer)
        const handles = [
            { x: bounds.x, y: bounds.y, type: 'top-left' },
            { x: bounds.x + bounds.w, y: bounds.y, type: 'top-right' },
            { x: bounds.x, y: bounds.y + bounds.h, type: 'bottom-left' },
            { x: bounds.x + bounds.w, y: bounds.y + bounds.h, type: 'bottom-right' },
            { x: bounds.x + bounds.w / 2, y: bounds.y, type: 'top' },
            { x: bounds.x + bounds.w / 2, y: bounds.y + bounds.h, type: 'bottom' },
            { x: bounds.x, y: bounds.y + bounds.h / 2, type: 'left' },
            { x: bounds.x + bounds.w, y: bounds.y + bounds.h / 2, type: 'right' }
        ];

        // Test each handle
        for (const handle of handles) {
            const distance = Math.hypot(
                pagePoint.x - handle.x,
                pagePoint.y - handle.y
            );

            if (distance <= hitMargin) {
                console.log('âœ… RESIZE CHUNK 1: Handle hit detected:', handle.type);
                return {
                    shape: shape,
                    handle: handle.type,
                    handlePos: { x: handle.x, y: handle.y },
                    distance: distance
                };
            }
        }

        console.log('âŒ RESIZE CHUNK 1: No handle hit');
        return null;
    }

    isPointInShape(point, shape, margin = 0) {
        const bounds = shape.getBounds();

        return point.x >= bounds.x - margin &&
               point.x <= bounds.x + bounds.w + margin &&
               point.y >= bounds.y - margin &&
               point.y <= bounds.y + bounds.h + margin;
    }

    getShapesInBounds(bounds) {
        const shapes = this.getShapes();
        return shapes.filter(shape => {
            const shapeBounds = shape.getBounds();
            return this.boundsIntersect(bounds, shapeBounds);
        });
    }

    boundsIntersect(a, b) {
        return !(a.x + a.w < b.x || 
                 b.x + b.w < a.x || 
                 a.y + a.h < b.y || 
                 b.y + b.h < a.y);
    }

    // Camera management
    getCamera() {
        return this.store.getCurrentCamera();
    }

    setCamera(camera) {
        this.store.put(camera);
        // Update renderer camera for HTML container positioning
        if (this.renderer) {
            this.renderer.updateCamera(camera);
        }
        return this;
    }

    panCamera(delta) {
        const camera = this.getCamera();
        if (camera) {
            camera.pan(delta);
            this.store.put(camera);
            // Update renderer camera for HTML container positioning
            if (this.renderer) {
                this.renderer.updateCamera(camera);
            }
        }
        return this;
    }

    zoomCamera(factor, center = null) {
        const camera = this.getCamera();
        if (camera) {
            camera.zoom(factor, center);
            this.store.put(camera);
            // Update renderer camera for HTML container positioning
            if (this.renderer) {
                this.renderer.updateCamera(camera);
            }
        }
        return this;
    }

    // History management
    undo() {
        return this.store.undo();
    }

    redo() {
        return this.store.redo();
    }

    mark(name) {
        this.store.mark(name);
        return this;
    }

    // Batch operations (like Editor.run())
    run(fn) {
        return this.store.transaction(fn);
    }

    // Export/Import
    getSnapshot() {
        return this.store.getSnapshot();
    }

    loadSnapshot(snapshot) {
        this.store.loadSnapshot(snapshot);
        return this;
    }

    // Event handling
    handleEvent(eventInfo) {
        return this.rootState.handleEvent(eventInfo);
    }

    // Context Menu Management - HIGHLY MODULAR!
    addContextMenuSection(id, config) {
        this.inputManager.registerContextMenuSection(id, config);
    }

    removeContextMenuSection(id) {
        this.inputManager.unregisterContextMenuSection(id);
    }

    // Cleanup
    destroy() {
        this.stopRenderLoop();
        this.inputManager.destroy();

        if (this.canvas && this.canvas.parentNode) {
            this.canvas.parentNode.removeChild(this.canvas);
        }
    }
}

// Input Manager - handles DOM events and converts to TLEventInfo
class InputManager {
    constructor(editor) {
        this.editor = editor;
        this.isEnabled = false;

        // Touch tracking
        this.activePointers = new Map();
        this.touchStartTime = 0;
        this.threeFingerTimeout = null;

        // Bound methods for cleanup
        this.boundHandlers = {};

        // MODULAR Context Menu System
        this.contextMenuSections = new Map();
        this.initializeContextMenuSections();
    }

    initialize() {
        if (this.isEnabled) return;

        const canvas = this.editor.canvas;

        // Pointer events
        this.boundHandlers.pointerdown = this.handlePointerDown.bind(this);
        this.boundHandlers.pointermove = this.handlePointerMove.bind(this);
        this.boundHandlers.pointerup = this.handlePointerUp.bind(this);
        this.boundHandlers.pointercancel = this.handlePointerUp.bind(this);

        canvas.addEventListener('pointerdown', this.boundHandlers.pointerdown);
        canvas.addEventListener('pointermove', this.boundHandlers.pointermove);
        canvas.addEventListener('pointerup', this.boundHandlers.pointerup);
        canvas.addEventListener('pointercancel', this.boundHandlers.pointercancel);

        // Mouse events
        this.boundHandlers.dblclick = this.handleDoubleClick.bind(this);
        this.boundHandlers.contextmenu = this.handleContextMenu.bind(this);
        this.boundHandlers.wheel = this.handleWheel.bind(this);

        canvas.addEventListener('dblclick', this.boundHandlers.dblclick);
        canvas.addEventListener('contextmenu', this.boundHandlers.contextmenu);
        canvas.addEventListener('wheel', this.boundHandlers.wheel);

        // Keyboard events (on document)
        this.boundHandlers.keydown = this.handleKeyDown.bind(this);
        this.boundHandlers.keyup = this.handleKeyUp.bind(this);

        document.addEventListener('keydown', this.boundHandlers.keydown);
        document.addEventListener('keyup', this.boundHandlers.keyup);

        this.isEnabled = true;
    }

    destroy() {
        if (!this.isEnabled) return;

        const canvas = this.editor.canvas;

        // Remove canvas events
        canvas.removeEventListener('pointerdown', this.boundHandlers.pointerdown);
        canvas.removeEventListener('pointermove', this.boundHandlers.pointermove);
        canvas.removeEventListener('pointerup', this.boundHandlers.pointerup);
        canvas.removeEventListener('pointercancel', this.boundHandlers.pointercancel);
        canvas.removeEventListener('dblclick', this.boundHandlers.dblclick);
        canvas.removeEventListener('contextmenu', this.boundHandlers.contextmenu);
        canvas.removeEventListener('wheel', this.boundHandlers.wheel);

        // Remove document events
        document.removeEventListener('keydown', this.boundHandlers.keydown);
        document.removeEventListener('keyup', this.boundHandlers.keyup);

        this.isEnabled = false;
    }

    createEventInfo(type, domEvent) {
        const rect = this.editor.canvas.getBoundingClientRect();
        let currentPagePoint = { x: 0, y: 0 };

        if (domEvent && domEvent.clientX !== undefined) {
            const screenPoint = {
                x: domEvent.clientX - rect.left,
                y: domEvent.clientY - rect.top
            };

            const camera = this.editor.getCamera();
            currentPagePoint = camera ? camera.screenToPage(screenPoint) : screenPoint;
        }

        return new TLEventInfo(type, {
            currentPagePoint,
            originPagePoint: currentPagePoint,
            screenPoint: domEvent && domEvent.clientX !== undefined ? {
                x: domEvent.clientX - rect.left,
                y: domEvent.clientY - rect.top
            } : { x: 0, y: 0 },
            shiftKey: domEvent?.shiftKey || false,
            ctrlKey: domEvent?.ctrlKey || false,
            altKey: domEvent?.altKey || false,
            metaKey: domEvent?.metaKey || false,
            pointerId: domEvent?.pointerId || 0,
            button: domEvent?.button || 0,
            isPen: domEvent?.pointerType === 'pen',
            target: domEvent?.target,
            key: domEvent?.key || '',
            code: domEvent?.code || '',
            delta: domEvent?.deltaX !== undefined ? 
                { x: domEvent.deltaX, y: domEvent.deltaY } : 
                { x: 0, y: 0 }
        });
    }

    handlePointerDown(event) {
        console.log('ğŸ”µ CANVAS CLICKED! Position:', event.clientX, event.clientY);
        event.preventDefault();

        // Track active pointers for multi-touch
        this.activePointers.set(event.pointerId, {
            x: event.clientX,
            y: event.clientY,
            type: event.pointerType,
            timestamp: Date.now()
        });

        console.log('ğŸ”µ Pointer tracked:', event.pointerId, 'Active pointers:', this.activePointers.size);

        // Check for three-finger touch
        if (this.activePointers.size === 3) {
            this.handleThreeFingerTouch(event);
            // CRITICAL FIX: Don't return early - still need to send event to tools
            // Three-finger gestures should STILL trigger tool behavior
        }

        // Handle pointer events (single or three-finger)
        if (this.activePointers.size === 1 || this.activePointers.size === 3) {
            console.log('ğŸ”µ CREATING EVENT INFO (size:', this.activePointers.size, ')');
            const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
            console.log('ğŸ”µ SENDING TO EDITOR');
            this.editor.handleEvent(eventInfo);
            console.log('ğŸ”µ EVENT SENT');
        }
    }

    handlePointerMove(event) {
        // Update pointer position
        if (this.activePointers.has(event.pointerId)) {
            this.activePointers.get(event.pointerId).x = event.clientX;
            this.activePointers.get(event.pointerId).y = event.clientY;
        }

        // Handle move for single pointer OR multi-finger drag
        if (this.activePointers.size >= 1) {
            const eventInfo = this.createEventInfo(TLEventType.POINTER_MOVE, event);
            this.editor.handleEvent(eventInfo);
        }
    }

    handlePointerUp(event) {
        // Remove pointer from tracking
        this.activePointers.delete(event.pointerId);

        console.log('Pointer up:', event.pointerId, 'Remaining pointers:', this.activePointers.size);

        // Handle up when returning to single pointer or no pointers
        // This handles both single-click and three-finger drag completion
        if (this.activePointers.size === 0 || this.activePointers.size === 2) {
            const eventInfo = this.createEventInfo(TLEventType.POINTER_UP, event);
            this.editor.handleEvent(eventInfo);
        }
    }

    handleDoubleClick(event) {
        event.preventDefault();
        const eventInfo = this.createEventInfo(TLEventType.DOUBLE_CLICK, event);
        this.editor.handleEvent(eventInfo);
    }

    handleContextMenu(event) {
        event.preventDefault();
        const eventInfo = this.createEventInfo(TLEventType.CONTEXT_MENU, event);
        this.editor.handleEvent(eventInfo);

        // Check if right-click is on a selected shape - show asset dropdown instead
        const pointerPos = {
            x: event.clientX - this.editor.canvas.getBoundingClientRect().left,
            y: event.clientY - this.editor.canvas.getBoundingClientRect().top
        };

        const shapeAtPoint = this.editor.getShapeAtPoint(pointerPos);
        const selectedShapes = this.editor.getSelectedShapes();

        if (shapeAtPoint && selectedShapes.some(s => s.id === shapeAtPoint.id)) {
            // Show TLDraw-style asset dropdown for selected shape
            this.showAssetDropdown(shapeAtPoint, event);
        } else {
            // Show general context menu
            this.showContextMenu(event);
        }
    }

    handleWheel(event) {
        event.preventDefault();

        // Handle zoom
        const factor = event.deltaY > 0 ? 0.9 : 1.1;
        const center = {
            x: event.clientX - this.editor.canvas.getBoundingClientRect().left,
            y: event.clientY - this.editor.canvas.getBoundingClientRect().top
        };

        // Use smooth zoom with HTML container synchronization
        this.editor.zoomCamera(factor, center);

        // Force render to update HTML container positions immediately
        this.editor.render();

        // Also send wheel event to state chart
        const eventInfo = this.createEventInfo(TLEventType.WHEEL, event);
        this.editor.handleEvent(eventInfo);
    }

    handleKeyDown(event) {
        const eventInfo = this.createEventInfo(TLEventType.KEY_DOWN, event);
        const result = this.editor.handleEvent(eventInfo);

        // Handle global shortcuts
        if (result.type === 'ignored') {
            this.handleGlobalShortcuts(event);
        }
    }

    handleKeyUp(event) {
        const eventInfo = this.createEventInfo(TLEventType.KEY_UP, event);
        this.editor.handleEvent(eventInfo);
    }

    initializeContextMenuSections() {
        // Tools Section
        this.registerContextMenuSection('tools', {
            title: 'Tools',
            items: () => {
                const currentTool = this.editor.getCurrentTool();
                return tools.map(tool => ({
                    label: tool.name,
                    icon: tool.icon,
                    shortcut: tool.shortcut,
                    active: currentTool === tool.id,
                    onClick: () => this.editor.setCurrentTool(tool.id)
                }));
            }
        });

        // Actions Section
        this.registerContextMenuSection('actions', {
            title: null, // No title = no header
            separator: true, // Add separator before this section
            items: () => [
                {
                    label: 'Select All',
                    icon: 'âŒ˜',
                    onClick: () => this.editor.selectAll()
                },
                {
                    label: 'Undo',
                    icon: 'â†¶',
                    onClick: () => this.editor.undo()
                },
                {
                    label: 'Redo',
                    icon: 'â†·',
                    onClick: () => this.editor.redo()
                }
            ]
        });

        // FUTURE: Easy to add embed section!
        // Example of how to add embed items to context menu:
        /*
        this.registerContextMenuSection('embeds', {
            title: 'Insert',
            separator: true,
            items: () => [
                {
                    label: 'Insert embed...',
                    icon: 'ğŸ”—',
                    onClick: () => openEmbedModal()
                },
                {
                    label: 'Upload image...',
                    icon: 'ğŸ“·',
                    onClick: () => uploadImage()
                }
            ]
        });
        */
    }

    registerContextMenuSection(id, config) {
        this.contextMenuSections.set(id, config);
    }

    unregisterContextMenuSection(id) {
        this.contextMenuSections.delete(id);
    }

    getContextMenuSections(context = {}) {
        // Return all sections for now
        // FUTURE: Can filter based on context (e.g., shape selected, empty space, etc.)
        return Array.from(this.contextMenuSections.values());
    }

    handleGlobalShortcuts(event) {
        // Tool shortcuts
        const toolShortcuts = {
            'v': 'select',
            'd': 'draw', 
            'h': 'hand',
            'r': 'rectangle',
            'o': 'ellipse',
            't': 'text',
            'x': 'eraser'
        };

        if (toolShortcuts[event.key.toLowerCase()]) {
            event.preventDefault();
            this.editor.setCurrentTool(toolShortcuts[event.key.toLowerCase()]);
            return;
        }

        // ENHANCEMENT: Resize shortcuts for selected shapes
        const selectedShapes = this.editor.getSelectedShapes();
        if (selectedShapes.length > 0) {
            const resizeAmount = 10;
            let handled = false;

            if (event.altKey && event.shiftKey) {
                // Alt+Shift+Arrow: Resize selected shapes
                switch (event.key) {
                    case 'ArrowUp':
                        this.resizeSelectedShapes(0, -resizeAmount);
                        handled = true;
                        break;
                    case 'ArrowDown':
                        this.resizeSelectedShapes(0, resizeAmount);
                        handled = true;
                        break;
                    case 'ArrowLeft':
                        this.resizeSelectedShapes(-resizeAmount, 0);
                        handled = true;
                        break;
                    case 'ArrowRight':
                        this.resizeSelectedShapes(resizeAmount, 0);
                        handled = true;
                        break;
                }
            } else if (event.altKey) {
                // Alt+Arrow: Resize proportionally
                switch (event.key) {
                    case 'ArrowUp':
                        this.resizeSelectedShapes(-resizeAmount, -resizeAmount);
                        handled = true;
                        break;
                    case 'ArrowDown':
                        this.resizeSelectedShapes(resizeAmount, resizeAmount);
                        handled = true;
                        break;
                }
            }

            if (handled) {
                event.preventDefault();
                return;
            }
        }

        // Action shortcuts
        if (event.ctrlKey || event.metaKey) {
            switch (event.key.toLowerCase()) {
                case 'z':
                    event.preventDefault();
                    if (event.shiftKey) {
                        this.editor.redo();
                    } else {
                        this.editor.undo();
                    }
                    break;
                case 'a':
                    event.preventDefault();
                    this.editor.selectAll();
                    break;
                case '0':
                    // Cmd/Ctrl+0: Reset size to original
                    if (selectedShapes.length > 0) {
                        event.preventDefault();
                        this.resetSelectedShapesToOriginalSize();
                    }
                    break;
            }
        }
    }

    // ENHANCEMENT: Resize selected shapes by delta amounts
    resizeSelectedShapes(deltaW, deltaH) {
        const selectedShapes = this.editor.getSelectedShapes();
        if (selectedShapes.length === 0) return;

        console.log('âŒ¨ï¸ Keyboard resize:', { deltaW, deltaH, count: selectedShapes.length });

        this.editor.store.transaction(() => {
            selectedShapes.forEach(shape => {
                const currentBounds = shape.getBounds();
                const newW = Math.max(20, currentBounds.w + deltaW);
                const newH = Math.max(20, currentBounds.h + deltaH);

                // Update shape properties
                if (shape.props) {
                    shape.props.w = newW;
                    shape.props.h = newH;
                } else {
                    shape.w = newW;
                    shape.h = newH;
                }

                this.editor.store.put(shape);

                // Update HTML container if it's an embed
                if ((shape.type === 'embed' || shape.type === 'youtube') && this.editor.htmlContainer) {
                    this.editor.htmlContainer.updateShapeContainer(shape.id);
                }

                console.log(`ğŸ“ Resized ${shape.id} to ${newW}Ã—${newH}`);
            });
        });
    }

    // ENHANCEMENT: Reset shapes to their original/default size
    resetSelectedShapesToOriginalSize() {
        const selectedShapes = this.editor.getSelectedShapes();
        if (selectedShapes.length === 0) return;

        console.log('ğŸ”„ Resetting shapes to original size');

        this.editor.store.transaction(() => {
            selectedShapes.forEach(shape => {
                let defaultW = 100;
                let defaultH = 100;

                // Shape-specific default sizes
                if (shape.type === 'embed' || shape.type === 'youtube') {
                    defaultW = 800;
                    defaultH = 450;
                } else if (shape.type === 'text') {
                    defaultW = 200;
                    defaultH = 50;
                } else if (shape.type === 'arrow' || shape.type === 'line') {
                    defaultW = 200;
                    defaultH = 0;
                }

                // Update shape properties
                if (shape.props) {
                    shape.props.w = defaultW;
                    shape.props.h = defaultH;
                } else {
                    shape.w = defaultW;
                    shape.h = defaultH;
                }

                this.editor.store.put(shape);

                // Update HTML container if it's an embed
                if ((shape.type === 'embed' || shape.type === 'youtube') && this.editor.htmlContainer) {
                    this.editor.htmlContainer.updateShapeContainer(shape.id);
                }

                console.log(`ğŸ”„ Reset ${shape.id} to ${defaultW}Ã—${defaultH}`);
            });
        });
    }

    showContextMenu(event) {
        // Create context menu
        const contextMenu = document.createElement('div');
        contextMenu.className = 'canvas-context-menu';
        contextMenu.id = 'tldraw-context-menu';

        // Build menu content from modular sections
        const sections = this.getContextMenuSections({ event });
        const menuContent = [];

        sections.forEach((section, index) => {
            // Add separator if needed
            if (section.separator && index > 0) {
                menuContent.push('<div style="border-top: 1px solid var(--border-color); margin: 4px 0;"></div>');
            }

            // Add section title if provided
            if (section.title) {
                menuContent.push(`<div class="context-menu-item"><strong>${section.title}</strong></div>`);
            }

            // Add section items
            const items = section.items();
            items.forEach(item => {
                const activeClass = item.active ? 'active' : '';
                const shortcutSpan = item.shortcut ? 
                    `<span style="margin-left: auto; color: var(--text-muted);">${item.shortcut}</span>` : '';

                menuContent.push(`
                    <div class="context-menu-item ${activeClass}" data-menu-action="${item.label}">
                        <span>${item.icon || ''}</span>
                        <span>${item.label}</span>
                        ${shortcutSpan}
                    </div>
                `);
            });
        });

        contextMenu.innerHTML = menuContent.join('');

        // Attach event handlers
        contextMenu.addEventListener('click', (e) => {
            const menuItem = e.target.closest('.context-menu-item');
            if (menuItem) {
                const label = menuItem.getAttribute('data-menu-action');

                // Find and execute the action
                sections.forEach(section => {
                    const items = section.items();
                    const item = items.find(i => i.label === label);
                    if (item && item.onClick) {
                        item.onClick();
                        contextMenu.remove();
                    }
                });
            }
        });

        // Position menu
        const x = Math.min(event.clientX, window.innerWidth - 200);
        const y = Math.min(event.clientY, window.innerHeight - 300);

        contextMenu.style.position = 'fixed';
        contextMenu.style.left = x + 'px';
        contextMenu.style.top = y + 'px';
        contextMenu.style.zIndex = '4000';

        document.body.appendChild(contextMenu);

        // Remove on click elsewhere
        const removeMenu = (e) => {
            if (!contextMenu.contains(e.target)) {
                contextMenu.remove();
                document.removeEventListener('click', removeMenu);
            }
        };

        setTimeout(() => {
            document.addEventListener('click', removeMenu);
        }, 0);
    }

    handleThreeFingerTouch(event) {
        console.log('ğŸ–ï¸ SMART CONTEXTUAL: Three finger touch detected!');

        // Clear any existing timeout
        if (this.threeFingerTimeout) {
            clearTimeout(this.threeFingerTimeout);
        }

        // UNIVERSAL SMART CONTEXTUAL INPUT SYSTEM
        const context = this.analyzeFullContext(event);
        const action = this.determineContextualAction(context);

        console.log('ğŸ–ï¸ SMART CONTEXTUAL: Full context analysis:', context);
        console.log('ğŸ–ï¸ SMART CONTEXTUAL: Determined action:', action);

        // Execute the contextually appropriate action
        this.executeContextualAction(action, context, event);

        // Show contextual feedback
        this.showContextualFeedback(action);

        // Set timeout to clear pointers after gesture
        this.threeFingerTimeout = setTimeout(() => {
            this.activePointers.clear();
            console.log('Three finger gesture completed, cleared pointers');
        }, 500);
    }

    showThreeFingerFeedback() {
        // Create visual feedback for three-finger gesture
        const feedback = document.createElement('div');
        feedback.textContent = 'âœï¸ Draw Tool Activated';
        feedback.style.cssText = `
            position: fixed;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            background: var(--accent-green);
            color: var(--bg-primary);
            padding: 12px 24px;
            border-radius: 8px;
            font-weight: 600;
            z-index: 5000;
            box-shadow: 0 4px 20px rgba(0,0,0,0.3);
            pointer-events: none;
        `;

        document.body.appendChild(feedback);

        // Remove after animation
        setTimeout(() => {
            feedback.style.opacity = '0';
            feedback.style.transform = 'translate(-50%, -50%) scale(0.8)';
            feedback.style.transition = 'all 0.3s ease';

            setTimeout(() => {
                if (feedback.parentNode) {
                    feedback.parentNode.removeChild(feedback);
                }
            }, 300);
        }, 1500);
    }

    // UNIVERSAL SMART CONTEXTUAL INPUT SYSTEM
    analyzeFullContext(event) {
        const pointerPos = {
            x: event.clientX - this.editor.canvas.getBoundingClientRect().left,
            y: event.clientY - this.editor.canvas.getBoundingClientRect().top
        };

        const currentTool = this.editor.getCurrentTool();
        const selectedShapes = this.editor.getSelectedShapes();
        const shapeAtPoint = this.editor.getShapeAtPoint(pointerPos);
        const resizeHandle = this.editor.getResizeHandleAtPoint(pointerPos);

        return {
            pointer: pointerPos,
            currentTool: currentTool ? currentTool.id : 'select',
            selectedShapes: selectedShapes,
            selectedCount: selectedShapes.length,
            shapeAtPoint: shapeAtPoint,
            resizeHandle: resizeHandle,
            isOverShape: !!shapeAtPoint,
            isOverHandle: !!resizeHandle,
            isEmptyCanvas: !shapeAtPoint && !resizeHandle
        };
    }

    determineContextualAction(context) {
        // Priority-based contextual decision system

        // HIGHEST PRIORITY: Resize handle detected
        if (context.isOverHandle && context.selectedCount > 0) {
            return {
                type: 'resize',
                target: context.selectedShapes,
                handle: context.resizeHandle,
                description: 'Resize selected shapes'
            };
        }

        // HIGH PRIORITY: Shape selected and pointer over selected shape = move
        if (context.selectedCount > 0 && context.shapeAtPoint && 
            context.selectedShapes.some(s => s.id === context.shapeAtPoint.id)) {
            return {
                type: 'move',
                target: context.selectedShapes,
                description: 'Move selected shapes'
            };
        }

        // MEDIUM PRIORITY: Shape creation tools
        if (['rectangle', 'ellipse', 'draw'].includes(context.currentTool)) {
            return {
                type: 'create',
                tool: context.currentTool,
                description: `Create ${context.currentTool} shape`
            };
        }

        // MEDIUM PRIORITY: Eraser tool over shape
        if (context.currentTool === 'eraser' && context.isOverShape) {
            return {
                type: 'erase',
                target: context.shapeAtPoint,
                description: 'Erase shape'
            };
        }

        // LOW PRIORITY: Shape at point but different tool = select shape then use tool
        if (context.isOverShape) {
            return {
                type: 'select_and_tool',
                target: context.shapeAtPoint,
                tool: context.currentTool,
                description: `Select shape and apply ${context.currentTool} tool`
            };
        }

        // DEFAULT: Empty canvas = use current tool
        return {
            type: 'tool_default',
            tool: context.currentTool,
            description: `Use ${context.currentTool} tool on empty canvas`
        };
    }

    executeContextualAction(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Executing action:', action.type);

        // SIMPLIFIED APPROACH: Just ensure correct tool is active
        // Let the normal pointer event flow handle the actual interaction
        switch (action.type) {
            case 'resize':
            case 'move':
                // For resize/move, ensure select tool is active
                this.editor.setCurrentTool('select');
                this.updateToolbarUI('select');
                break;

            case 'create':
                // For shape creation, ensure correct tool is active
                this.editor.setCurrentTool(action.tool);
                this.updateToolbarUI(action.tool);
                break;

            case 'erase':
                // For erasing, ensure eraser tool is active
                this.editor.setCurrentTool('eraser');
                this.updateToolbarUI('eraser');
                break;

            case 'select_and_tool':
                // Select the shape first
                this.editor.setSelectedShapes([action.target]);
                // Set the tool
                this.editor.setCurrentTool(action.tool);
                this.updateToolbarUI(action.tool);
                break;

            case 'tool_default':
                // Ensure correct tool is active
                this.editor.setCurrentTool(action.tool);
                this.updateToolbarUI(action.tool);
                break;
        }

        // The normal pointer event flow will handle the actual interaction
        console.log('ğŸ–ï¸ CONTEXTUAL: Tool set to', action.tool || 'select', '- letting normal event flow handle interaction');
    }

    startContextualResize(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Starting resize operation');
        // Switch to select tool and trigger resize
        this.editor.setCurrentTool('select');
        this.updateToolbarUI('select');

        // Trigger resize state directly
        const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
        this.editor.handleEvent(eventInfo);
    }

    startContextualMove(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Starting move operation');
        // Switch to select tool for moving
        this.editor.setCurrentTool('select');
        this.updateToolbarUI('select');

        // Trigger move via pointer down
        const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
        this.editor.handleEvent(eventInfo);
    }

    startContextualCreate(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Starting shape creation with', action.tool);
        // Ensure correct tool is active
        this.editor.setCurrentTool(action.tool);
        this.updateToolbarUI(action.tool);

        // Trigger creation via pointer down
        const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
        this.editor.handleEvent(eventInfo);
    }

    startContextualErase(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Starting erase operation');
        // Ensure eraser tool is active
        this.editor.setCurrentTool('eraser');
        this.updateToolbarUI('eraser');

        // Trigger erase via pointer down
        const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
        this.editor.handleEvent(eventInfo);
    }

    selectAndApplyTool(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Selecting shape and applying tool');
        // Select the shape first
        this.editor.setSelectedShapes([action.target]);

        // Then apply the tool behavior
        this.applyDefaultToolBehavior(action, context, event);
    }

    applyDefaultToolBehavior(action, context, event) {
        console.log('ğŸ–ï¸ CONTEXTUAL: Applying default tool behavior for', action.tool);
        // Ensure correct tool is active
        this.editor.setCurrentTool(action.tool);
        this.updateToolbarUI(action.tool);

        // Trigger tool behavior
        const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
        this.editor.handleEvent(eventInfo);
    }

    updateToolbarUI(toolId) {
        const toolButtons = document.querySelectorAll('.toolbar-btn[data-tool]');
        toolButtons.forEach(btn => btn.classList.remove('active'));

        const activeBtn = document.querySelector(`.toolbar-btn[data-tool="${toolId}"]`);
        if (activeBtn) {
            activeBtn.classList.add('active');
        }
    }

    showContextualFeedback(action) {
        const feedback = document.createElement('div');
        feedback.textContent = `ğŸ–ï¸ ${action.description}`;
        feedback.style.cssText = `
            position: fixed;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            background: var(--accent-green);
            color: var(--bg-primary);
            padding: 12px 24px;
            border-radius: 8px;
            font-weight: 600;
            z-index: 5000;
            box-shadow: 0 4px 20px rgba(0,0,0,0.3);
            pointer-events: none;
        `;

        document.body.appendChild(feedback);

        // Remove after animation
        setTimeout(() => {
            feedback.style.opacity = '0';
            feedback.style.transform = 'translate(-50%, -50%) scale(0.8)';
            feedback.style.transition = 'all 0.3s ease';

            setTimeout(() => {
                if (feedback.parentNode) {
                    feedback.parentNode.removeChild(feedback);
                }
            }, 300);
        }, 1500);
    }

    // TLDRAW-STYLE ASSET DROPDOWN MENU SYSTEM
    showAssetDropdown(shape, event) {
        // Remove any existing dropdown
        this.hideAssetDropdown();

        const dropdown = document.createElement('div');
        dropdown.id = 'asset-dropdown';
        dropdown.className = 'asset-dropdown';

        // Position near the shape or cursor
        const rect = this.editor.canvas.getBoundingClientRect();
        const x = shape.x + rect.left + 20;
        const y = shape.y + rect.top - 10;

        dropdown.style.cssText = `
            position: fixed;
            left: ${x}px;
            top: ${y}px;
            background: var(--bg-secondary);
            border: 1px solid var(--border-color);
            border-radius: 8px;
            box-shadow: 0 8px 24px rgba(0,0,0,0.2);
            z-index: 5000;
            font-family: 'Inter', sans-serif;
            font-size: 14px;
            min-width: 200px;
            max-width: 280px;
        `;

        // Create header
        const header = document.createElement('div');
        header.style.cssText = `
            padding: 12px 16px;
            border-bottom: 1px solid var(--border-color);
            color: var(--text-primary);
            font-weight: 600;
            display: flex;
            align-items: center;
            gap: 8px;
        `;

        const shapeIcon = this.getShapeIcon(shape.type);
        header.innerHTML = `${shapeIcon} ${this.getShapeName(shape.type)} Properties`;
        dropdown.appendChild(header);

        // Create sections
        this.addTransformSection(dropdown, shape);
        this.addStyleSection(dropdown, shape);
        this.addActionsSection(dropdown, shape);

        document.body.appendChild(dropdown);

        // Add click outside to close
        setTimeout(() => {
            document.addEventListener('click', this.handleDropdownOutsideClick.bind(this), { once: true });
        }, 0);

        console.log('ğŸ“‹ Asset dropdown shown for shape:', shape.type, shape.id);
    }

    addTransformSection(dropdown, shape) {
        const section = this.createDropdownSection('Transform', 'ğŸ”„');

        const options = [
            { label: 'Move Forward', icon: 'â¬†ï¸', action: () => this.moveShapeLayer(shape, 'forward') },
            { label: 'Move Backward', icon: 'â¬‡ï¸', action: () => this.moveShapeLayer(shape, 'backward') },
            { label: 'Move to Front', icon: 'â«', action: () => this.moveShapeLayer(shape, 'front') },
            { label: 'Move to Back', icon: 'â¬', action: () => this.moveShapeLayer(shape, 'back') },
            { label: 'Duplicate', icon: 'ğŸ“‹', action: () => this.duplicateShape(shape) },
            { label: 'Reset Size', icon: 'ğŸ“', action: () => this.resetShapeSize(shape) }
        ];

        options.forEach(option => {
            const item = this.createDropdownItem(option.label, option.icon, option.action);
            section.appendChild(item);
        });

        dropdown.appendChild(section);
    }

    addStyleSection(dropdown, shape) {
        const section = this.createDropdownSection('Style', 'ğŸ¨');

        const options = [
            { label: 'Change Color', icon: 'ğŸŒˆ', action: () => this.showColorPicker(shape) },
            { label: 'Fill Style', icon: 'ğŸ–Œï¸', action: () => this.toggleFillStyle(shape) },
            { label: 'Stroke Width', icon: 'âœï¸', action: () => this.adjustStrokeWidth(shape) },
            { label: 'Opacity', icon: 'ğŸ‘»', action: () => this.adjustOpacity(shape) }
        ];

        if (shape.type === 'text') {
            options.push(
                { label: 'Font Size', icon: 'ğŸ“', action: () => this.adjustFontSize(shape) },
                { label: 'Text Align', icon: 'ğŸ“„', action: () => this.changeTextAlign(shape) }
            );
        }

        options.forEach(option => {
            const item = this.createDropdownItem(option.label, option.icon, option.action);
            section.appendChild(item);
        });

        dropdown.appendChild(section);
    }

    addActionsSection(dropdown, shape) {
        const section = this.createDropdownSection('Actions', 'âš¡');

        const options = [
            { label: 'Group', icon: 'ğŸ“¦', action: () => this.groupSelectedShapes() },
            { label: 'Lock', icon: 'ğŸ”’', action: () => this.toggleShapeLock(shape) },
            { label: 'Export', icon: 'ğŸ“¤', action: () => this.exportShape(shape) },
            { label: 'Delete', icon: 'ğŸ—‘ï¸', action: () => this.deleteShape(shape), danger: true }
        ];

        options.forEach(option => {
            const item = this.createDropdownItem(option.label, option.icon, option.action, option.danger);
            section.appendChild(item);
        });

        dropdown.appendChild(section);
    }

    createDropdownSection(title, icon) {
        const section = document.createElement('div');
        section.style.cssText = `
            border-bottom: 1px solid var(--border-color);
        `;

        const header = document.createElement('div');
        header.style.cssText = `
            padding: 8px 16px;
            color: var(--text-muted);
            font-size: 12px;
            font-weight: 600;
            text-transform: uppercase;
            letter-spacing: 0.5px;
            background: var(--bg-primary);
        `;
        header.textContent = `${icon} ${title}`;
        section.appendChild(header);

        return section;
    }

    createDropdownItem(label, icon, action, danger = false) {
        const item = document.createElement('div');
        item.className = 'dropdown-item';
        item.style.cssText = `
            padding: 10px 16px;
            cursor: pointer;
            display: flex;
            align-items: center;
            gap: 10px;
            color: ${danger ? '#ef4444' : 'var(--text-primary)'};
            transition: background 0.15s ease;
        `;

        item.innerHTML = `<span style="min-width: 20px;">${icon}</span>${label}`;

        item.addEventListener('mouseenter', () => {
            item.style.background = danger ? 'rgba(239, 68, 68, 0.1)' : 'var(--bg-hover)';
        });

        item.addEventListener('mouseleave', () => {
            item.style.background = 'transparent';
        });

        item.addEventListener('click', (e) => {
            e.stopPropagation();
            action();
            this.hideAssetDropdown();
        });

        return item;
    }

    hideAssetDropdown() {
        const existing = document.getElementById('asset-dropdown');
        if (existing) {
            existing.remove();
        }
    }

    handleDropdownOutsideClick(event) {
        const dropdown = document.getElementById('asset-dropdown');
        if (dropdown && !dropdown.contains(event.target)) {
            this.hideAssetDropdown();
        }
    }

    getShapeIcon(type) {
        const icons = {
            'rectangle': 'â¬œ',
            'ellipse': 'â­•', 
            'text': 'ğŸ“',
            'arrow': 'â¡ï¸',
            'line': 'ğŸ“',
            'draw': 'âœï¸',
            'embed': 'ğŸ“º',
            'youtube': 'ğŸ“º'
        };
        return icons[type] || 'ğŸ”·';
    }

    getShapeName(type) {
        const names = {
            'rectangle': 'Rectangle',
            'ellipse': 'Ellipse',
            'text': 'Text',
            'arrow': 'Arrow', 
            'line': 'Line',
            'draw': 'Drawing',
            'embed': 'Embed',
            'youtube': 'YouTube'
        };
        return names[type] || 'Shape';
    }

    // MODULAR TRANSFORMATION ACTIONS
    moveShapeLayer(shape, direction) {
        console.log('ğŸ”„ Moving shape layer:', direction);
        // Implementation for layer management
    }

    duplicateShape(shape) {
        console.log('ğŸ“‹ Duplicating shape:', shape.id);
        const newShape = { ...shape, id: generateId(), x: shape.x + 20, y: shape.y + 20 };
        this.editor.store.put(newShape);
        const page = this.editor.store.getCurrentPage();
        if (page) {
            page.addShape(newShape.id);
            this.editor.store.put(page);
        }
    }

    resetShapeSize(shape) {
        console.log('ğŸ“ Resetting shape size');
        // Reset to default size based on shape type
    }

    showColorPicker(shape) {
        console.log('ğŸŒˆ Showing color picker');
        // Implementation for color picker
    }

    toggleFillStyle(shape) {
        console.log('ğŸ–Œï¸ Toggling fill style');
        // Cycle through fill styles: none, solid, pattern
    }

    deleteShape(shape) {
        console.log('ğŸ—‘ï¸ Deleting shape:', shape.id);
        this.editor.store.remove(shape.id);
        const page = this.editor.store.getCurrentPage();
        if (page) {
            page.removeShape(shape.id);
            this.editor.store.put(page);
        }
    }
}

// Export
if (typeof window !== 'undefined') {
    window.TLDrawEditor = TLDrawEditor;
    window.InputManager = InputManager;
}

