/**
 * Complete Canvas System Integration
 * Main editor class that orchestrates all canvas functionality
 */

// =====================================================
// MAIN CANVAS EDITOR
// =====================================================

class CanvasEditor extends EventTarget {
    constructor(container, options = {}) {
        super();

        // Core setup
        this.container = container;
        this.options = { 
            persistenceKey: 'tldraw-canvas',
            autoSave: true,
            ...options 
        };

        // Initialize systems
        this.initializeCanvas();
        this.initializeSystems();
        this.setupEventListeners();
        this.setupPersistence();

        // Start the editor
        this.start();
    }

    // =====================================================
    // INITIALIZATION
    // =====================================================

    initializeCanvas() {
        // Create canvas element
        this.canvas = document.createElement('canvas');
        this.canvas.className = 'tldraw-canvas';
        this.canvas.style.width = '100%';
        this.canvas.style.height = '100%';
        this.canvas.style.display = 'block';
        this.canvas.style.touchAction = 'none';

        // Set size
        const rect = this.container.getBoundingClientRect();
        this.canvas.width = rect.width;
        this.canvas.height = rect.height;

        // Add to container
        this.container.appendChild(this.canvas);
    }

    initializeSystems() {
        // Core systems
        this.store = new ReactiveStore();
        this.camera = new CameraSystem(this.store, this.canvas);
        this.drawingEngine = new DrawingEngine(this.canvas, this.store, this.camera);
        this.toolManager = new SimpleToolManager(this);

        // UI state
        this.currentColor = '#000000';
        this.currentFill = 'none';
        this.currentStroke = '#000000';
        this.currentStrokeWidth = 2;
        this.currentSize = 'medium';
        this.currentOpacity = 1;

        // Interaction state
        this.brush = null;
        this.contextMenu = null;
        this.isEditingText = false;

        // Performance tracking
        this.renderStats = {
            frameCount: 0,
            lastFrameTime: 0,
            averageFPS: 60
        };
    }

    setupEventListeners() {
        // Only setup canvas event handlers if UniversalInputSystem is not active
        // This prevents event handler conflicts
        if (!window.UniversalInputSystem || !window.tlEditor?.universalInput) {
            console.log('CanvasSystem: Setting up event handlers (UniversalInputSystem not active)');

            // Canvas events - comprehensive interaction support
            this.canvas.addEventListener('pointerdown', this.handlePointerDown.bind(this));
            this.canvas.addEventListener('pointermove', this.handlePointerMove.bind(this));
            this.canvas.addEventListener('pointerup', this.handlePointerUp.bind(this));
            this.canvas.addEventListener('pointercancel', this.handlePointerUp.bind(this));
            this.canvas.addEventListener('dblclick', this.handleDoubleClick.bind(this));
            this.canvas.addEventListener('contextmenu', this.handleContextMenu.bind(this));
            this.canvas.addEventListener('wheel', this.handleWheel.bind(this), { passive: false });

            // Touch events for multi-touch gestures
            this.canvas.addEventListener('touchstart', this.handleTouchStart.bind(this), { passive: false });
            this.canvas.addEventListener('touchmove', this.handleTouchMove.bind(this), { passive: false });
            this.canvas.addEventListener('touchend', this.handleTouchEnd.bind(this), { passive: false });
            this.canvas.addEventListener('touchcancel', this.handleTouchEnd.bind(this), { passive: false });
        } else {
            console.log('CanvasSystem: Skipping canvas event handlers (UniversalInputSystem is active)');
        }

        // Keyboard events
        document.addEventListener('keydown', this.handleKeyDown.bind(this));
        document.addEventListener('keyup', this.handleKeyUp.bind(this));

        // Window events
        window.addEventListener('resize', this.handleResize.bind(this));
        window.addEventListener('beforeunload', this.handleBeforeUnload.bind(this));

        // Store events
        this.store.addEventListener('change', this.handleStoreChange.bind(this));

        // Drawing engine events
        this.drawingEngine.canvas.addEventListener('render', this.handleRender.bind(this));

        // Interaction state
        this.pointers = new Map(); // Track multiple pointers for multi-touch
        this.lastPinchDistance = 0;
        this.isPanning = false;
        this.isResizing = false;
        this.isDragging = false;
    }

    setupPersistence() {
        if (this.options.persistenceKey) {
            this.persistence = new PersistenceManager(this.store, this.options.persistenceKey);

            // Try to load saved state
            this.persistence.load();

            // Listen for save events
            this.store.addEventListener('saved', (event) => {
                this.emit('saved', event.detail);
                this.updateLastSaved();
            });
        }
    }

    // =====================================================
    // LIFECYCLE
    // =====================================================

    start() {
        // Build spatial index
        this.drawingEngine.buildSpatialIndex();

        // Start rendering
        this.drawingEngine.scheduleRender();

        // Initialize tools
        this.setTool(TLToolType.SELECT);

        // Emit ready event
        this.emit('ready');
    }

    destroy() {
        // Clean up event listeners
        this.canvas.removeEventListener('pointerdown', this.handlePointerDown);
        this.canvas.removeEventListener('pointermove', this.handlePointerMove);
        this.canvas.removeEventListener('pointerup', this.handlePointerUp);
        this.canvas.removeEventListener('dblclick', this.handleDoubleClick);
        this.canvas.removeEventListener('contextmenu', this.handleContextMenu);
        this.canvas.removeEventListener('wheel', this.handleWheel);

        document.removeEventListener('keydown', this.handleKeyDown);
        document.removeEventListener('keyup', this.handleKeyUp);
        window.removeEventListener('resize', this.handleResize);
        window.removeEventListener('beforeunload', this.handleBeforeUnload);

        // Remove canvas
        if (this.canvas.parentNode) {
            this.canvas.parentNode.removeChild(this.canvas);
        }

        // Clean up systems
        this.toolManager = null;
        this.drawingEngine = null;
        this.camera = null;
        this.store = null;
    }

    // =====================================================
    // EVENT HANDLERS
    // =====================================================

    handlePointerDown(event) {
        event.preventDefault();
        this.canvas.setPointerCapture(event.pointerId);

        const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
        this.toolManager.handleEvent(eventInfo);
    }

    handlePointerMove(event) {
        const eventInfo = this.createEventInfo(TLEventType.POINTER_MOVE, event);
        this.toolManager.handleEvent(eventInfo);

        // Update hover state and cursor
        this.updateHover(eventInfo.currentPagePoint);
        this.updateCursor(eventInfo.currentPagePoint);
    }

    updateCursor(point) {
        let cursor = 'default';

        if (this.getActiveToolId() === 'select') {
            // Check for resize handles first
            const handle = this.drawingEngine.getHandleAtPoint(point);
            if (handle) {
                cursor = handle.cursor;
            } else {
                // Check if hovering over a shape
                const hitShape = this.drawingEngine.getShapeAtPoint(point);
                if (hitShape) {
                    cursor = 'move';
                }
            }
        } else if (this.getActiveToolId() === 'hand') {
            cursor = 'grab';
        } else if (['rectangle', 'ellipse', 'arrow', 'line'].includes(this.getActiveToolId())) {
            cursor = 'crosshair';
        } else if (this.getActiveToolId() === 'text') {
            cursor = 'text';
        } else if (this.getActiveToolId() === 'draw') {
            cursor = 'crosshair';
        }

        this.canvas.style.cursor = cursor;
    }

    handlePointerUp(event) {
        event.preventDefault();
        this.canvas.releasePointerCapture(event.pointerId);

        const eventInfo = this.createEventInfo(TLEventType.POINTER_UP, event);
        this.toolManager.handleEvent(eventInfo);
    }

    handleDoubleClick(event) {
        event.preventDefault();

        const eventInfo = this.createEventInfo(TLEventType.DOUBLE_CLICK, event);
        this.toolManager.handleEvent(eventInfo);
    }

    handleContextMenu(event) {
        console.log('CanvasEditor: handleContextMenu called', event);
        event.preventDefault();

        // Delegate to SimpleToolManager's context menu
        if (this.toolManager && this.toolManager.showContextMenu) {
            console.log('CanvasEditor: Delegating to SimpleToolManager');
            this.toolManager.showContextMenu(event);
        } else {
            console.log('CanvasEditor: No SimpleToolManager found, using fallback');
            // Fallback to emit event
            const rect = this.canvas.getBoundingClientRect();
            const screenPoint = {
                x: event.clientX - rect.left,
                y: event.clientY - rect.top
            };

            const pagePoint = this.camera.screenToPage(screenPoint);
            const hitShape = this.drawingEngine.getShapeAtPoint(screenPoint);

            this.showContextMenu(screenPoint, hitShape);
        }
    }

    handleWheel(event) {
        event.preventDefault();

        const rect = this.canvas.getBoundingClientRect();
        const screenPoint = {
            x: event.clientX - rect.left,
            y: event.clientY - rect.top
        };

        const camera = this.store.getCurrentCamera();
        if (!camera) return;

        // Handle zoom with wheel (Ctrl+scroll or trackpad pinch)
        if (event.ctrlKey || event.metaKey || Math.abs(event.deltaY) > Math.abs(event.deltaX)) {
            const zoomDelta = event.deltaY > 0 ? -0.1 : 0.1;
            const pagePoint = this.camera.screenToPage(screenPoint);
            camera.zoom(zoomDelta, pagePoint);
            this.store.put(camera);
        } else {
            // Pan with wheel (horizontal scroll or Shift+scroll)
            const panDelta = {
                x: -event.deltaX * 0.5,
                y: -event.deltaY * 0.5
            };
            camera.pan(panDelta);
            this.store.put(camera);
        }
    }

    // Touch event handlers for multi-touch gestures
    handleTouchStart(event) {
        event.preventDefault();

        // Check for multi-finger touch gestures
        if (event.touches.length === 3) {
            console.log('Three-finger touch detected - switching to draw tool');
            this.setTool('draw');
            return;
        }

        // CRITICAL FIX: Check for four-finger touch to activate eraser tool
        if (event.touches.length === 4) {
            console.log('Four-finger touch detected - switching to eraser tool');
            this.setTool('eraser');
            return;
        }

        // Update pointer tracking
        for (const touch of event.changedTouches) {
            this.pointers.set(touch.identifier, {
                x: touch.clientX,
                y: touch.clientY,
                pageX: touch.pageX,
                pageY: touch.pageY
            });
        }

        // Handle multi-touch gestures
        if (event.touches.length === 2) {
            const touch1 = event.touches[0];
            const touch2 = event.touches[1];
            this.lastPinchDistance = this.getTouchDistance(touch1, touch2);
        }
    }

    handleTouchMove(event) {
        event.preventDefault();

        if (event.touches.length === 1) {
            // Single touch - pan the canvas
            const touch = event.touches[0];
            const lastPointer = this.pointers.get(touch.identifier);

            if (lastPointer) {
                const deltaX = touch.clientX - lastPointer.x;
                const deltaY = touch.clientY - lastPointer.y;

                const camera = this.store.getCurrentCamera();
                if (camera) {
                    camera.pan({ x: deltaX, y: deltaY });
                    this.store.put(camera);
                }

                // Update pointer position
                this.pointers.set(touch.identifier, {
                    x: touch.clientX,
                    y: touch.clientY,
                    pageX: touch.pageX,
                    pageY: touch.pageY
                });
            }
        } else if (event.touches.length === 2) {
            // Two fingers - pinch to zoom
            const touch1 = event.touches[0];
            const touch2 = event.touches[1];
            const currentDistance = this.getTouchDistance(touch1, touch2);

            if (this.lastPinchDistance > 0) {
                const zoomDelta = (currentDistance - this.lastPinchDistance) * 0.01;
                const centerX = (touch1.clientX + touch2.clientX) / 2;
                const centerY = (touch1.clientY + touch2.clientY) / 2;

                const rect = this.canvas.getBoundingClientRect();
                const screenPoint = {
                    x: centerX - rect.left,
                    y: centerY - rect.top
                };

                const camera = this.store.getCurrentCamera();
                if (camera) {
                    const pagePoint = this.camera.screenToPage(screenPoint);
                    camera.zoom(zoomDelta, pagePoint);
                    this.store.put(camera);
                }
            }

            this.lastPinchDistance = currentDistance;
        }
    }

    handleTouchEnd(event) {
        event.preventDefault();

        // Remove ended touches from tracking
        for (const touch of event.changedTouches) {
            this.pointers.delete(touch.identifier);
        }

        // Reset pinch tracking if no touches remain
        if (event.touches.length < 2) {
            this.lastPinchDistance = 0;
        }
    }

    getTouchDistance(touch1, touch2) {
        const dx = touch2.clientX - touch1.clientX;
        const dy = touch2.clientY - touch1.clientY;
        return Math.sqrt(dx * dx + dy * dy);
    }

    handleKeyDown(event) {
        // Check for tool shortcuts first
        if (this.toolManager.handleKeyboardShortcut(event.key, event.shiftKey)) {
            event.preventDefault();
            return;
        }

        const eventInfo = this.createEventInfo(TLEventType.KEY_DOWN, event);

        // Handle global shortcuts
        if (event.metaKey || event.ctrlKey) {
            switch (event.key) {
                case 'z':
                    if (event.shiftKey) {
                        this.redo();
                    } else {
                        this.undo();
                    }
                    event.preventDefault();
                    break;
                case 'y':
                    this.redo();
                    event.preventDefault();
                    break;
                case 's':
                    this.save();
                    event.preventDefault();
                    break;
                case 'a':
                    this.selectAll();
                    event.preventDefault();
                    break;
                case 'c':
                    this.copy();
                    event.preventDefault();
                    break;
                case 'v':
                    this.paste();
                    event.preventDefault();
                    break;
                case 'x':
                    this.cut();
                    event.preventDefault();
                    break;
                case 'd':
                    this.duplicate();
                    event.preventDefault();
                    break;
            }
        }

        // Pass to current tool
        this.toolManager.handleEvent(eventInfo);
    }

    handleKeyUp(event) {
        const eventInfo = this.createEventInfo(TLEventType.KEY_UP, event);
        this.toolManager.handleEvent(eventInfo);
    }

    handleResize() {
        const rect = this.container.getBoundingClientRect();
        this.canvas.width = rect.width;
        this.canvas.height = rect.height;

        this.drawingEngine.handleResize();
        this.camera.updateViewport();
    }

    handleBeforeUnload(event) {
        if (this.persistence && this.options.autoSave) {
            this.persistence.save();
        }
    }

    handleStoreChange(event) {
        const changeInfo = event.detail;

        // Update UI based on store changes
        if (changeInfo.record?.type === TLRecordType.INSTANCE) {
            this.updateUI();
        }

        // Emit change event
        this.emit('change', changeInfo);
    }

    handleRender(event) {
        const renderInfo = event.detail;
        this.renderStats = {
            ...this.renderStats,
            ...renderInfo
        };

        // Emit render event
        this.emit('render', renderInfo);
    }

    // =====================================================
    // EVENT INFO CREATION
    // =====================================================

    createEventInfo(type, domEvent) {
        const rect = this.canvas.getBoundingClientRect();
        const camera = this.store.getCurrentCamera();

        let currentPagePoint = { x: 0, y: 0 };

        if (domEvent && domEvent.clientX !== undefined) {
            const screenPoint = {
                x: domEvent.clientX - rect.left,
                y: domEvent.clientY - rect.top
            };

            if (camera) {
                currentPagePoint = camera.screenToPage(screenPoint);
            }
        }

        return new TLEventInfo(type, {
            currentPagePoint,
            originPagePoint: currentPagePoint,
            shiftKey: domEvent?.shiftKey || false,
            ctrlKey: domEvent?.ctrlKey || false,
            altKey: domEvent?.altKey || false,
            metaKey: domEvent?.metaKey || false,
            pointerId: domEvent?.pointerId || 0,
            button: domEvent?.button || 0,
            pressure: domEvent?.pressure || 0.5,
            isPen: domEvent?.pointerType === 'pen',
            target: domEvent?.target,
            key: domEvent?.key || '',
            code: domEvent?.code || '',
            delta: domEvent?.deltaX !== undefined ? 
                { x: domEvent.deltaX, y: domEvent.deltaY } : { x: 0, y: 0 }
        });
    }

    // =====================================================
    // PUBLIC API METHODS
    // =====================================================

    // Tool management (TLDraw-style API)
    setTool(toolId, options = {}) {
        console.log('Canvas: Setting tool to:', toolId);
        this.toolManager.setTool(toolId);
        return this;
    }

    getToolCursor(toolId) {
        switch(toolId) {
            case 'select': return 'default';
            case 'hand': return 'grab';
            case 'draw': return 'crosshair';
            case 'text': return 'text';
            case 'eraser': return 'crosshair';
            case 'rectangle':
            case 'ellipse':
            case 'arrow':
            case 'line':
            case 'triangle':
            case 'diamond':
            case 'star':
            case 'hexagon':
                return 'crosshair';
            default: return 'default';
        }
    }

    getCurrentTool() {
        return this.toolManager.currentTool;
    }

    getActiveToolId() {
        return this.toolManager.currentTool || 'select';
    }

    isToolActive(toolId) {
        return this.getActiveToolId() === toolId;
    }

    // Shape operations (TLDraw-style API)
    createShapes(shapesToCreate, options = {}) {
        const shapes = [];
        this.store.transaction(() => {
            shapesToCreate.forEach(shapeData => {
                const shape = new TLShape({
                    id: shapeData.id || generateId(),
                    ...shapeData
                });
                shapes.push(shape);
                this.store.put(shape);
            });
        });
        return shapes;
    }

    updateShapes(shapesToUpdate, options = {}) {
        this.store.transaction(() => {
            shapesToUpdate.forEach(update => {
                const shape = this.store.get(update.id);
                if (shape) {
                    if (update.props) {
                        shape.props = { ...shape.props, ...update.props };
                    }
                    Object.keys(update).forEach(key => {
                        if (key !== 'id' && key !== 'props') {
                            shape[key] = update[key];
                        }
                    });
                    this.store.put(shape);
                }
            });
        });
        return this;
    }

    deleteShapes(shapeIds, options = {}) {
        this.store.transaction(() => {
            shapeIds.forEach(id => this.store.remove(id));
        });
        return this;
    }

    getShape(id) {
        return this.store.get(id);
    }

    getShapes() {
        return this.store.getCurrentPageShapes();
    }

    getShapeAtPoint(point) {
        return this.drawingEngine.getShapeAtPoint(point);
    }

    getRenderingShapes() {
        return this.store.getCurrentPageShapes().filter(shape => !shape.isHidden);
    }

    getShapePageBounds(id) {
        const shape = this.store.get(id);
        return shape ? shape.getBounds() : null;
    }

    // Selection management (TLDraw-style API)
    select(...shapeIds) {
        const instance = this.store.getCurrentInstance();
        if (instance) {
            instance.setSelectedShapes(shapeIds);
            this.store.put(instance);
        }
        return this;
    }

    selectAll(options = {}) {
        const shapes = this.store.getCurrentPageShapes();
        const instance = this.store.getCurrentInstance();
        if (instance) {
            instance.setSelectedShapes(shapes.map(s => s.id));
            this.store.put(instance);
        }
        return this;
    }

    deselect(options = {}) {
        const instance = this.store.getCurrentInstance();
        if (instance) {
            instance.clearSelection();
            this.store.put(instance);
        }
        return this;
    }

    selectNone() {
        return this.deselect();
    }

    setSelectedShapes(shapeIds) {
        return this.select(...shapeIds);
    }

    getSelectedShapes() {
        return this.store.getSelectedShapes();
    }

    getSelectedShapeIds() {
        const instance = this.store.getCurrentInstance();
        return instance ? instance.selectedShapeIds : [];
    }

    getOnlySelectedShape() {
        const selected = this.getSelectedShapes();
        return selected.length === 1 ? selected[0] : null;
    }

    isShapeSelected(id) {
        return this.getSelectedShapeIds().includes(id);
    }

    hasSelection() {
        return this.getSelectedShapeIds().length > 0;
    }

    addToSelection(ids, options = {}) {
        const currentIds = this.getSelectedShapeIds();
        const newIds = Array.isArray(ids) ? ids : [ids];
        const combinedIds = [...new Set([...currentIds, ...newIds])];
        return this.select(...combinedIds);
    }

    removeFromSelection(ids, options = {}) {
        const currentIds = this.getSelectedShapeIds();
        const toRemove = Array.isArray(ids) ? ids : [ids];
        const filteredIds = currentIds.filter(id => !toRemove.includes(id));
        return this.select(...filteredIds);
    }

    // Clipboard operations
    copy() {
        const selectedShapes = this.getSelectedShapes();
        if (selectedShapes.length > 0) {
            const clipboard = selectedShapes.map(shape => shape.toJSON());
            localStorage.setItem('tldraw-clipboard', JSON.stringify(clipboard));
            this.emit('copied', { shapes: selectedShapes });
        }
    }

    cut() {
        const selectedShapes = this.getSelectedShapes();
        if (selectedShapes.length > 0) {
            this.copy();
            this.deleteShapes(selectedShapes.map(s => s.id));
            this.emit('cut', { shapes: selectedShapes });
        }
    }

    paste() {
        try {
            const clipboardData = localStorage.getItem('tldraw-clipboard');
            if (clipboardData) {
                const shapes = JSON.parse(clipboardData);
                const newShapes = [];

                this.store.transaction(() => {
                    shapes.forEach(shapeData => {
                        const newShape = new TLShape({
                            ...shapeData,
                            id: generateId(),
                            x: shapeData.x + 20,
                            y: shapeData.y + 20
                        });
                        newShapes.push(newShape);
                        this.store.put(newShape);
                    });
                });

                this.selectShapes(newShapes.map(s => s.id));
                this.emit('pasted', { shapes: newShapes });
            }
        } catch (error) {
            console.error('Paste failed:', error);
        }
    }

    duplicate() {
        const selectedShapes = this.getSelectedShapes();
        if (selectedShapes.length > 0) {
            const newShapes = [];

            this.store.transaction(() => {
                selectedShapes.forEach(shape => {
                    const newShape = new TLShape({
                        ...shape.toJSON(),
                        id: generateId(),
                        x: shape.x + 20,
                        y: shape.y + 20
                    });
                    newShapes.push(newShape);
                    this.store.put(newShape);
                });
            });

            this.select(...newShapes.map(s => s.id));
            this.emit('duplicated', { shapes: newShapes });
        }
    }

    // Shape manipulation methods (TLDraw-style API)
    moveShapes(shapesToMove, delta, options = {}) {
        const shapeIds = Array.isArray(shapesToMove) ? shapesToMove : [shapesToMove];
        this.store.transaction(() => {
            shapeIds.forEach(id => {
                const shape = this.store.get(id);
                if (shape) {
                    shape.x += delta.x;
                    shape.y += delta.y;
                    this.store.put(shape);
                }
            });
        });
        return this;
    }

    resizeShapes(shapesToResize, scaleX, scaleY, options = {}) {
        const shapeIds = Array.isArray(shapesToResize) ? shapesToResize : [shapesToResize];
        this.store.transaction(() => {
            shapeIds.forEach(id => {
                const shape = this.store.get(id);
                if (shape && shape.props) {
                    if (shape.props.w !== undefined) shape.props.w *= scaleX;
                    if (shape.props.h !== undefined) shape.props.h *= scaleY;
                    this.store.put(shape);
                }
            });
        });
        return this;
    }

    rotateShapes(shapesToRotate, rotationDelta, options = {}) {
        const shapeIds = Array.isArray(shapesToRotate) ? shapesToRotate : [shapesToRotate];
        this.store.transaction(() => {
            shapeIds.forEach(id => {
                const shape = this.store.get(id);
                if (shape) {
                    shape.rotation = (shape.rotation || 0) + rotationDelta;
                    this.store.put(shape);
                }
            });
        });
        return this;
    }

    duplicateShapes(ids, options = {}) {
        const shapesToDuplicate = ids.map(id => this.store.get(id)).filter(Boolean);
        return this.createShapes(shapesToDuplicate.map(shape => ({
            ...shape.toJSON(),
            id: generateId(),
            x: shape.x + 20,
            y: shape.y + 20
        })));
    }

    // History operations
    undo() {
        if (this.store.history.undo()) {
            this.emit('undo');
        }
    }

    redo() {
        if (this.store.history.redo()) {
            this.emit('redo');
        }
    }

    canUndo() {
        return this.store.history.canUndo();
    }

    canRedo() {
        return this.store.history.canRedo();
    }

    // Camera operations
    zoomIn() {
        this.camera.zoomIn();
    }

    zoomOut() {
        this.camera.zoomOut();
    }

    zoomToFit() {
        this.camera.zoomToFit();
    }

    zoomToSelection() {
        this.camera.zoomToSelection();
    }

    resetZoom() {
        this.camera.resetZoom();
    }

    getZoomLevel() {
        return this.camera.getZoomLevel();
    }

    // Style management
    getCurrentColor() {
        return this.currentColor;
    }

    setCurrentColor(color) {
        this.currentColor = color;
        this.emit('style-changed', { color });
    }

    getCurrentFill() {
        return this.currentFill;
    }

    setCurrentFill(fill) {
        this.currentFill = fill;
        this.emit('style-changed', { fill });
    }

    getCurrentStroke() {
        return this.currentStroke;
    }

    setCurrentStroke(stroke) {
        this.currentStroke = stroke;
        this.emit('style-changed', { stroke });
    }

    getCurrentStrokeWidth() {
        return this.currentStrokeWidth;
    }

    setCurrentStrokeWidth(strokeWidth) {
        this.currentStrokeWidth = strokeWidth;
        this.emit('style-changed', { strokeWidth });
    }

    getCurrentSize() {
        return this.currentSize;
    }

    setCurrentSize(size) {
        this.currentSize = size;
        this.emit('style-changed', { size });
    }

    getCurrentOpacity() {
        return this.currentOpacity;
    }

    setCurrentOpacity(opacity) {
        this.currentOpacity = opacity;
        this.emit('style-changed', { opacity });
    }

    // UI helpers
    setBrush(brush) {
        this.brush = brush;
        const instance = this.store.getCurrentInstance();
        if (instance) {
            instance.brush = brush;
            this.store.put(instance);
        }
    }

    updateHover(point) {
        const hitShape = this.drawingEngine.getShapeAtPoint(point);
        const instance = this.store.getCurrentInstance();

        if (instance) {
            const currentHover = instance.hoveredShapeId;
            const newHover = hitShape?.id || null;

            if (currentHover !== newHover) {
                instance.setHoveredShape(newHover);
                this.store.put(instance);
            }
        }
    }

    showContextMenu(screenPoint, shape = null) {
        this.emit('context-menu', { 
            point: screenPoint, 
            shape,
            selectedShapes: this.getSelectedShapes()
        });
    }

    hideContextMenu() {
        this.contextMenu = null;
        this.emit('context-menu-hide');
    }

    // Persistence
    save() {
        if (this.persistence) {
            this.persistence.save();
        }
    }

    load(data) {
        if (typeof data === 'string') {
            // Load from JSON string
            this.store.loadSnapshot(JSON.parse(data));
        } else if (data && data.records) {
            // Load from snapshot object
            this.store.loadSnapshot(data);
        }
    }

    export(format = 'json') {
        switch (format) {
            case 'json':
                return JSON.stringify(this.store.getSnapshot(), null, 2);
            case 'png':
                return this.drawingEngine.exportToDataURL('image/png');
            case 'svg':
                return this.drawingEngine.exportToSVG();
            default:
                throw new Error(`Unsupported export format: ${format}`);
        }
    }

    // Utilities
    getStats() {
        return {
            store: this.store.getStats(),
            render: this.renderStats,
            performance: this.drawingEngine.getPerformanceStats()
        };
    }

    updateUI() {
        const instance = this.store.getCurrentInstance();
        if (!instance) return;

        // Update toolbar state
        document.querySelectorAll('.toolbar-btn').forEach(btn => {
            btn.classList.toggle('active', 
                btn.dataset.tool === instance.currentTool ||
                btn.dataset.shape === instance.currentTool
            );
        });

        // Update selection info
        const selectionInfo = document.getElementById('selection-info');
        if (selectionInfo) {
            const count = instance.selectedShapeIds.length;
            selectionInfo.textContent = count > 0 ? 
                `${count} shape${count > 1 ? 's' : ''} selected` :
                'No selection';
        }

        // Update zoom level
        const zoomLevel = document.getElementById('zoom-level');
        if (zoomLevel) {
            zoomLevel.textContent = `${this.camera.getZoomPercentage()}%`;
        }
    }

    updateLastSaved() {
        const lastSaved = document.getElementById('last-saved');
        if (lastSaved) {
            lastSaved.textContent = new Date().toLocaleTimeString();
        }
    }

    // Event emitter helpers
    emit(eventName, data = {}) {
        this.dispatchEvent(new CustomEvent(eventName, { detail: data }));
    }

    on(eventName, handler) {
        this.addEventListener(eventName, handler);
    }

    off(eventName, handler) {
        this.removeEventListener(eventName, handler);
    }

    // Canvas management methods
    clearCanvas() {
        const allShapes = this.getShapes();
        if (allShapes.length > 0) {
            if (confirm('Are you sure you want to clear the entire canvas? This action cannot be undone.')) {
                this.deleteShapes(allShapes.map(s => s.id));
                this.emit('canvas-cleared');
                console.log('Canvas cleared');
            }
        }
    }

    exportCanvas() {
        try {
            const canvas = this.canvas;
            const link = document.createElement('a');
            link.download = 'canvas-export.png';
            link.href = canvas.toDataURL();
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            this.emit('canvas-exported');
            console.log('Canvas exported');
        } catch (error) {
            console.error('Failed to export canvas:', error);
            alert('Failed to export canvas. Please try again.');
        }
    }
}

// =====================================================
// GLOBAL INITIALIZATION
// =====================================================

// Auto-initialize if canvas container exists
document.addEventListener('DOMContentLoaded', () => {
    const canvasContainer = document.getElementById('canvas-container');
    if (canvasContainer && !window.canvasEditor) {
        window.canvasEditor = new CanvasEditor(canvasContainer);

        // Setup toolbar event listeners
        setupCanvasUI(window.canvasEditor);
    }
});

// =====================================================
// UI INTEGRATION HELPERS
// =====================================================

function setupCanvasUI(editor) {
    console.log('Setting up canvas UI');

    // Tool buttons
    const toolButtons = document.querySelectorAll('.toolbar-btn[data-tool]');
    console.log('Found tool buttons:', toolButtons.length);

    toolButtons.forEach(btn => {
        console.log('Setting up button for tool:', btn.dataset.tool);
        btn.addEventListener('click', () => {
            const tool = btn.dataset.tool;
            console.log('Tool button clicked:', tool);
            editor.setTool(tool);

            // Update UI to show active tool
            updateActiveToolUI(tool);
        });
    });

    // Function to update active tool UI
    function updateActiveToolUI(activeTool) {
        document.querySelectorAll('.toolbar-btn').forEach(btn => {
            btn.classList.remove('active');
            if (btn.dataset.tool === activeTool) {
                btn.classList.add('active');
            }
        });
    }

    // Listen for tool changes to update UI
    editor.on('tool-changed', (event) => {
        updateActiveToolUI(event.detail.tool);
    });

    // Initialize with select tool active
    const selectBtn = document.querySelector('.toolbar-btn[data-tool="select"]');
    if (selectBtn) {
        selectBtn.classList.add('active');
    }

    // Zoom controls
    const zoomIn = document.getElementById('zoom-in');
    const zoomOut = document.getElementById('zoom-out');

    if (zoomIn) zoomIn.addEventListener('click', () => editor.zoomIn());
    if (zoomOut) zoomOut.addEventListener('click', () => editor.zoomOut());

    // Save button
    const saveBtn = document.getElementById('save-btn');
    if (saveBtn) {
        saveBtn.addEventListener('click', () => editor.save());
    }

    // Export button
    const exportBtn = document.getElementById('export-btn');
    if (exportBtn) {
        exportBtn.addEventListener('click', () => {
            const dataURL = editor.export('png');
            const a = document.createElement('a');
            a.href = dataURL;
            a.download = 'canvas-export.png';
            a.click();
        });
    }

    // Color swatches
    document.querySelectorAll('.color-swatch').forEach(swatch => {
        swatch.addEventListener('click', () => {
            const color = swatch.dataset.color;
            editor.setCurrentColor(color);

            // Update active state
            document.querySelectorAll('.color-swatch').forEach(s => s.classList.remove('active'));
            swatch.classList.add('active');
        });
    });

    // Size buttons
    document.querySelectorAll('.size-btn').forEach(btn => {
        btn.addEventListener('click', () => {
            const size = btn.dataset.size;
            editor.setCurrentSize(size);

            // Update active state
            document.querySelectorAll('.size-btn').forEach(b => b.classList.remove('active'));
            btn.classList.add('active');
        });
    });

    // Stroke weight slider
    const strokeSlider = document.getElementById('stroke-weight');
    if (strokeSlider) {
        strokeSlider.addEventListener('input', () => {
            editor.setCurrentStrokeWidth(parseInt(strokeSlider.value));

            // Update preview
            const preview = document.querySelector('.slider-preview');
            if (preview) {
                preview.style.height = `${strokeSlider.value}px`;
            }
        });
    }

    // Listen for editor events
    editor.on('tool-changed', (event) => {
        console.log('Tool changed to:', event.detail.tool);
    });

    editor.on('saved', () => {
        console.log('Canvas saved');
    });

    editor.on('context-menu', (event) => {
        showContextMenu(event.detail);
    });

    editor.on('ready', () => {
        console.log('Canvas editor ready');

        // Add some debug methods to window for testing
        window.debugCanvas = () => {
            console.log('Current shapes:', editor.getShapes());
            console.log('Selected shapes:', editor.getSelectedShapes());
            console.log('Current tool:', editor.getActiveToolId());
            console.log('Camera:', editor.camera);
        };

        // Create test shapes for demonstration
        setTimeout(() => {
            console.log('Creating test shapes...');
            const testShapes = [
                {
                    type: TLShapeType.RECTANGLE,
                    x: 100,
                    y: 100,
                    props: {
                        w: 150,
                        h: 100,
                        color: '#ff0000',
                        fill: 'solid'
                    }
                },
                {
                    type: TLShapeType.ELLIPSE,
                    x: 300,
                    y: 150,
                    props: {
                        w: 120,
                        h: 80,
                        color: '#00ff00',
                        fill: 'solid'
                    }
                },
                {
                    type: TLShapeType.TEXT,
                    x: 150,
                    y: 250,
                    props: {
                        text: 'Test Text',
                        color: '#0000ff',
                        fontSize: 24,
                        w: 200,
                        h: 50
                    }
                }
            ];

            editor.createShapes(testShapes);
            console.log('Test shapes created:', testShapes.length);
        }, 1000);
    });
}

function showContextMenu(data) {
    // Implementation for context menu display
    console.log('Show context menu:', data);
}

// =====================================================
// EXPORTS
// =====================================================

if (typeof window !== 'undefined') {
    window.CanvasEditor = CanvasEditor;
    window.setupCanvasUI = setupCanvasUI;
} else if (typeof module !== 'undefined' && module.exports) {
    module.exports = {
        CanvasEditor,
        setupCanvasUI
    };
}

