                                window.SNAPEHumanMock.logInteraction('click', {x, y, button, target: target.tagName});
                                resolve(true);
                            }, Math.random() * 100 + 50); // Human-like delay
                        });
                    });
                },

                // Drag operation
                drag: function(startX, startY, endX, endY, duration = 500) {
                    return window.SNAPEHumanMock.movement.moveToPosition(startX, startY, 200).then(() => {
                        const startTarget = document.elementFromPoint(startX, startY);
                        if (!startTarget) return false;

                        // Start drag
                        const mouseDown = new MouseEvent('mousedown', {
                            bubbles: true,
                            cancelable: true,
                            clientX: startX,
                            clientY: startY,
                            button: 0
                        });
                        startTarget.dispatchEvent(mouseDown);

                        // Drag movement
                        return window.SNAPEHumanMock.movement.moveToPosition(endX, endY, duration).then(() => {
                            const endTarget = document.elementFromPoint(endX, endY);

                            const mouseUp = new MouseEvent('mouseup', {
                                bubbles: true,
                                cancelable: true,
                                clientX: endX,
                                clientY: endY,
                                button: 0
                            });
                            (endTarget || startTarget).dispatchEvent(mouseUp);

                            window.SNAPEHumanMock.logInteraction('drag', {
                                start: {x: startX, y: startY},
                                end: {x: endX, y: endY},
                                duration
                            });

                            return true;
                        });
                    });
                }
            },

