                    success = true;
                } else if (window.canvasEditor && window.canvasEditor.toolManager) {
                    window.canvasEditor.toolManager.setTool(toolId);
                    success = true;
                }

                // Update toolbar UI
                this.domCache.toolButtons.forEach(btn => {
                    btn.classList.remove('active');
                    if (btn.dataset.tool === toolId) {
                        btn.classList.add('active');
                    }
                });

                this.logInteraction('tool_change', {toolId, success});
                return success;
            },

            getCurrentTool: function() {
                if (window.tlEditor && window.tlEditor.getCurrentTool) {
                    return window.tlEditor.getCurrentTool();
                } else if (window.canvasEditor && window.canvasEditor.getCurrentTool) {
                    return window.canvasEditor.getCurrentTool();
                } else if (window.canvasEditor && window.canvasEditor.toolManager) {
                    return window.canvasEditor.toolManager.currentTool;
                }
                return this.currentTool;
            },

            // Event listener setup
            setupEventListeners: function() {
                // Track mouse position
                document.addEventListener('mousemove', (e) => {
                    this.mousePosition = {x: e.clientX, y: e.clientY};
                });

                // Track clicks
