                };
                this.interactionHistory.push(log);
                console.log('ğŸ“ SNAPE Interaction:', type, data);
                return log;
            },

            // Get interaction report
            getInteractionReport: function() {
                return {
                    totalInteractions: this.interactionHistory.length,
                    interactionTypes: [...new Set(this.interactionHistory.map(i => i.type))],
                    recentInteractions: this.interactionHistory.slice(-10),
                    pageInfo: this.getPageInfo(),
                    domCache: Object.keys(this.domCache).reduce((acc, key) => {
