#!/usr/bin/env python3
"""
SNAPE Visual Interactions System
===============================
Natural Selenium interactions with comprehensive visual highlighting
for all pointer movements, clicks, drags, gestures, and keyboard actions
"""

import time
import json
import random
import math
from selenium import webdriver
from selenium.webdriver.chrome.options import Options
from selenium.webdriver.common.action_chains import ActionChains
from selenium.webdriver.common.by import By
from selenium.webdriver.common.keys import Keys
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from selenium.common.exceptions import TimeoutException, WebDriverException

class SNAPEVisualInteractions:
    def __init__(self):
        self.driver = None
        self.action_chains = None
        self.interaction_log = []
        self.current_tool = 'select'
        self.highlight_id_counter = 0

    def setup_visual_chrome(self):
        """Setup Chrome with visual interaction capabilities"""
        print("ğŸš€ SNAPE: Setting up visual interaction Chrome...")

        chrome_options = Options()
        chrome_options.add_argument("--disable-web-security")
        chrome_options.add_argument("--disable-blink-features=AutomationControlled")
        chrome_options.add_experimental_option("excludeSwitches", ["enable-automation"])
        chrome_options.add_experimental_option('useAutomationExtension', False)
        chrome_options.add_argument("--no-sandbox")
        chrome_options.add_argument("--disable-dev-shm-usage")

        self.driver = webdriver.Chrome(options=chrome_options)
        self.driver.execute_script("Object.defineProperty(navigator, 'webdriver', {get: () => undefined})")
        self.driver.set_window_size(1400, 900)
        self.action_chains = ActionChains(self.driver)

        print("âœ… SNAPE: Visual Chrome setup complete")
        return self.driver

    def inject_visual_system(self):
        """Inject comprehensive visual highlighting system"""
        print("ğŸ¨ SNAPE: Injecting visual highlighting system...")

        visual_system_js = """
        // SNAPE Visual Interaction System
        window.SNAPEVisual = {
            highlights: new Map(),
            trails: [],

            // Create visual cursor
            createCursor: function() {
                const cursor = document.createElement('div');
                cursor.id = 'snape-cursor';
                cursor.style.cssText = `
                    position: fixed;
                    width: 20px;
                    height: 20px;
                    background: radial-gradient(circle, #ff6b6b 0%, #ff8e8e 70%, transparent 100%);
                    border: 2px solid #ff4757;
                    border-radius: 50%;
                    pointer-events: none;
                    z-index: 10000;
                    transform: translate(-50%, -50%);
                    box-shadow: 0 0 10px rgba(255, 71, 87, 0.5);
                    transition: all 0.1s ease;
                `;
                document.body.appendChild(cursor);
                return cursor;
            },

            // Update cursor position
            updateCursor: function(x, y) {
                let cursor = document.getElementById('snape-cursor');
                if (!cursor) cursor = this.createCursor();

                cursor.style.left = x + 'px';
                cursor.style.top = y + 'px';
                cursor.style.opacity = '1';
            },

            // Highlight click
            highlightClick: function(x, y, type = 'left') {
                const highlight = document.createElement('div');
                const id = 'snape-click-' + Date.now();
                highlight.id = id;

                const colors = {
                    'left': '#4ecdc4',
                    'right': '#ff6b6b',
                    'double': '#45b7d1'
                };

                const color = colors[type] || colors['left'];

                highlight.style.cssText = `
                    position: fixed;
                    width: 30px;
                    height: 30px;
                    background: radial-gradient(circle, ${color} 0%, transparent 70%);
                    border: 3px solid ${color};
                    border-radius: 50%;
                    pointer-events: none;
                    z-index: 9999;
                    left: ${x}px;
                    top: ${y}px;
                    transform: translate(-50%, -50%) scale(0);
                    animation: snapeClickPulse 0.6s ease-out forwards;
                `;

                document.body.appendChild(highlight);

                setTimeout(() => {
                    if (document.getElementById(id)) {
                        document.getElementById(id).remove();
                    }
                }, 600);

                console.log('ğŸ‘† SNAPE Click:', type, 'at', x, y);
            },

            // Highlight drag
            highlightDrag: function(startX, startY, endX, endY, tool = 'unknown') {
                const dragLine = document.createElement('div');
                const id = 'snape-drag-' + Date.now();
                dragLine.id = id;

                const length = Math.sqrt(Math.pow(endX - startX, 2) + Math.pow(endY - startY, 2));
                const angle = Math.atan2(endY - startY, endX - startX) * 180 / Math.PI;

                const toolColors = {
                    'draw': '#9b59b6',
                    'rectangle': '#e74c3c',
                    'ellipse': '#f39c12',
                    'select': '#3498db',
                    'unknown': '#95a5a6'
                };

                const color = toolColors[tool] || toolColors['unknown'];

                dragLine.style.cssText = `
                    position: fixed;
                    width: ${length}px;
                    height: 4px;
                    background: linear-gradient(90deg, ${color} 0%, ${color}88 100%);
                    left: ${startX}px;
                    top: ${startY}px;
                    transform-origin: 0 50%;
                    transform: rotate(${angle}deg);
                    pointer-events: none;
                    z-index: 9998;
                    border-radius: 2px;
                    box-shadow: 0 0 8px ${color}44;
                    animation: snapeDragFade 3s ease-out forwards;
                `;

                document.body.appendChild(dragLine);

                // Add start and end markers
                this.addDragMarker(startX, startY, 'start', color);
                this.addDragMarker(endX, endY, 'end', color);

                setTimeout(() => {
                    if (document.getElementById(id)) {
                        document.getElementById(id).remove();
                    }
                }, 3000);

                console.log('ğŸ“ SNAPE Drag:', tool, 'from', startX, startY, 'to', endX, endY);
            },

            // Add drag markers
            addDragMarker: function(x, y, type, color) {
                const marker = document.createElement('div');
                marker.style.cssText = `
                    position: fixed;
                    width: 12px;
                    height: 12px;
                    background: ${color};
                    border: 2px solid white;
                    border-radius: 50%;
                    left: ${x}px;
                    top: ${y}px;
                    transform: translate(-50%, -50%);
                    pointer-events: none;
                    z-index: 9999;
                    box-shadow: 0 0 4px rgba(0,0,0,0.3);
                    animation: ${type === 'start' ? 'snapeMarkerStart' : 'snapeMarkerEnd'} 3s ease-out forwards;
                `;

                document.body.appendChild(marker);

                setTimeout(() => marker.remove(), 3000);
            },

            // Highlight three-finger gesture
            highlightThreeFinger: function(centerX, centerY) {
                const fingers = [
                    {x: centerX - 25, y: centerY - 15},
                    {x: centerX + 25, y: centerY - 15},
                    {x: centerX, y: centerY + 20}
                ];

                fingers.forEach((finger, i) => {
                    const highlight = document.createElement('div');
                    highlight.style.cssText = `
                        position: fixed;
                        width: 24px;
                        height: 24px;
                        background: radial-gradient(circle, #ff9ff3 0%, #f368e0 70%, transparent 100%);
                        border: 2px solid #e91e63;
                        border-radius: 50%;
                        left: ${finger.x}px;
                        top: ${finger.y}px;
                        transform: translate(-50%, -50%) scale(0);
                        pointer-events: none;
                        z-index: 9999;
                        animation: snapeThreeFingerPulse 1.5s ease-out forwards;
                        animation-delay: ${i * 0.1}s;
                    `;

                    document.body.appendChild(highlight);

                    setTimeout(() => highlight.remove(), 2000);
                });

                // Add connecting lines
                const container = document.createElement('div');
                container.style.cssText = `
                    position: fixed;
                    left: ${centerX}px;
                    top: ${centerY}px;
                    transform: translate(-50%, -50%);
                    pointer-events: none;
                    z-index: 9998;
                    animation: snapeThreeFingerLines 1.5s ease-out forwards;
                `;

                fingers.forEach((finger, i) => {
                    const line = document.createElement('div');
                    const length = Math.sqrt(Math.pow(finger.x - centerX, 2) + Math.pow(finger.y - centerY, 2));
                    const angle = Math.atan2(finger.y - centerY, finger.x - centerX) * 180 / Math.PI;

                    line.style.cssText = `
                        position: absolute;
                        width: ${length}px;
                        height: 2px;
                        background: linear-gradient(90deg, #e91e63aa 0%, transparent 100%);
                        transform-origin: 0 50%;
                        transform: rotate(${angle}deg);
                    `;

                    container.appendChild(line);
                });

                document.body.appendChild(container);
                setTimeout(() => container.remove(), 2000);

                console.log('ğŸ–ï¸ SNAPE Three-finger gesture at', centerX, centerY);
            },

            // Highlight keyboard action
            highlightKeyboard: function(key, description) {
                const keyDisplay = document.createElement('div');
                keyDisplay.style.cssText = `
                    position: fixed;
                    top: 50px;
                    right: 50px;
                    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                    color: white;
                    padding: 12px 20px;
                    border-radius: 8px;
                    font-family: 'Monaco', 'Menlo', monospace;
                    font-size: 16px;
                    font-weight: bold;
                    pointer-events: none;
                    z-index: 10001;
                    box-shadow: 0 4px 20px rgba(0,0,0,0.3);
                    transform: translateX(300px);
                    animation: snapeKeyboardSlide 2s ease-out forwards;
                `;

                keyDisplay.innerHTML = `
                    <div style="font-size: 12px; opacity: 0.8;">SNAPE Keyboard</div>
                    <div style="font-size: 18px;">${key.toUpperCase()}</div>
                    <div style="font-size: 10px; opacity: 0.6;">${description}</div>
                `;

                document.body.appendChild(keyDisplay);

                setTimeout(() => keyDisplay.remove(), 2000);

                console.log('âŒ¨ï¸ SNAPE Keyboard:', key, '-', description);
            },

            // Highlight scroll
            highlightScroll: function(direction, amount) {
                const scrollIndicator = document.createElement('div');
                scrollIndicator.style.cssText = `
                    position: fixed;
                    left: 50%;
                    top: 50%;
                    transform: translate(-50%, -50%);
                    background: rgba(52, 152, 219, 0.9);
                    color: white;
                    padding: 15px 25px;
                    border-radius: 50px;
                    font-family: Arial, sans-serif;
                    font-size: 14px;
                    font-weight: bold;
                    pointer-events: none;
                    z-index: 10001;
                    box-shadow: 0 4px 20px rgba(52, 152, 219, 0.4);
                    animation: snapeScrollPulse 1s ease-out forwards;
                `;

                const arrow = direction === 'down' ? 'â¬‡ï¸' : 'â¬†ï¸';
                scrollIndicator.innerHTML = `${arrow} SCROLL ${direction.toUpperCase()} ${amount}px`;

                document.body.appendChild(scrollIndicator);

                setTimeout(() => scrollIndicator.remove(), 1000);

                console.log('ğŸ“œ SNAPE Scroll:', direction, amount + 'px');
            },

            // Add trail effect
            addTrail: function(x, y) {
                const trail = document.createElement('div');
                trail.style.cssText = `
                    position: fixed;
                    width: 8px;
                    height: 8px;
                    background: radial-gradient(circle, #ff6b6b88 0%, transparent 100%);
                    border-radius: 50%;
                    left: ${x}px;
                    top: ${y}px;
                    transform: translate(-50%, -50%);
                    pointer-events: none;
                    z-index: 9997;
                    animation: snapeTrailFade 1s ease-out forwards;
                `;

                document.body.appendChild(trail);

                setTimeout(() => trail.remove(), 1000);
            },

            // Show tool change
            showToolChange: function(toolName) {
                const toolDisplay = document.createElement('div');
                toolDisplay.style.cssText = `
                    position: fixed;
                    top: 50px;
                    left: 50px;
                    background: linear-gradient(135deg, #11998e 0%, #38ef7d 100%);
                    color: white;
                    padding: 12px 20px;
                    border-radius: 8px;
                    font-family: Arial, sans-serif;
                    font-size: 16px;
                    font-weight: bold;
                    pointer-events: none;
                    z-index: 10001;
                    box-shadow: 0 4px 20px rgba(17, 153, 142, 0.4);
                    transform: translateX(-300px);
                    animation: snapeToolSlide 2s ease-out forwards;
                `;

                toolDisplay.innerHTML = `
                    <div style="font-size: 12px; opacity: 0.8;">SNAPE Tool</div>
                    <div style="font-size: 18px;">${toolName.toUpperCase()}</div>
                `;

                document.body.appendChild(toolDisplay);

                setTimeout(() => toolDisplay.remove(), 2000);

                console.log('ğŸ”§ SNAPE Tool changed to:', toolName);
            }
        };

        // CSS Animations
        const style = document.createElement('style');
        style.textContent = `
            @keyframes snapeClickPulse {
                0% { transform: translate(-50%, -50%) scale(0); opacity: 1; }
                50% { transform: translate(-50%, -50%) scale(1.2); opacity: 0.8; }
                100% { transform: translate(-50%, -50%) scale(1.5); opacity: 0; }
            }

            @keyframes snapeDragFade {
                0% { opacity: 1; transform: rotate(var(--angle)) scaleX(0); }
                20% { opacity: 1; transform: rotate(var(--angle)) scaleX(1); }
                100% { opacity: 0; transform: rotate(var(--angle)) scaleX(1); }
            }

            @keyframes snapeMarkerStart {
                0% { transform: translate(-50%, -50%) scale(0); opacity: 1; }
                20% { transform: translate(-50%, -50%) scale(1.3); opacity: 1; }
                100% { transform: translate(-50%, -50%) scale(1); opacity: 0; }
            }

            @keyframes snapeMarkerEnd {
                0% { transform: translate(-50%, -50%) scale(0); opacity: 0; }
                20% { transform: translate(-50%, -50%) scale(0); opacity: 0; }
                40% { transform: translate(-50%, -50%) scale(1.3); opacity: 1; }
                100% { transform: translate(-50%, -50%) scale(1); opacity: 0; }
            }

            @keyframes snapeThreeFingerPulse {
                0% { transform: translate(-50%, -50%) scale(0); opacity: 1; }
                50% { transform: translate(-50%, -50%) scale(1.3); opacity: 0.9; }
                100% { transform: translate(-50%, -50%) scale(1); opacity: 0.3; }
            }

            @keyframes snapeThreeFingerLines {
                0% { opacity: 0; transform: translate(-50%, -50%) scale(0); }
                50% { opacity: 1; transform: translate(-50%, -50%) scale(1.1); }
                100% { opacity: 0; transform: translate(-50%, -50%) scale(1); }
            }

            @keyframes snapeKeyboardSlide {
                0% { transform: translateX(300px); opacity: 0; }
                20% { transform: translateX(0); opacity: 1; }
                80% { transform: translateX(0); opacity: 1; }
                100% { transform: translateX(300px); opacity: 0; }
            }

            @keyframes snapeToolSlide {
                0% { transform: translateX(-300px); opacity: 0; }
                20% { transform: translateX(0); opacity: 1; }
                80% { transform: translateX(0); opacity: 1; }
                100% { transform: translateX(-300px); opacity: 0; }
            }

            @keyframes snapeScrollPulse {
                0% { transform: translate(-50%, -50%) scale(0); opacity: 0; }
                20% { transform: translate(-50%, -50%) scale(1.1); opacity: 1; }
                80% { transform: translate(-50%, -50%) scale(1); opacity: 1; }
                100% { transform: translate(-50%, -50%) scale(0.8); opacity: 0; }
            }

            @keyframes snapeTrailFade {
                0% { opacity: 0.6; transform: translate(-50%, -50%) scale(1); }
                100% { opacity: 0; transform: translate(-50%, -50%) scale(0.3); }
            }
        `;
        document.head.appendChild(style);

        console.log('ğŸ¨ SNAPE Visual System Ready');
        """

        self.driver.execute_script(visual_system_js)
        print("âœ… SNAPE: Visual highlighting system injected")

    def load_canvas_with_visuals(self):
        """Load canvas and inject visual system"""
        print("ğŸŒ SNAPE: Loading canvas with visual enhancements...")

        self.driver.get("file:///Users/dezmondhollins/my-web-app/public/canvas.html")

        # Wait for canvas and basic elements
        try:
            WebDriverWait(self.driver, 10).until(
                EC.presence_of_element_located((By.TAG_NAME, "canvas"))
            )

            # Wait for toolbar
            WebDriverWait(self.driver, 5).until(
                EC.presence_of_element_located((By.CSS_SELECTOR, ".toolbar-btn"))
            )

            # Inject visual system
            self.inject_visual_system()

            print("âœ… SNAPE: Canvas loaded with visual enhancements")
            time.sleep(1)
            return True

        except TimeoutException:
            print("âŒ SNAPE: Canvas failed to load")
            return False

    def visual_mouse_move(self, target_x, target_y, duration=1.0, show_trail=True):
        """Visual mouse movement with highlighting and trails"""
        print(f"ğŸ–±ï¸ SNAPE: Visual move to ({target_x}, {target_y})")

        try:
            # Calculate smooth path
            steps = max(20, int(duration * 40))

            for i in range(steps + 1):
                progress = i / steps
                # Ease-in-out curve
                ease_progress = 3 * progress**2 - 2 * progress**3

                # Add slight randomness for human-like movement
                noise_x = (random.random() - 0.5) * 3
                noise_y = (random.random() - 0.5) * 3

                # Calculate current position
                current_x = target_x * ease_progress + noise_x
                current_y = target_y * ease_progress + noise_y

                # Update visual cursor
                self.driver.execute_script(f"window.SNAPEVisual.updateCursor({current_x}, {current_y});")

                # Add trail effect
                if show_trail and i % 3 == 0:  # Every 3rd step
                    self.driver.execute_script(f"window.SNAPEVisual.addTrail({current_x}, {current_y});")

                # Move actual mouse
                self.action_chains.move_by_offset(
                    current_x - (0 if i == 0 else prev_x),
                    current_y - (0 if i == 0 else prev_y)
                ).perform()

                prev_x, prev_y = current_x, current_y
                time.sleep(duration / steps)

            self.log_interaction('visual_move', f'to ({target_x}, {target_y})')
            return True

        except Exception as e:
            print(f"âŒ SNAPE: Visual movement failed: {e}")
            return False

    def visual_click(self, x, y, button='left', double_click=False):
        """Visual click with highlighting"""
        print(f"ğŸ–±ï¸ SNAPE: Visual {button} click at ({x}, {y})")

        try:
            # Move to position with visual feedback
            self.visual_mouse_move(x, y, 0.5)

            # Show click highlight
            click_type = 'double' if double_click else button
            self.driver.execute_script(f"window.SNAPEVisual.highlightClick({x}, {y}, '{click_type}');")

            # Find element and perform actual click
            element = self.driver.execute_script(f"return document.elementFromPoint({x}, {y});")
            if not element:
                canvas = self.driver.find_element(By.TAG_NAME, "canvas")
                element = canvas

            # Perform click
            if button == 'right':
                self.action_chains.context_click(element).perform()
            elif double_click:
                self.action_chains.double_click(element).perform()
            else:
                self.action_chains.click(element).perform()

            self.log_interaction(f'visual_{click_type}_click', f'at ({x}, {y})')
            time.sleep(0.2)
            return True

        except Exception as e:
            print(f"âŒ SNAPE: Visual click failed: {e}")
            return False

    def visual_drag(self, start_x, start_y, end_x, end_y, duration=1.0):
        """Visual drag with full highlighting"""
        print(f"ğŸ–±ï¸ SNAPE: Visual drag from ({start_x}, {start_y}) to ({end_x}, {end_y})")

        try:
            # Move to start position
            self.visual_mouse_move(start_x, start_y, 0.3)

            # Show drag highlight
            self.driver.execute_script(f"window.SNAPEVisual.highlightDrag({start_x}, {start_y}, {end_x}, {end_y}, '{self.current_tool}');")

            # Get canvas element
            canvas = self.driver.find_element(By.TAG_NAME, "canvas")

            # Start drag
            self.action_chains.click_and_hold(canvas).perform()
            time.sleep(0.1)

            # Visual drag movement
            steps = max(15, int(duration * 25))
            for i in range(1, steps + 1):
                progress = i / steps
                ease_progress = 3 * progress**2 - 2 * progress**3

                current_x = start_x + (end_x - start_x) * ease_progress
                current_y = start_y + (end_y - start_y) * ease_progress

                # Add randomness
                current_x += (random.random() - 0.5) * 2
                current_y += (random.random() - 0.5) * 2

                # Update visual cursor
                self.driver.execute_script(f"window.SNAPEVisual.updateCursor({current_x}, {current_y});")

                # Move mouse
                self.action_chains.move_by_offset(
                    current_x - start_x - (0 if i == 1 else sum_x),
                    current_y - start_y - (0 if i == 1 else sum_y)
                ).perform()

                if i == 1:
                    sum_x = current_x - start_x
                    sum_y = current_y - start_y
                else:
                    sum_x += current_x - start_x - sum_x
                    sum_y += current_y - start_y - sum_y

                time.sleep(duration / steps)

            # Release drag
            self.action_chains.release().perform()

            self.log_interaction('visual_drag', f'from ({start_x}, {start_y}) to ({end_x}, {end_y}) with {self.current_tool}')
            return True

        except Exception as e:
            print(f"âŒ SNAPE: Visual drag failed: {e}")
            return False

    def visual_three_finger_gesture(self, center_x, center_y):
        """Visual three-finger gesture simulation"""
        print(f"ğŸ–ï¸ SNAPE: Visual three-finger gesture at ({center_x}, {center_y})")

        try:
            # Show three-finger visual
            self.driver.execute_script(f"window.SNAPEVisual.highlightThreeFinger({center_x}, {center_y});")

            # Simulate three-finger by rapid sequential touches
            touch_points = [
                (center_x - 20, center_y - 15),
                (center_x + 20, center_y - 15),
                (center_x, center_y + 15)
            ]

            for point in touch_points:
                self.visual_click(point[0], point[1])
                time.sleep(0.05)

            # If draw tool is active, make a short drag
            if self.current_tool == 'draw':
                time.sleep(0.2)
                self.visual_drag(center_x, center_y, center_x + 80, center_y + 40, 0.8)

            self.log_interaction('visual_three_finger', f'at ({center_x}, {center_y})')
            return True

        except Exception as e:
            print(f"âŒ SNAPE: Visual three-finger gesture failed: {e}")
            return False

    def visual_keyboard_input(self, key, description=""):
        """Visual keyboard input with highlighting"""
        print(f"âŒ¨ï¸ SNAPE: Visual keyboard input: {key}")

        try:
            # Show keyboard highlight
            self.driver.execute_script(f"window.SNAPEVisual.highlightKeyboard('{key}', '{description}');")

            # Send actual key
            self.action_chains.send_keys(key).perform()

            self.log_interaction('visual_keyboard', f'{key} - {description}')
            time.sleep(0.3)
            return True

        except Exception as e:
            print(f"âŒ SNAPE: Visual keyboard input failed: {e}")
            return False

    def visual_scroll(self, direction='down', amount=300):
        """Visual scrolling with highlighting"""
        print(f"ğŸ“œ SNAPE: Visual scroll {direction} by {amount}px")

        try:
            # Show scroll highlight
            self.driver.execute_script(f"window.SNAPEVisual.highlightScroll('{direction}', {amount});")

            # Perform actual scroll
            canvas = self.driver.find_element(By.TAG_NAME, "canvas")
            scroll_amount = amount if direction == 'down' else -amount

            self.action_chains.move_to_element(canvas).scroll_by_amount(0, scroll_amount).perform()

            self.log_interaction('visual_scroll', f'{direction} by {amount}px')
            time.sleep(0.5)
            return True

        except Exception as e:
            print(f"âŒ SNAPE: Visual scroll failed: {e}")
            return False

    def visual_tool_change(self, tool_name):
        """Visual tool change with highlighting"""
        print(f"ğŸ”§ SNAPE: Visual tool change to {tool_name}")

        try:
            # Find toolbar button
            button = self.driver.find_element(By.CSS_SELECTOR, f'.toolbar-btn[data-tool="{tool_name}"]')

            if button:
                # Get button position
                rect = button.rect
                center_x = rect['x'] + rect['width'] / 2
                center_y = rect['y'] + rect['height'] / 2

                # Visual click on button
                success = self.visual_click(center_x, center_y)

                if success:
                    # Show tool change highlight
                    self.driver.execute_script(f"window.SNAPEVisual.showToolChange('{tool_name}');")

                    self.current_tool = tool_name
                    self.log_interaction('visual_tool_change', tool_name)
                    time.sleep(0.3)

                return success
            else:
                print(f"âŒ SNAPE: Toolbar button {tool_name} not found")
                return False

        except Exception as e:
            print(f"âŒ SNAPE: Visual tool change failed: {e}")
            return False

    def log_interaction(self, interaction_type, details):
        """Log interactions for debugging"""
        log_entry = {
            'timestamp': time.time(),
            'type': interaction_type,
            'details': details,
            'tool': self.current_tool
        }
        self.interaction_log.append(log_entry)
        print(f"ğŸ“ SNAPE: {interaction_type} - {details}")

    def run_visual_test_suite(self):
        """Run comprehensive visual test suite"""
        print("ğŸš€ SNAPE Visual Interactions - Full Test Suite")
        print("=" * 60)

        try:
            # Setup
            self.setup_visual_chrome()
            if not self.load_canvas_with_visuals():
                return False

            print("âœ… SNAPE: Visual system ready")
            time.sleep(2)

            # Test 1: Toolbar interactions with visuals
            print("\nğŸ› ï¸ Test 1: Visual toolbar interactions")
            tools = ['select', 'draw', 'rectangle', 'ellipse', 'text', 'eraser']
            for tool in tools:
                self.visual_tool_change(tool)
                time.sleep(1)

            # Test 2: Canvas drawing with visual feedback
            print("\nğŸ¨ Test 2: Visual canvas drawing")
            canvas = self.driver.find_element(By.TAG_NAME, "canvas")
            canvas_rect = canvas.rect

            # Draw with different tools
            drawing_tests = [
                ('draw', [(200, 150), (280, 200), (360, 150), (440, 200)]),
                ('rectangle', [(500, 200), (600, 300)]),
                ('ellipse', [(200, 350), (300, 450)])
            ]

            for tool, positions in drawing_tests:
                self.visual_tool_change(tool)
                time.sleep(0.5)

                if tool == 'draw':
                    # Freehand drawing
                    start_x, start_y = positions[0]
                    adj_start_x = canvas_rect['x'] + start_x
                    adj_start_y = canvas_rect['y'] + start_y

                    for i in range(1, len(positions)):
                        end_x, end_y = positions[i]
                        adj_end_x = canvas_rect['x'] + end_x
                        adj_end_y = canvas_rect['y'] + end_y

                        self.visual_drag(adj_start_x, adj_start_y, adj_end_x, adj_end_y, 0.8)
                        adj_start_x, adj_start_y = adj_end_x, adj_end_y
                        time.sleep(0.3)
                else:
                    # Shape tools
                    start_x, start_y = positions[0]
                    end_x, end_y = positions[1]

                    adj_start_x = canvas_rect['x'] + start_x
                    adj_start_y = canvas_rect['y'] + start_y
                    adj_end_x = canvas_rect['x'] + end_x
                    adj_end_y = canvas_rect['y'] + end_y

                    self.visual_drag(adj_start_x, adj_start_y, adj_end_x, adj_end_y, 1.2)

                time.sleep(1.5)

            # Test 3: Right-click context menus
            print("\nğŸ–±ï¸ Test 3: Visual right-click interactions")
            test_positions = [
                (canvas_rect['x'] + 400, canvas_rect['y'] + 200),
                (canvas_rect['x'] + 600, canvas_rect['y'] + 350)
            ]

            for pos in test_positions:
                self.visual_click(pos[0], pos[1], 'right')
                time.sleep(1)

                # Try to click menu item
                try:
                    menu_items = self.driver.find_elements(By.CSS_SELECTOR, 
                        '.context-menu-item, .notion-menu-item')
                    if menu_items:
                        item = menu_items[0]
                        item_rect = item.rect
                        item_x = item_rect['x'] + item_rect['width'] / 2
                        item_y = item_rect['y'] + item_rect['height'] / 2
                        self.visual_click(item_x, item_y)
                except:
                    pass

                time.sleep(1)

            # Test 4: Three-finger gestures
            print("\nğŸ–ï¸ Test 4: Visual three-finger gestures")
            self.visual_tool_change('draw')
            time.sleep(0.5)

            gesture_positions = [
                (canvas_rect['x'] + 300, canvas_rect['y'] + 200),
                (canvas_rect['x'] + 500, canvas_rect['y'] + 350)
            ]

            for pos in gesture_positions:
                self.visual_three_finger_gesture(pos[0], pos[1])
                time.sleep(2)

            # Test 5: Keyboard shortcuts
            print("\nâŒ¨ï¸ Test 5: Visual keyboard shortcuts")
            shortcuts = [
                ('v', 'Select tool'),
                ('d', 'Draw tool'),
                ('r', 'Rectangle tool'),
                ('o', 'Ellipse tool'),
                ('t', 'Text tool')
            ]

            for key, desc in shortcuts:
                self.visual_keyboard_input(key, desc)
                time.sleep(1)

            # Test 6: Scrolling
            print("\nğŸ“œ Test 6: Visual scrolling")
            scroll_tests = [
                ('down', 200),
                ('up', 300),
                ('down', 150)
            ]

            for direction, amount in scroll_tests:
                self.visual_scroll(direction, amount)
                time.sleep(1)

            # Final report
            print("\nğŸ“Š SNAPE: Visual Test Complete!")
            print(f"âœ… Total interactions: {len(self.interaction_log)}")
            print(f"âœ… All interactions visually highlighted")
            print(f"âœ… Current tool: {self.current_tool}")

            # Save report
            report = {
                'total_interactions': len(self.interaction_log),
                'interactions': self.interaction_log,
                'final_tool': self.current_tool,
                'test_duration': time.time() - self.interaction_log[0]['timestamp'] if self.interaction_log else 0
            }

            with open('snape_visual_interactions_report.json', 'w') as f:
                json.dump(report, f, indent=2, default=str)

            print("ğŸ“Š Detailed report saved to: snape_visual_interactions_report.json")

            # Keep browser open
            input("\nPress Enter to close browser...")

            return True

        except Exception as e:
            print(f"âŒ Visual test failed: {e}")
            return False
        finally:
            if self.driver:
                self.driver.quit()

if __name__ == "__main__":
