   903â†’        
   904â†’        // Current System State
   905â†’        if (data.stateHistory && data.stateHistory.length > 0) {
   906â†’            const currentState = data.stateHistory[data.stateHistory.length - 1];
   907â†’            output += `=== CURRENT SYSTEM STATE ===\n`;
   908â†’            output += `Timestamp: ${new Date(currentState.timestamp).toISOString()}\n\n`;
   909â†’            
   910â†’            // Editor State
   911â†’            output += `[EDITOR]\n`;
   912â†’            output += `Available: ${currentState.editor?.available || false}\n`;
   913â†’            output += `Ready: ${currentState.editor?.ready || false}\n`;
   914â†’            output += `Current Tool: ${currentState.editor?.currentTool || 'none'}\n\n`;
   915â†’            
   916â†’            // Tools State
   917â†’            output += `[TOOLS]\n`;
   918â†’            output += `Tool Count: ${currentState.tools?.toolCount || 0}\n`;
   919â†’            output += `Active Tool: ${currentState.tools?.activeTool || 'none'}\n`;
   920â†’            if (currentState.tools?.availableTools) {
   921â†’                output += `Available Tools:\n`;
