  1605â†’    screenToCanvasCoords(screenX, screenY) {
  1606â†’        const canvas = document.querySelector('canvas');
  1607â†’        if (!canvas) return {x: screenX, y: screenY};
  1608â†’        
  1609â†’        const rect = canvas.getBoundingClientRect();
  1610â†’        const camera = window.tlEditor?.getCamera() || {x: 0, y: 0, z: 1};
  1611â†’        
  1612â†’        return {
  1613â†’            x: ((screenX - rect.left) / camera.z) - camera.x,
  1614â†’            y: ((screenY - rect.top) / camera.z) - camera.y
  1615â†’        };
  1616â†’    }
