  1624â†’        
  1625â†’        console.log(`ğŸ–±ï¸ Simulating three-finger ${type} at`, position);
  1626â†’        
  1627â†’        // Create three pointer events with correct event names
  1628â†’        const eventType = type === 'down' ? 'pointerdown' : 
  1629â†’                         type === 'move' ? 'pointermove' : 
  1630â†’                         type === 'up' ? 'pointerup' : `pointer${type}`;
  1631â†’        
  1632â†’        for (let i = 0; i < 3; i++) {
  1633â†’            const event = new PointerEvent(eventType, {
  1634â†’                pointerId: i + 1,
  1635â†’                clientX: position.x + (i * 10), // Spread fingers slightly
  1636â†’                clientY: position.y + (i * 10),
  1637â†’                button: 0,
  1638â†’                buttons: type === 'up' ? 0 : 1, // No buttons pressed on up
  1639â†’                bubbles: true,
  1640â†’                cancelable: true
  1641â†’            });
  1642â†’            
  1643â†’            canvas.dispatchEvent(event);
  1644â†’            
  1645â†’            // Track cursor movement with highlight
  1646â†’            this.trackCursorMovement(position.x + (i * 10), position.y + (i * 10), true);
  1647â†’        }
  1648â†’        
  1649â†’        // Capture state after gesture
  1650â†’        const stateAfter = this.captureCurrentState();
  1651â†’        this.logUserAction(`three_finger_${type}`, {
  1652â†’            position: position,
  1653â†’            state: stateAfter
  1654â†’        });
  1655â†’    }
