  1814â†’    compareShapeStates(beforeShapes, afterShapes, expectedShapes) {
  1815â†’        if (!expectedShapes) return {passed: true, actual: afterShapes, expected: null};
  1816â†’        
  1817â†’        // Handle both array and object formats
  1818â†’        const beforeCount = Array.isArray(beforeShapes) ? beforeShapes.length : beforeShapes.totalShapes || 0;
  1819â†’        const afterCount = Array.isArray(afterShapes) ? afterShapes.length : afterShapes.totalShapes || 0;
  1820â†’        const afterShapeArray = Array.isArray(afterShapes) ? afterShapes : (afterShapes.shapes || []);
  1821â†’        
  1822â†’        return {
  1823â†’            passed: afterCount === expectedShapes.count,
  1824â†’            actual: {
  1825â†’                count: afterCount,
  1826â†’                storeCount: afterShapes.storeShapes || afterCount,
  1827â†’                domCount: afterShapes.domShapes || afterCount,
  1828â†’                types: afterShapeArray.map(s => s.type || 'unknown'),
  1829â†’                shapeTypes: afterShapes.shapeTypes || {}
  1830â†’            },
  1831â†’            expected: expectedShapes,
  1832â†’            difference: afterCount - expectedShapes.count
  1833â†’        };
  1834â†’    }
  1835â†’    
  1836â†’    compareErrorStates(beforeErrors, afterErrors, expectedErrors) {
  1837â†’        if (!expectedErrors) return {passed: true, actual: afterErrors, expected: null};
  1838â†’        
  1839â†’        const timeoutErrors = afterErrors.filter(e => e.message.includes('timeout'));
  1840â†’        
  1841â†’        return {
  1842â†’            passed: timeoutErrors.length === expectedErrors.timeoutErrors.length,
  1843â†’            actual: {
  1844â†’                total: afterErrors.length,
  1845â†’                timeouts: timeoutErrors.length,
  1846â†’                timeoutMessages: timeoutErrors.map(e => e.message)
  1847â†’            },
  1848â†’            expected: expectedErrors,
  1849â†’            difference: timeoutErrors.length - expectedErrors.timeoutErrors.length
  1850â†’        };
  1851â†’    }
