   666â†’    makeDraggable(element) {
   667â†’        let pos1 = 0, pos2 = 0, pos3 = 0, pos4 = 0;
   668â†’        let isDragging = false;
   669â†’        const header = element.querySelector('.diagnostic-header');
   670â†’        
   671â†’        // Ensure element has proper positioning
   672â†’        element.style.position = 'fixed';
   673â†’        
   674â†’        header.onmousedown = dragMouseDown;
   675â†’        
   676â†’        function dragMouseDown(e) {
   677â†’            e = e || window.event;
   678â†’            e.preventDefault();
   679â†’            e.stopPropagation();
   680â†’            
   681â†’            isDragging = true;
   682â†’            pos3 = e.clientX;
   683â†’            pos4 = e.clientY;
   684â†’            
   685â†’            // Add dragging class for visual feedback
   686â†’            element.classList.add('dragging');
   687â†’            
   688â†’            // Attach events to document to handle dragging outside element
   689â†’            document.addEventListener('mouseup', closeDragElement);
   690â†’            document.addEventListener('mousemove', elementDrag);
   691â†’            
   692â†’            // Prevent text selection while dragging
   693â†’            document.body.style.userSelect = 'none';
   694â†’            document.body.style.webkitUserSelect = 'none';
   695â†’        }
   696â†’        
   697â†’        function elementDrag(e) {
   698â†’            if (!isDragging) return;
   699â†’            
   700â†’            e = e || window.event;
   701â†’            e.preventDefault();
   702â†’            e.stopPropagation();
   703â†’            
   704â†’            pos1 = pos3 - e.clientX;
   705â†’            pos2 = pos4 - e.clientY;
   706â†’            pos3 = e.clientX;
   707â†’            pos4 = e.clientY;
   708â†’            
   709â†’            // Calculate new position
   710â†’            let newTop = element.offsetTop - pos2;
   711â†’            let newLeft = element.offsetLeft - pos1;
   712â†’            
   713â†’            // Keep element within viewport bounds
   714â†’            const rect = element.getBoundingClientRect();
   715â†’            const viewportWidth = window.innerWidth;
   716â†’            const viewportHeight = window.innerHeight;
   717â†’            
   718â†’            // Constrain to viewport
   719â†’            newLeft = Math.max(0, Math.min(newLeft, viewportWidth - rect.width));
   720â†’            newTop = Math.max(0, Math.min(newTop, viewportHeight - rect.height));
   721â†’            
   722â†’            // Apply new position
   723â†’            element.style.top = newTop + "px";
   724â†’            element.style.left = newLeft + "px";
   725â†’        }
   726â†’        
   727â†’        function closeDragElement() {
   728â†’            isDragging = false;
   729â†’            
   730â†’            // Remove dragging class
   731â†’            element.classList.remove('dragging');
   732â†’            
   733â†’            // Remove event listeners
   734â†’            document.removeEventListener('mouseup', closeDragElement);
   735â†’            document.removeEventListener('mousemove', elementDrag);
   736â†’            
   737â†’            // Restore text selection
   738â†’            document.body.style.userSelect = '';
   739â†’            document.body.style.webkitUserSelect = '';
   740â†’        }
   741â†’        
   742â†’        // Handle touch events for mobile
   743â†’        header.ontouchstart = function(e) {
   744â†’            e.preventDefault();
   745â†’            const touch = e.touches[0];
   746â†’            dragMouseDown({
   747â†’                clientX: touch.clientX,
   748â†’                clientY: touch.clientY,
   749â†’                preventDefault: () => e.preventDefault(),
   750â†’                stopPropagation: () => e.stopPropagation()
   751â†’            });
   752â†’        };
   753â†’    }
