   755â†’    copyErrorsToClipboard() {
   756â†’        try {
   757â†’            // Get all errors from the error log
   758â†’            const allErrors = this.errorLog.slice(); // Copy array
   759â†’            
   760â†’            if (allErrors.length === 0) {
   761â†’                this.showCopyFeedback('copy-errors-btn', 'No errors to copy', false);
   762â†’                return;
   763â†’            }
   764â†’            
   765â†’            // Format errors for clipboard
   766â†’            const errorText = this.formatErrorsForClipboard(allErrors);
   767â†’            
   768â†’            // Copy to clipboard using modern API
   769â†’            if (navigator.clipboard && window.isSecureContext) {
   770â†’                navigator.clipboard.writeText(errorText).then(() => {
   771â†’                    this.showCopyFeedback('copy-errors-btn', `Copied ${allErrors.length} errors`, true);
   772â†’                }).catch(() => {
   773â†’                    // Fallback to legacy method
   774â†’                    this.fallbackCopyToClipboard(errorText);
   775â†’                });
   776â†’            } else {
   777â†’                // Fallback for older browsers or non-secure contexts
   778â†’                this.fallbackCopyToClipboard(errorText);
   779â†’            }
   780â†’            
   781â†’        } catch (error) {
   782â†’            console.error('Copy to clipboard failed:', error);
   783â†’            this.showCopyFeedback('copy-errors-btn', 'Copy failed', false);
   784â†’        }
   785â†’    }
