   787â†’    formatErrorsForClipboard(errors) {
   788â†’        const timestamp = new Date().toISOString();
   789â†’        const header = `=== UNIFIED DIAGNOSTICS ERROR LOG ===\nExported: ${timestamp}\nTotal Errors: ${errors.length}\n\n`;
   790â†’        
   791â†’        const formattedErrors = errors.map((error, index) => {
   792â†’            const errorTime = new Date(error.timestamp).toISOString();
   793â†’            const errorNumber = (index + 1).toString().padStart(3, '0');
   794â†’            
   795â†’            let formattedError = `[${errorNumber}] ${errorTime} [${error.level.toUpperCase()}]\n`;
   796â†’            formattedError += `Message: ${error.message}\n`;
   797â†’            
   798â†’            if (error.filename) {
   799â†’                formattedError += `File: ${error.filename}:${error.lineno}:${error.colno}\n`;
   800â†’            }
   801â†’            
   802â†’            if (error.stack) {
   803â†’                formattedError += `Stack Trace:\n${error.stack}\n`;
   804â†’            }
   805â†’            
   806â†’            formattedError += `${'â”€'.repeat(80)}\n`;
   807â†’            
   808â†’            return formattedError;
   809â†’        }).join('\n');
   810â†’        
   811â†’        const footer = `\n=== END ERROR LOG ===\nGenerated by Unified Diagnostics System\nTotal entries: ${errors.length}`;
   812â†’        
   813â†’        return header + formattedErrors + footer;
   814â†’    }
   815â†’    
   816â†’    fallbackCopyToClipboard(text) {
   817â†’        try {
   818â†’            // Create temporary textarea
   819â†’            const textarea = document.createElement('textarea');
   820â†’            textarea.value = text;
