   894â†’    formatDiagnosticDataForClipboard(data) {
   895â†’        const timestamp = new Date().toISOString();
   896â†’        let output = `=== UNIFIED DIAGNOSTICS COMPLETE REPORT ===\n`;
   897â†’        output += `Generated: ${timestamp}\n`;
   898â†’        output += `System: ${data.systemInfo?.userAgent || 'Unknown'}\n`;
   899â†’        output += `Platform: ${data.systemInfo?.platform || 'Unknown'}\n`;
   900â†’        output += `Monitoring Active: ${data.monitoringActive}\n`;
   901â†’        output += `State Snapshots: ${data.stateHistory?.length || 0}\n`;
   902â†’        output += `Total Errors: ${data.errorLog?.length || 0}\n\n`;
   903â†’        
   904â†’        // Current System State
   905â†’        if (data.stateHistory && data.stateHistory.length > 0) {
   906â†’            const currentState = data.stateHistory[data.stateHistory.length - 1];
   907â†’            output += `=== CURRENT SYSTEM STATE ===\n`;
   908â†’            output += `Timestamp: ${new Date(currentState.timestamp).toISOString()}\n\n`;
   909â†’            
   910â†’            // Editor State
   911â†’            output += `[EDITOR]\n`;
   912â†’            output += `Available: ${currentState.editor?.available || false}\n`;
   913â†’            output += `Ready: ${currentState.editor?.ready || false}\n`;
   914â†’            output += `Current Tool: ${currentState.editor?.currentTool || 'none'}\n\n`;
   915â†’            
   916â†’            // Tools State
   917â†’            output += `[TOOLS]\n`;
   918â†’            output += `Tool Count: ${currentState.tools?.toolCount || 0}\n`;
   919â†’            output += `Active Tool: ${currentState.tools?.activeTool || 'none'}\n`;
   920â†’            if (currentState.tools?.availableTools) {
   921â†’                output += `Available Tools:\n`;
   922â†’                currentState.tools.availableTools.forEach(tool => {
   923â†’                    output += `  - ${tool.name}: ${tool.visible ? 'visible' : 'hidden'}, ${tool.enabled ? 'enabled' : 'disabled'}\n`;
   924â†’                });
   925â†’            }
   926â†’            output += `\n`;
   927â†’            
   928â†’            // Canvas State
   929â†’            output += `[CANVAS]\n`;
   930â†’            output += `Visible: ${currentState.canvas?.canvasVisible || false}\n`;
   931â†’            output += `Canvas Count: ${currentState.canvas?.canvasCount || 0}\n`;
   932â†’            if (currentState.canvas?.dimensions) {
   933â†’                output += `Dimensions: ${currentState.canvas.dimensions.width}x${currentState.canvas.dimensions.height}\n`;
   934â†’            }
   935â†’            output += `\n`;
   936â†’            
   937â†’            // Shapes State
   938â†’            output += `[SHAPES]\n`;
   939â†’            output += `Total Shapes: ${currentState.shapes?.totalShapes || 0}\n`;
   940â†’            if (currentState.shapes?.shapeTypes) {
   941â†’                output += `Shape Types:\n`;
   942â†’                Object.entries(currentState.shapes.shapeTypes).forEach(([type, count]) => {
   943â†’                    output += `  - ${type}: ${count}\n`;
   944â†’                });
   945â†’            }
   946â†’            output += `\n`;
   947â†’        }
   948â†’        
   949â†’        // Performance Metrics
   950â†’        if (data.performanceMetrics && Object.keys(data.performanceMetrics).length > 0) {
   951â†’            output += `=== PERFORMANCE METRICS ===\n`;
   952â†’            Object.entries(data.performanceMetrics).forEach(([key, value]) => {
   953â†’                if (typeof value === 'object' && value !== null) {
   954â†’                    output += `${key}:\n`;
   955â†’                    Object.entries(value).forEach(([subKey, subValue]) => {
   956â†’                        output += `  ${subKey}: ${subValue}\n`;
   957â†’                    });
   958â†’                } else {
   959â†’                    output += `${key}: ${value}\n`;
   960â†’                }
   961â†’            });
   962â†’            output += `\n`;
   963â†’        }
   964â†’        
   965â†’        // Error Log Summary
   966â†’        if (data.errorLog && data.errorLog.length > 0) {
   967â†’            output += `=== ERROR LOG SUMMARY ===\n`;
   968â†’            output += `Total Errors: ${data.errorLog.length}\n\n`;
   969â†’            
   970â†’            // Group errors by type
   971â†’            const errorTypes = {};
   972â†’            data.errorLog.forEach(error => {
   973â†’                errorTypes[error.level] = (errorTypes[error.level] || 0) + 1;
   974â†’            });
   975â†’            
   976â†’            output += `Error Breakdown:\n`;
   977â†’            Object.entries(errorTypes).forEach(([type, count]) => {
   978â†’                output += `  ${type.toUpperCase()}: ${count}\n`;
   979â†’            });
   980â†’            output += `\n`;
   981â†’            
   982â†’            // Recent errors (last 5)
   983â†’            output += `Recent Errors (Last 5):\n`;
   984â†’            const recentErrors = data.errorLog.slice(-5);
   985â†’            recentErrors.forEach((error, index) => {
   986â†’                const errorTime = new Date(error.timestamp).toLocaleTimeString();
   987â†’                output += `${index + 1}. [${errorTime}] ${error.level.toUpperCase()}: ${error.message}\n`;
   988â†’            });
   989â†’        }
   990â†’        
   991â†’        output += `\n=== END DIAGNOSTIC REPORT ===\n`;
   992â†’        output += `Generated by Unified Diagnostics System v1.0\n`;
   993â†’        output += `For technical support, include this complete report\n`;
   994â†’        
   995â†’        return output;
   996â†’    }
