  1300â†’                console.log(`  â†³ Created new error group`);
  1301â†’            }
  1302â†’        });
  1303â†’        
  1304â†’        // Convert to array and sort by latest timestamp (most recent first)
  1305â†’        return Array.from(errorGroups.values()).sort((a, b) => b.latestTimestamp - a.latestTimestamp);
  1306â†’    }
