    makeDraggable(element) {
        let pos1 = 0, pos2 = 0, pos3 = 0, pos4 = 0;
        let isDragging = false;
        const header = element.querySelector('.diagnostic-header');

        // Ensure element has proper positioning
        element.style.position = 'fixed';

        header.onmousedown = dragMouseDown;

        function dragMouseDown(e) {
            e = e || window.event;
            e.preventDefault();
            e.stopPropagation();

            isDragging = true;
            pos3 = e.clientX;
            pos4 = e.clientY;

            // Add dragging class for visual feedback
            element.classList.add('dragging');

            // Attach events to document to handle dragging outside element
            document.addEventListener('mouseup', closeDragElement);
            document.addEventListener('mousemove', elementDrag);

            // Prevent text selection while dragging
            document.body.style.userSelect = 'none';
            document.body.style.webkitUserSelect = 'none';
        }

        function elementDrag(e) {
            if (!isDragging) return;

            e = e || window.event;
            e.preventDefault();
            e.stopPropagation();

            pos1 = pos3 - e.clientX;
            pos2 = pos4 - e.clientY;
            pos3 = e.clientX;
            pos4 = e.clientY;

            // Calculate new position
            let newTop = element.offsetTop - pos2;
            let newLeft = element.offsetLeft - pos1;

            // Keep element within viewport bounds
            const rect = element.getBoundingClientRect();
            const viewportWidth = window.innerWidth;
            const viewportHeight = window.innerHeight;

            // Constrain to viewport
            newLeft = Math.max(0, Math.min(newLeft, viewportWidth - rect.width));
            newTop = Math.max(0, Math.min(newTop, viewportHeight - rect.height));

            // Apply new position
            element.style.top = newTop + "px";
            element.style.left = newLeft + "px";
        }

        function closeDragElement() {
            isDragging = false;

            // Remove dragging class
            element.classList.remove('dragging');

            // Remove event listeners
            document.removeEventListener('mouseup', closeDragElement);
            document.removeEventListener('mousemove', elementDrag);

            // Restore text selection
            document.body.style.userSelect = '';
            document.body.style.webkitUserSelect = '';
        }

        // Handle touch events for mobile
        header.ontouchstart = function(e) {
            e.preventDefault();
            const touch = e.touches[0];
            dragMouseDown({
                clientX: touch.clientX,
                clientY: touch.clientY,
                preventDefault: () => e.preventDefault(),
                stopPropagation: () => e.stopPropagation()
            });
        };
    }

    copyErrorsToClipboard() {
        try {
            // Get all errors from the error log
            const allErrors = this.errorLog.slice(); // Copy array

            if (allErrors.length === 0) {
                this.showCopyFeedback('copy-errors-btn', 'No errors to copy', false);
                return;
            }

            // Format errors for clipboard
            const errorText = this.formatErrorsForClipboard(allErrors);

            // Copy to clipboard using modern API
            if (navigator.clipboard && window.isSecureContext) {
                navigator.clipboard.writeText(errorText).then(() => {
                    this.showCopyFeedback('copy-errors-btn', `Copied ${allErrors.length} errors`, true);
                }).catch(() => {
                    // Fallback to legacy method
                    this.fallbackCopyToClipboard(errorText);
                });
            } else {
                // Fallback for older browsers or non-secure contexts
                this.fallbackCopyToClipboard(errorText);
            }

        } catch (error) {
            console.error('Copy to clipboard failed:', error);
            this.showCopyFeedback('copy-errors-btn', 'Copy failed', false);
        }
    }

    formatErrorsForClipboard(errors) {
        const timestamp = new Date().toISOString();
        const header = `=== UNIFIED DIAGNOSTICS ERROR LOG ===\nExported: ${timestamp}\nTotal Errors: ${errors.length}\n\n`;

        const formattedErrors = errors.map((error, index) => {
            const errorTime = new Date(error.timestamp).toISOString();
            const errorNumber = (index + 1).toString().padStart(3, '0');

            let formattedError = `[${errorNumber}] ${errorTime} [${error.level.toUpperCase()}]\n`;
            formattedError += `Message: ${error.message}\n`;

            if (error.filename) {
                formattedError += `File: ${error.filename}:${error.lineno}:${error.colno}\n`;
            }

            if (error.stack) {
                formattedError += `Stack Trace:\n${error.stack}\n`;
            }

            formattedError += `${'â”€'.repeat(80)}\n`;

            return formattedError;
        }).join('\n');

        const footer = `\n=== END ERROR LOG ===\nGenerated by Unified Diagnostics System\nTotal entries: ${errors.length}`;

        return header + formattedErrors + footer;
    }

    fallbackCopyToClipboard(text) {
        try {
            // Create temporary textarea
            const textarea = document.createElement('textarea');
            textarea.value = text;
            textarea.style.position = 'fixed';
            textarea.style.left = '-9999px';
            textarea.style.top = '-9999px';
            document.body.appendChild(textarea);

            // Select and copy
            textarea.focus();
            textarea.select();
            const successful = document.execCommand('copy');

            // Clean up
            document.body.removeChild(textarea);

            if (successful) {
                this.showCopyFeedback('copy-errors-btn', `Copied ${this.errorLog.length} errors`, true);
            } else {
                this.showCopyFeedback('copy-errors-btn', 'Copy failed', false);
            }

        } catch (error) {
            console.error('Fallback copy failed:', error);
            this.showCopyFeedback('copy-errors-btn', 'Copy not supported', false);
        }
    }

    showCopyFeedback(buttonId, message, success) {
        const button = document.getElementById(buttonId);
        if (!button) return;

        // Store original content
        const originalText = button.textContent;
        const originalTitle = button.title;

        // Show feedback
        button.textContent = success ? 'âœ“' : 'âœ—';
        button.title = message;
        button.classList.add(success ? 'copied' : 'copy-error');

        // Reset after delay
        setTimeout(() => {
            button.textContent = originalText;
            button.title = originalTitle;
            button.classList.remove('copied', 'copy-error');
        }, 2000);
    }

    copyAllDiagnosticData() {
        try {
            // Get complete diagnostic data
            const diagnosticData = this.exportDiagnosticData();

            // Format for clipboard
            const formattedData = this.formatDiagnosticDataForClipboard(diagnosticData);

            // Copy to clipboard using modern API
            if (navigator.clipboard && window.isSecureContext) {
                navigator.clipboard.writeText(formattedData).then(() => {
                    this.showCopyFeedback('copy-all-btn', 'Complete diagnostics copied', true);
                }).catch(() => {
                    // Fallback to legacy method
                    this.fallbackCopyAllData(formattedData);
                });
            } else {
                // Fallback for older browsers or non-secure contexts
                this.fallbackCopyAllData(formattedData);
            }

        } catch (error) {
            console.error('Copy all diagnostic data failed:', error);
            this.showCopyFeedback('copy-all-btn', 'Copy failed', false);
        }
    }

    formatDiagnosticDataForClipboard(data) {
        const timestamp = new Date().toISOString();
        let output = `=== UNIFIED DIAGNOSTICS COMPLETE REPORT ===\n`;
        output += `Generated: ${timestamp}\n`;
        output += `System: ${data.systemInfo?.userAgent || 'Unknown'}\n`;
        output += `Platform: ${data.systemInfo?.platform || 'Unknown'}\n`;
        output += `Monitoring Active: ${data.monitoringActive}\n`;
        output += `State Snapshots: ${data.stateHistory?.length || 0}\n`;
        output += `Total Errors: ${data.errorLog?.length || 0}\n\n`;

        // Current System State
        if (data.stateHistory && data.stateHistory.length > 0) {
            const currentState = data.stateHistory[data.stateHistory.length - 1];
            output += `=== CURRENT SYSTEM STATE ===\n`;
            output += `Timestamp: ${new Date(currentState.timestamp).toISOString()}\n\n`;

            // Editor State
            output += `[EDITOR]\n`;
            output += `Available: ${currentState.editor?.available || false}\n`;
            output += `Ready: ${currentState.editor?.ready || false}\n`;
            output += `Current Tool: ${currentState.editor?.currentTool || 'none'}\n\n`;

            // Tools State
            output += `[TOOLS]\n`;
            output += `Tool Count: ${currentState.tools?.toolCount || 0}\n`;
            output += `Active Tool: ${currentState.tools?.activeTool || 'none'}\n`;
            if (currentState.tools?.availableTools) {
                output += `Available Tools:\n`;
                currentState.tools.availableTools.forEach(tool => {
                    output += `  - ${tool.name}: ${tool.visible ? 'visible' : 'hidden'}, ${tool.enabled ? 'enabled' : 'disabled'}\n`;
                });
            }
            output += `\n`;

            // Canvas State
            output += `[CANVAS]\n`;
            output += `Visible: ${currentState.canvas?.canvasVisible || false}\n`;
            output += `Canvas Count: ${currentState.canvas?.canvasCount || 0}\n`;
            if (currentState.canvas?.dimensions) {
                output += `Dimensions: ${currentState.canvas.dimensions.width}x${currentState.canvas.dimensions.height}\n`;
            }
            output += `\n`;

            // Shapes State
            output += `[SHAPES]\n`;
            output += `Total Shapes: ${currentState.shapes?.totalShapes || 0}\n`;
            if (currentState.shapes?.shapeTypes) {
                output += `Shape Types:\n`;
                Object.entries(currentState.shapes.shapeTypes).forEach(([type, count]) => {
                    output += `  - ${type}: ${count}\n`;
                });
            }
            output += `\n`;
        }

        // Performance Metrics
        if (data.performanceMetrics && Object.keys(data.performanceMetrics).length > 0) {
            output += `=== PERFORMANCE METRICS ===\n`;
            Object.entries(data.performanceMetrics).forEach(([key, value]) => {
                if (typeof value === 'object' && value !== null) {
                    output += `${key}:\n`;
                    Object.entries(value).forEach(([subKey, subValue]) => {
                        output += `  ${subKey}: ${subValue}\n`;
                    });
                } else {
                    output += `${key}: ${value}\n`;
                }
            });
            output += `\n`;
        }

        // Error Log Summary
        if (data.errorLog && data.errorLog.length > 0) {
            output += `=== ERROR LOG SUMMARY ===\n`;
            output += `Total Errors: ${data.errorLog.length}\n\n`;

            // Group errors by type
            const errorTypes = {};
            data.errorLog.forEach(error => {
                errorTypes[error.level] = (errorTypes[error.level] || 0) + 1;
            });

            output += `Error Breakdown:\n`;
            Object.entries(errorTypes).forEach(([type, count]) => {
                output += `  ${type.toUpperCase()}: ${count}\n`;
            });
            output += `\n`;

            // Recent errors (last 5)
            output += `Recent Errors (Last 5):\n`;
            const recentErrors = data.errorLog.slice(-5);
            recentErrors.forEach((error, index) => {
                const errorTime = new Date(error.timestamp).toLocaleTimeString();
                output += `${index + 1}. [${errorTime}] ${error.level.toUpperCase()}: ${error.message}\n`;
            });
        }

        output += `\n=== END DIAGNOSTIC REPORT ===\n`;
        output += `Generated by Unified Diagnostics System v1.0\n`;
        output += `For technical support, include this complete report\n`;

        return output;
    }

