    compareShapeStates(beforeShapes, afterShapes, expectedShapes) {
        if (!expectedShapes) return {passed: true, actual: afterShapes, expected: null};

        // Handle both array and object formats
        const beforeCount = Array.isArray(beforeShapes) ? beforeShapes.length : beforeShapes.totalShapes || 0;
        const afterCount = Array.isArray(afterShapes) ? afterShapes.length : afterShapes.totalShapes || 0;
        const afterShapeArray = Array.isArray(afterShapes) ? afterShapes : (afterShapes.shapes || []);

        return {
            passed: afterCount === expectedShapes.count,
            actual: {
                count: afterCount,
                storeCount: afterShapes.storeShapes || afterCount,
                domCount: afterShapes.domShapes || afterCount,
                types: afterShapeArray.map(s => s.type || 'unknown'),
                shapeTypes: afterShapes.shapeTypes || {}
            },
            expected: expectedShapes,
            difference: afterCount - expectedShapes.count
        };
    }

    compareErrorStates(beforeErrors, afterErrors, expectedErrors) {
        if (!expectedErrors) return {passed: true, actual: afterErrors, expected: null};

        const timeoutErrors = afterErrors.filter(e => e.message.includes('timeout'));

        return {
            passed: timeoutErrors.length === expectedErrors.timeoutErrors.length,
            actual: {
                total: afterErrors.length,
                timeouts: timeoutErrors.length,
                timeoutMessages: timeoutErrors.map(e => e.message)
            },
            expected: expectedErrors,
            difference: timeoutErrors.length - expectedErrors.timeoutErrors.length
        };
    }

    comparePerformanceStates(beforePerf, afterPerf) {
        return {
            passed: true, // Performance comparison is informational
            actual: afterPerf,
            expected: null
        };
    }

    compareInputStates(beforeInput, afterInput, expectedInput) {
        if (!expectedInput) return {passed: true, actual: afterInput, expected: null};

        return {
            passed: true, // Input state comparison needs specific implementation
            actual: afterInput,
            expected: expectedInput
        };
    }

