/**
 * Unified Client-Side Diagnostics System
 * 
 * Consolidates all diagnostic capabilities into one seamless system:
 * - State monitoring and capture
 * - Error tracking and logging
 * - Performance metrics
 * - Visual feedback systems
 * - Enterprise-grade architecture
 * 
 * Works seamlessly with unified-diagnostic-system.py
 * 
 * Date: 2025-07-25
 * Priority: P0 CRITICAL - Replaces all other diagnostic JS files
 */

class UnifiedDiagnostics {
    constructor() {
        this.initialized = false;
        this.monitoringActive = false;
        this.stateHistory = [];
        this.errorLog = [];
        this.performanceMetrics = {};
        this.maxHistorySize = 100;
        this.dashboardElement = null;

        // Pointer tracking for SNAPE assistance
        this.currentPointerCoordinates = { x: null, y: null };
        this.lastPointerUpdate = null;

        // Initialize immediately
        this.initialize();

        console.log('ğŸ” Unified Diagnostics System initialized');
    }

    initialize() {
        try {
            this.setupErrorTracking();
            this.setupPerformanceMonitoring();
            this.createDashboard();
            this.initialized = true;

            // Auto-start monitoring
            this.startMonitoring();

            // Initialize cursor tracking for user mock testing
            this.initializeCursorTracking();

            // Initialize pointer coordinate tracking for SNAPE assistance
            this.initializePointerTracking();

            // ğŸ§™â€â™€ï¸ Initialize witch-powered error purging system
            this.initializeWitchErrorPurging();

            console.log('âœ… Unified diagnostics fully initialized');
        } catch (error) {
            console.error('âŒ Failed to initialize unified diagnostics:', error);
        }
    }

    /**
     * Log a message with timestamp and optional level
     * Used by external systems like witch-embed-system.js
     */
    logMessage(message, level = 'info') {
        const timestamp = new Date().toLocaleTimeString();
        const logEntry = {
            timestamp: Date.now(),
            level: level,
            message: message,
            formattedTime: timestamp
        };

        // Add to logs array
        window.unifiedDiagnosticLogs = window.unifiedDiagnosticLogs || [];
        window.unifiedDiagnosticLogs.push(logEntry);

        // Also add to internal log for dashboard
        this.errorLog.push(logEntry);

        // Limit log size
        if (window.unifiedDiagnosticLogs.length > 100) {
            window.unifiedDiagnosticLogs.shift();
        }
        if (this.errorLog.length > 100) {
            this.errorLog.shift();
        }

        // Output to console with timestamp
        console.log(`[${timestamp}] ${message}`);

        // Update dashboard if it exists
        setTimeout(() => this.updateDashboard(), 10);

        return logEntry;
    }

    setupErrorTracking() {
        // Enhanced error tracking
        window.unifiedDiagnosticErrors = window.unifiedDiagnosticErrors || [];
        window.unifiedDiagnosticLogs = window.unifiedDiagnosticLogs || [];

        // Override console methods for tracking
        const originalError = console.error;
        const originalLog = console.log;
        const originalWarn = console.warn;

        console.error = (...args) => {
            const errorEntry = {
                timestamp: Date.now(),
                level: 'error',
                message: args.join(' '),
                stack: new Error().stack
            };

            window.unifiedDiagnosticErrors.push(errorEntry);
            this.errorLog.push(errorEntry);

            // Limit log size
            if (this.errorLog.length > 100) {
                this.errorLog.shift();
                window.unifiedDiagnosticErrors.shift();
            }

            originalError.apply(console, args);

            // Force immediate dashboard update for errors
            setTimeout(() => this.updateDashboard(), 10);
        };

        console.warn = (...args) => {
            const warnEntry = {
                timestamp: Date.now(),
                level: 'warning',
                message: args.join(' ')
            };

            window.unifiedDiagnosticLogs.push(warnEntry);
            this.errorLog.push(warnEntry); // Also add warnings to error log for visibility

            originalWarn.apply(console, args);

            // Force immediate dashboard update for warnings
            setTimeout(() => this.updateDashboard(), 10);
        };

        console.log = (...args) => {
            const logEntry = {
                timestamp: Date.now(),
                level: 'log',
                message: args.join(' ')
            };

            window.unifiedDiagnosticLogs.push(logEntry);

            // Limit log size
            if (window.unifiedDiagnosticLogs.length > 200) {
                window.unifiedDiagnosticLogs.shift();
            }

            originalLog.apply(console, args);
        };

        // Global error handler - captures ALL JavaScript errors
        window.addEventListener('error', (e) => {
            let message = e.message;

            // Enhanced error message formatting
            if (e.error && e.error.name) {
                message = `${e.error.name}: ${e.message}`;
            }

            const errorEntry = {
                timestamp: Date.now(),
                level: 'javascript_error',
                message: message,
                filename: e.filename,
                lineno: e.lineno,
                colno: e.colno,
                stack: e.error ? e.error.stack : null
            };

            window.unifiedDiagnosticErrors.push(errorEntry);
            this.errorLog.push(errorEntry);

            console.log(`ğŸ”´ CAPTURED ERROR: ${message} at ${e.filename}:${e.lineno}`);

            // Force immediate dashboard update for global errors
            setTimeout(() => this.updateDashboard(), 10);
        });

        // Unhandled promise rejection handler
        window.addEventListener('unhandledrejection', (e) => {
            const errorEntry = {
                timestamp: Date.now(),
                level: 'promise_rejection',
                message: `Unhandled Promise Rejection: ${e.reason}`,
                promise: e.promise,
                reason: e.reason
            };

            window.unifiedDiagnosticErrors.push(errorEntry);
            this.errorLog.push(errorEntry);

            // Force immediate dashboard update for promise rejections
            setTimeout(() => this.updateDashboard(), 10);
        });
    }

    setupPerformanceMonitoring() {
        // Performance observer for monitoring
        if (typeof PerformanceObserver !== 'undefined') {
            try {
                const observer = new PerformanceObserver((list) => {
                    for (const entry of list.getEntries()) {
                        this.performanceMetrics[entry.name] = {
                            type: entry.entryType,
                            startTime: entry.startTime,
                            duration: entry.duration,
                            timestamp: Date.now()
                        };
                    }
                });

                observer.observe({entryTypes: ['measure', 'navigation', 'resource']});
            } catch (e) {
                console.warn('Performance Observer not fully supported:', e);
            }
        }

        // Memory monitoring
        setInterval(() => {
            if (performance.memory) {
                this.performanceMetrics.memory = {
                    usedJSHeapSize: performance.memory.usedJSHeapSize,
                    totalJSHeapSize: performance.memory.totalJSHeapSize,
                    jsHeapSizeLimit: performance.memory.jsHeapSizeLimit,
                    timestamp: Date.now()
                };
            }
        }, 5000); // Every 5 seconds
    }

    createDashboard() {
        // Create floating diagnostic dashboard
        this.dashboardElement = document.createElement('div');
        this.dashboardElement.id = 'unified-diagnostic-dashboard';
        this.dashboardElement.innerHTML = `
            <div class="diagnostic-header">
                <h3>ğŸ” Unified Diagnostics</h3>
                <button id="toggle-dashboard">â”€</button>
            </div>
            <div class="diagnostic-content">
                <div class="diagnostic-section">
                    <h4>ğŸ“Š System Status</h4>
                    <div id="system-status">Initializing...</div>
                </div>
                <div class="diagnostic-section" style="flex: 1;">
                    <h4>âš ï¸ Errors (<span id="error-count">0</span>) 
                        <button id="copy-errors-btn" class="copy-btn" title="Copy all errors to clipboard">ğŸ“‹</button>
                    </h4>
                    <div id="error-list" style="max-height: 200px; overflow-y: auto; min-height: 60px;"></div>
                </div>
                <div class="diagnostic-section">
                    <h4>ğŸ¯ Current State</h4>
                    <div id="current-state">Monitoring...</div>
                </div>
                <div class="diagnostic-section">
                    <h4>ğŸ“ˆ Performance 
                        <button id="copy-all-btn" class="copy-btn" title="Copy complete diagnostic data to clipboard">ğŸ“Š</button>
                    </h4>
                    <div id="performance-metrics">Collecting...</div>
                </div>
            </div>
        `;

        // Add styles with red liquid glass theme
        const styles = `
            #unified-diagnostic-dashboard {
                position: fixed;
                top: 10px;
                right: 10px;
                width: 320px;
                background: rgba(255, 0, 0, 0.08);
                backdrop-filter: blur(20px);
                -webkit-backdrop-filter: blur(20px);
                border: 1px solid rgba(255, 0, 0, 0.2);
                border-radius: 16px;
                font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
                font-size: 12px;
                z-index: 999999;
                max-height: 600px;
                overflow: hidden;
                box-shadow: 
                    0 8px 32px rgba(255, 0, 0, 0.12),
                    inset 0 1px 0 rgba(255, 255, 255, 0.1),
                    inset 0 -1px 0 rgba(0, 0, 0, 0.1);
                transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
            }

            #unified-diagnostic-dashboard:hover {
                background: rgba(255, 0, 0, 0.12);
                border-color: rgba(255, 0, 0, 0.3);
                box-shadow: 
                    0 12px 40px rgba(255, 0, 0, 0.18),
                    inset 0 1px 0 rgba(255, 255, 255, 0.15),
                    inset 0 -1px 0 rgba(0, 0, 0, 0.1);
            }

            .diagnostic-header {
                background: linear-gradient(135deg, 
                    rgba(255, 0, 0, 0.15) 0%, 
                    rgba(255, 50, 50, 0.1) 100%);
                backdrop-filter: blur(10px);
                padding: 12px 16px;
                display: flex;
                justify-content: space-between;
                align-items: center;
                cursor: move;
                border-bottom: 1px solid rgba(255, 0, 0, 0.15);
