    setupCursorTracking() {
        document.addEventListener('mousemove', (e) => {
            this.trackCursorMovement(e.clientX, e.clientY, false);
        });
    }

    trackCursorMovement(x, y, highlight = true) {
        const cursorData = {
            x: x,
            y: y,
            timestamp: Date.now(),
            canvasPosition: this.screenToCanvasCoords(x, y)
        };

        this.cursorHistory.push(cursorData);

        // Limit history size
        if (this.cursorHistory.length > 100) {
            this.cursorHistory.shift();
        }

        // Show highlight if requested
        if (highlight && this.cursorHighlight) {
            this.cursorHighlight.style.left = (x - 10) + 'px';
            this.cursorHighlight.style.top = (y - 10) + 'px';
            this.cursorHighlight.style.display = 'block';

            // Auto-hide after 2 seconds
            clearTimeout(this.cursorHighlightTimeout);
            this.cursorHighlightTimeout = setTimeout(() => {
                this.cursorHighlight.style.display = 'none';
            }, 2000);
        }

        return cursorData;
    }

    screenToCanvasCoords(screenX, screenY) {
        const canvas = document.querySelector('canvas');
        if (!canvas) return {x: screenX, y: screenY};

        const rect = canvas.getBoundingClientRect();
        const camera = window.tlEditor?.getCamera() || {x: 0, y: 0, z: 1};

        return {
            x: ((screenX - rect.left) / camera.z) - camera.x,
            y: ((screenY - rect.top) / camera.z) - camera.y
        };
    }

    simulateThreeFingerTouch(type, position) {
        const canvas = document.querySelector('canvas');
        if (!canvas) {
            console.error('âŒ Canvas not found for three-finger simulation');
            return;
        }

        console.log(`ğŸ–±ï¸ Simulating three-finger ${type} at`, position);

        // Create three pointer events with correct event names
        const eventType = type === 'down' ? 'pointerdown' : 
                         type === 'move' ? 'pointermove' : 
                         type === 'up' ? 'pointerup' : `pointer${type}`;

        for (let i = 0; i < 3; i++) {
            const event = new PointerEvent(eventType, {
                pointerId: i + 1,
                clientX: position.x + (i * 10), // Spread fingers slightly
                clientY: position.y + (i * 10),
                button: 0,
                buttons: type === 'up' ? 0 : 1, // No buttons pressed on up
                bubbles: true,
                cancelable: true
            });

            canvas.dispatchEvent(event);

            // Track cursor movement with highlight
            this.trackCursorMovement(position.x + (i * 10), position.y + (i * 10), true);
        }

        // Capture state after gesture
        const stateAfter = this.captureCurrentState();
        this.logUserAction(`three_finger_${type}`, {
            position: position,
            state: stateAfter
        });
    }

    simulateThreeFingerDrag(path) {
